INSERT INTO quiz_questions (question_id, question, options, correct_answer, explanation, category, difficulty, regulation, image_url, course)
VALUES
  ('inspect-visual-1', 'During initial visual inspection, what should be checked first?', '["Cable condition and routing","Adequacy of earthing and bonding","Presence of danger notices and labels","Condition of protective devices"]'::jsonb, 2, 'Before any detailed inspection, ensure proper warning labels, danger notices, and identification are present and legible for safety.', 'Visual Inspection', 'Beginner', '611.2', NULL, 'inspection-testing'),
  ('inspect-visual-2', 'What indicates inadequate IP rating during visual inspection?', '["Rust on metal enclosures","Water ingress marks or corrosion around cable entries","Loose cable terminations","Faded equipment labels"]'::jsonb, 1, 'Water ingress marks, corrosion around cable entries, or visible moisture damage indicates the IP rating is inadequate for the environment.', 'Visual Inspection', 'Beginner', '611.2', NULL, 'inspection-testing'),
  ('inspect-visual-3', 'During cable inspection, what indicates potential thermal damage?', '["Cable installed in conduit","Discolouration, hardening, or cracking of insulation","Cable supported at regular intervals","Use of different cable manufacturers"]'::jsonb, 1, 'Thermal damage manifests as discolouration, hardening, cracking, or embrittlement of cable insulation indicating overheating.', 'Visual Inspection', 'Beginner', '611.2', NULL, 'inspection-testing'),
  ('inspect-visual-4', 'What should be checked when inspecting socket outlets visually?', '["Brand compatibility only","Correct orientation and secure fixing","Electrical testing results","Load connected to each outlet"]'::jsonb, 1, 'Visual inspection includes checking socket outlets are correctly oriented, securely fixed, undamaged, and appropriate for their location.', 'Visual Inspection', 'Beginner', '611.2', NULL, 'inspection-testing'),
  ('inspect-visual-5', 'During inspection of distribution boards, what indicates poor workmanship?', '["Use of different MCB manufacturers","Unterminated cores not sleeved, poor cable dressing, missing labels","Presence of RCD protection","Use of modular components"]'::jsonb, 1, 'Poor workmanship is evident through unterminated cores without protective sleeving, poor cable management, and missing circuit identification.', 'Visual Inspection', 'Beginner', '611.2', NULL, 'inspection-testing'),
  ('inspect-visual-6', 'What visual signs indicate overloaded circuits?', '["Multiple cable types in use","Discoloured or burnt connections, excessive heat damage","Use of extension leads","Different switch types"]'::jsonb, 1, 'Overloaded circuits show signs of overheating such as discoloured connections, burnt terminals, or heat damage to surrounding materials.', 'Visual Inspection', 'Beginner', '611.2', NULL, 'inspection-testing'),
  ('inspect-visual-7', 'During visual inspection of earthing arrangements, what should be verified?', '["Earth electrode resistance value","Proper connections, labels, and accessibility of earthing conductors","Soil conditions around earth electrode","Earthing conductor colour only"]'::jsonb, 1, 'Visual inspection must verify earthing conductors are properly connected, labelled, accessible, and show no signs of corrosion or damage.', 'Visual Inspection', 'Beginner', '611.2', NULL, 'inspection-testing'),
  ('inspect-visual-8', 'What indicates inadequate IP protection in outdoor installations?', '["Use of galvanised steel conduit","Corrosion, water ingress, or damaged seals at cable entries","Metal enclosures instead of plastic","Standard indoor-rated equipment"]'::jsonb, 1, 'Outdoor installations showing corrosion, water ingress, or damaged seals indicate inadequate IP rating for the environment.', 'Visual Inspection', 'Intermediate', '611.2', NULL, 'inspection-testing'),
  ('test-procedures-1', 'Before commencing electrical testing, what must be verified?', '["All circuits are energised","Test equipment is calibrated and proved on known supply","Installation is fully loaded","Weather conditions are suitable"]'::jsonb, 1, 'Test equipment must be verified as working correctly on a known source before and after testing to ensure reliable results.', 'Testing Procedures', 'Intermediate', '612.1', NULL, 'inspection-testing'),
  ('test-procedures-2', 'Why must insulation resistance testing be performed with the installation de-energised?', '["To protect test equipment from damage","To prevent false readings from applied voltages and protect against shock","To comply with health and safety regulations only","To prevent nuisance RCD operation"]'::jsonb, 1, 'De-energising prevents false readings from applied voltages and protects against electric shock during high-voltage insulation testing.', 'Testing Procedures', 'Intermediate', '612.3.1', NULL, 'inspection-testing'),
  ('test-procedures-3', 'When testing RCD operation, what safety precaution is essential?', '["Test at maximum load current","Ensure no load is connected to RCD protected circuits","Test only during daylight hours","Use only battery-powered test equipment"]'::jsonb, 1, 'RCD testing must be performed with no load connected to prevent equipment damage and ensure accurate trip time measurements.', 'Testing Procedures', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('test-procedures-4', 'What is the correct sequence for continuity testing of a ring final circuit?', '["Test live to neutral, then live to earth","Test end-to-end resistance of each conductor, then cross-connect and test R1+R2","Test R1+R2 at each socket outlet only","Test with circuit energised to verify continuity"]'::jsonb, 1, 'Ring circuit testing requires end-to-end resistance measurement of each conductor, followed by cross-connection method to verify ring integrity.', 'Testing Procedures', 'Intermediate', '612.2.2', NULL, 'inspection-testing'),
  ('test-procedures-5', 'Why should earth fault loop impedance testing be performed last in the test sequence?', '["It requires the highest test voltage","It requires the circuit to be energised, posing safety risks","It is the most complex test to perform","It may damage sensitive equipment if performed first"]'::jsonb, 1, 'Zs testing requires energised circuits, so it should be performed last to minimise safety risks during the testing sequence.', 'Testing Procedures', 'Intermediate', '612.9', NULL, 'inspection-testing'),
  ('test-procedures-6', 'What documentation should accompany test equipment?', '["Purchase receipt only","Current calibration certificate and instruction manual","Warranty information","Previous test results"]'::jsonb, 1, 'Test equipment must have current calibration certificates and proper documentation to ensure accuracy and traceability.', 'Testing Procedures', 'Intermediate', '612.1', NULL, 'inspection-testing'),
  ('continuity-1', 'What does a continuity reading of infinite resistance indicate?', '["Perfect continuity","Open circuit or disconnection","Low resistance connection","Test equipment malfunction"]'::jsonb, 1, 'Infinite resistance indicates an open circuit, broken conductor, or disconnected joint preventing current flow.', 'Continuity Testing', 'Intermediate', '612.2', NULL, 'inspection-testing'),
  ('continuity-2', 'In a 32A ring final circuit using 2.5mm² cable, what would be typical R1+R2 reading at the origin?', '["0.1Ω to 0.2Ω","0.3Ω to 0.6Ω","1.0Ω to 1.5Ω","2.0Ω to 3.0Ω"]'::jsonb, 1, 'A typical 32A ring using 2.5mm² cable would show R1+R2 readings of approximately 0.3Ω to 0.6Ω depending on circuit length.', 'Continuity Testing', 'Intermediate', '612.2.2', NULL, 'inspection-testing'),
  ('continuity-3', 'What indicates a broken ring final circuit during continuity testing?', '["Zero resistance between live and neutral","R1+R2 values that are significantly higher than expected","Perfect continuity at all outlets","RCD operation during testing"]'::jsonb, 1, 'A broken ring will show as a radial circuit with much higher R1+R2 values than expected for a complete ring.', 'Continuity Testing', 'Intermediate', '612.2.2', NULL, 'inspection-testing'),
  ('continuity-4', 'How should protective conductor continuity be verified for a radial circuit?', '["Test between phase and protective conductor","Test between origin and furthest point of protective conductor","Test only at the origin of the circuit","Visual inspection is sufficient"]'::jsonb, 1, 'Protective conductor continuity requires testing between the earthing terminal at the origin and the furthest point on the circuit.', 'Continuity Testing', 'Intermediate', '612.2.1', NULL, 'inspection-testing'),
  ('continuity-5', 'What compensation should be applied to continuity measurements?', '["No compensation required","Add test lead resistance to all readings","Subtract test lead resistance from all readings","Apply temperature correction only"]'::jsonb, 2, 'Test lead resistance must be subtracted from continuity measurements to obtain accurate conductor resistance values.', 'Continuity Testing', 'Intermediate', '612.2', NULL, 'inspection-testing'),
  ('continuity-6', 'What current should be used for continuity testing?', '["Maximum available from test instrument","Between 4mA and 24mA DC","100mA minimum","Any DC current is acceptable"]'::jsonb, 1, 'BS 7671 specifies continuity testing should use current between 4mA and 24mA DC to avoid damage to electronic components.', 'Continuity Testing', 'Intermediate', '612.2', NULL, 'inspection-testing'),
  ('continuity-7', 'When testing ring final circuits, what indicates correct wiring?', '["Same resistance reading at all outlets","R1+R2 at midpoint approximately (R1+R2 origin)/4","Infinite resistance between conductors","Zero resistance throughout"]'::jsonb, 1, 'In a correctly wired ring, the R1+R2 reading at the midpoint should be approximately one quarter of the reading at the origin.', 'Continuity Testing', 'Advanced', '612.2.2', NULL, 'inspection-testing'),
  ('insulation-1', 'Why might insulation resistance be lower on a damp day?', '["Test equipment malfunction","Moisture reduces insulation effectiveness","Temperature affects cable resistance","Voltage drop in supply"]'::jsonb, 1, 'Moisture and humidity can penetrate insulation, providing leakage paths that reduce insulation resistance readings.', 'Insulation Resistance', 'Intermediate', '612.3', NULL, 'inspection-testing'),
  ('insulation-2', 'What equipment should be disconnected before insulation resistance testing?', '["All socket outlets","Electronic equipment, surge protectors, and sensitive devices","Only faulty equipment","Protective devices only"]'::jsonb, 1, 'Electronic equipment, surge protection devices, and equipment with filters must be disconnected to prevent damage and false readings.', 'Insulation Resistance', 'Intermediate', '612.3.1', NULL, 'inspection-testing'),
  ('insulation-3', 'A circuit shows 0.3MΩ insulation resistance. What action is required?', '["Accept as satisfactory","Investigate cause and rectify before energising","Apply voltage reduction","Install additional RCD protection"]'::jsonb, 1, 'Any reading below 1.0MΩ for 230V circuits indicates inadequate insulation requiring investigation and rectification.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('insulation-4', 'How long should test voltage be applied during insulation resistance testing?', '["Instantaneous reading","Until reading stabilises, typically 60 seconds","5 minutes minimum","10 seconds maximum"]'::jsonb, 1, 'Test voltage should be applied until the reading stabilises, typically 60 seconds, to allow for capacitive charging effects.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('insulation-5', 'What test voltage is used for insulation testing of 400V three-phase circuits?', '["500V DC","1000V DC","1500V DC","2500V DC"]'::jsonb, 1, 'For circuits with nominal voltage between 500V and 1000V, the test voltage is 1000V DC.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('insulation-6', 'What is the minimum insulation resistance for SELV circuits?', '["0.25MΩ","0.5MΩ","1.0MΩ","2.0MΩ"]'::jsonb, 0, 'SELV circuits operating at voltages up to 50V AC require minimum insulation resistance of 0.25MΩ.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('insulation-7', 'Why should electronic equipment be disconnected during insulation testing?', '["To prevent equipment operation","Electronic components provide alternative current paths causing low readings","To reduce test current requirements","Electronic equipment is not relevant to insulation"]'::jsonb, 1, 'Electronic components contain capacitors and filters that provide current paths, resulting in artificially low insulation readings.', 'Insulation Resistance', 'Advanced', '612.3.1', NULL, 'inspection-testing'),
  ('efli-1', 'Why does earth fault loop impedance increase with temperature?', '["Conductor resistance increases with temperature","Test equipment becomes less accurate","Earth resistance changes","Protective devices operate differently"]'::jsonb, 0, 'Conductor resistance increases with temperature, so Zs readings taken at ambient temperature must be corrected to operating temperature.', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('efli-2', 'A 16A Type B MCB protects a circuit with Zs of 2.8Ω. Is this acceptable?', '["Yes, well within limits","No, exceeds maximum Zs of 2.75Ω","Acceptable with RCD protection","Depends on cable length"]'::jsonb, 1, 'For 16A Type B MCB, maximum Zs is 2.75Ω. At 2.8Ω, the circuit exceeds this limit and requires rectification.', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('efli-3', 'What affects the external earth fault loop impedance (Ze)?', '["Installation wiring only","Supply authority earthing system and transformer impedance","Load connected to installation","Ambient temperature only"]'::jsonb, 1, 'Ze is determined by the supply authority\''s earthing system, transformer impedance, and supply network characteristics.', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('efli-4', 'How should high Zs readings be corrected?', '["Install larger protective devices","Use longer cables to reduce impedance","Improve earthing connections or upgrade cables","Add RCD protection only"]'::jsonb, 2, 'High Zs is corrected by improving earthing connections, increasing cable sizes, or reducing circuit length to lower impedance.', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('efli-5', 'When testing Zs on RCD-protected circuits, what precaution is necessary?', '["Test at maximum current","Test with RCD bypassed or use no-trip setting","Test only when circuit is fully loaded","Use DC test voltage"]'::jsonb, 1, 'RCD-protected circuits require the RCD to be bypassed or no-trip test methods used to prevent operation during testing.', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('efli-6', 'What is the formula for calculating temperature-corrected Zs?', '["Zs × (230 + 20) / (230 + operating temp)","Zs × (230 + operating temp) / (230 + 20)","Zs × 1.2 for copper, 1.38 for aluminium","No correction required for modern cables"]'::jsonb, 1, 'Temperature correction multiplies measured Zs by (230 + operating temp) / (230 + 20) to account for increased resistance at operating temperature.', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('rcd-1', 'An RCD rated at 30mA trips at 28mA during testing. Is this acceptable?', '["No, should trip exactly at 30mA","Yes, within acceptable range of 15mA to 30mA","No, indicates RCD is faulty","Yes, but only if trip time is correct"]'::jsonb, 1, 'RCDs should not trip below 50% (15mA) but must trip by 100% (30mA) of rated current. 28mA is acceptable.', 'RCD Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('rcd-2', 'What is the maximum trip time for an RCD at 5× rated current?', '["300ms","40ms","200ms","100ms"]'::jsonb, 1, 'At 5× rated current, RCDs must trip within 40ms to provide adequate protection against electric shock.', 'RCD Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('rcd-3', 'Why is RCD testing performed at different multiples of rated current?', '["To verify sensitivity and ensure adequate protection speed","To calibrate the RCD","To test at different voltages","To check mechanical operation only"]'::jsonb, 0, 'Testing at ½×, 1×, and 5× rated current verifies the RCD won\''t trip below threshold but will operate quickly enough for protection.', 'RCD Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('rcd-4', 'What indicates RCD deterioration during testing?', '["Consistent trip times","Increasing trip times or decreasing sensitivity","Normal operation at rated current","Immediate tripping"]'::jsonb, 1, 'RCD deterioration is indicated by increasing trip times, reduced sensitivity, or failure to operate within specified parameters.', 'RCD Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('rcd-5', 'How should Type A RCDs be tested compared to Type AC?', '["Identical test procedures","Type A requires AC and pulsating DC tests","Type A needs higher test currents","Type A testing is not required"]'::jsonb, 1, 'Type A RCDs must be tested with both AC and pulsating DC currents to verify operation with different fault types.', 'RCD Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('rcd-6', 'What is the maximum non-operation current for a 30mA RCD?', '["10mA","15mA","20mA","25mA"]'::jsonb, 1, 'RCDs must not operate at 50% of rated current, so a 30mA RCD should not trip at 15mA or below.', 'RCD Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('rcd-7', 'Why should RCD ramp testing be performed?', '["To find exact trip current and verify it\\''s within acceptable limits","To calibrate the RCD","To test mechanical operation","To check installation wiring"]'::jsonb, 0, 'Ramp testing determines the exact trip current, verifying it falls between 50% and 100% of the rated residual current.', 'RCD Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('scenario-1', 'During testing, a kitchen ring circuit shows R1+R2 of 1.2Ω at the origin but 2.1Ω at socket outlets. What is indicated?', '["Normal readings for a long ring","Possible interconnection between rings","Broken ring or spur connected as ring","Incorrect test procedure"]'::jsonb, 2, 'Significantly higher R1+R2 at outlets suggests a broken ring operating as a radial, or spurs incorrectly connected as part of the ring.', 'Practical Testing', 'Advanced', '612.2.2', NULL, 'inspection-testing'),
  ('scenario-2', 'A bathroom circuit passes all tests except Zs which reads 3.2Ω. The MCB is 6A Type B. What action is required?', '["Accept as RCD protected","Install supplementary bonding only","Reduce Zs below 7.67Ω maximum","Change to higher rated MCB"]'::jsonb, 2, 'Even with RCD protection, the circuit must meet basic protection requirements. 6A Type B MCB requires Zs ≤ 7.67Ω, so 3.2Ω is acceptable.', 'Practical Testing', 'Advanced', '411.3.2', NULL, 'inspection-testing'),
  ('scenario-3', 'An office lighting circuit shows insulation resistance of 0.8MΩ between live and neutral. Investigation reveals several computers connected. What should be done?', '["Accept reading as adequate","Disconnect computers and retest circuit insulation","Install RCD protection","Replace all cables"]'::jsonb, 1, 'Electronic equipment can cause low insulation readings. Disconnect equipment and retest to verify actual cable insulation.', 'Practical Testing', 'Advanced', '612.3.1', NULL, 'inspection-testing'),
  ('scenario-4', 'During RCD testing, the device trips correctly at 1× but fails to trip at 5× rated current. What is the likely cause?', '["RCD is functioning normally","RCD internal contacts are welded or mechanism jammed","Test equipment fault","Circuit overload"]'::jsonb, 1, 'Failure to trip at high current while operating at low current suggests mechanical failure such as welded contacts or jammed mechanism.', 'Practical Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('scenario-5', 'A new installation shows Ze of 0.8Ω but individual circuit Zs readings are all above maximum values. What should be investigated?', '["Supply authority earthing","Main earthing conductor connections and installation earthing","Individual circuit cables only","Protective device settings"]'::jsonb, 1, 'Good Ze but high Zs suggests problems with installation earthing, main earthing conductor, or equipotential bonding connections.', 'Practical Testing', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('scenario-6', 'A workshop circuit shows normal insulation resistance but very high Zs readings. All connections appear tight. What should be checked?', '["Cable size adequacy","Protective conductor continuity and main earthing conductor integrity","Supply voltage stability","Load current only"]'::jsonb, 1, 'High Zs with good insulation suggests protective conductor continuity issues or problems with main earthing connections.', 'Practical Testing', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('cert-1', 'What determines whether an Electrical Installation Certificate or Minor Works Certificate is required?', '["Value of work carried out","Extent of work - new installation vs minor additions/alterations","Client preference","Type of building"]'::jsonb, 1, 'EIC is required for new installations or major alterations. MWC is for minor work that doesn\''t affect existing test results.', 'Certification', 'Intermediate', '633', NULL, 'inspection-testing'),
  ('cert-2', 'Who can sign the "Design" section of an Electrical Installation Certificate?', '["Any qualified electrician","The person responsible for the design","Building control inspector","Installation company director"]'::jsonb, 1, 'Only the person responsible for the design of the installation can sign the design section, demonstrating accountability.', 'Certification', 'Intermediate', '633', NULL, 'inspection-testing'),
  ('cert-3', 'What limitation should be recorded if insulation testing is not possible on all circuits?', '["No limitation required","Record specific circuits not tested and reasons","General statement about limitations","Note equipment was connected"]'::jsonb, 1, 'Specific limitations must be clearly recorded, identifying untested circuits and reasons to inform future inspectors.', 'Certification', 'Intermediate', '633', NULL, 'inspection-testing'),
  ('cert-4', 'How long should test certificates be retained?', '["Until next inspection","Indefinitely for building records","5 years minimum","10 years only"]'::jsonb, 1, 'Certificates should be retained indefinitely as they form part of the building\''s electrical installation records.', 'Certification', 'Intermediate', '633', NULL, 'inspection-testing'),
  ('cert-5', 'What must be provided with every Electrical Installation Certificate?', '["Schedule of test results only","Schedule of test results and schedule of inspections","Site plans and drawings","Material certificates"]'::jsonb, 1, 'EIC must be accompanied by schedules of both test results and inspections to provide complete documentation.', 'Certification', 'Intermediate', '633', NULL, 'inspection-testing'),
  ('cert-6', 'What information must be recorded on the Schedule of Test Results?', '["Test results only","Circuit details, test results, and maximum permitted values","Equipment serial numbers","Installation costs"]'::jsonb, 1, 'The schedule must include circuit identification, test results obtained, and maximum permitted values for comparison.', 'Certification', 'Intermediate', '633', NULL, 'inspection-testing'),
  ('reg-1', 'According to BS 7671, what is the purpose of initial verification?', '["To test electrical equipment","To verify the installation complies with BS 7671","To check building regulations compliance","To test protective devices only"]'::jsonb, 1, 'Initial verification confirms the installation has been designed, constructed, and installed in accordance with BS 7671.', 'Regulations', 'Beginner', '610', NULL, 'inspection-testing'),
  ('reg-2', 'What information must be provided before commencing initial verification?', '["Material certificates","Charts, diagrams, and schedules for the installation","Previous test results","Building control approval"]'::jsonb, 1, 'Relevant charts, diagrams, schedules, and installation details must be provided to enable proper verification.', 'Regulations', 'Beginner', '610.1', NULL, 'inspection-testing'),
  ('reg-3', 'Which regulation covers the requirements for periodic inspection and testing?', '["Part 6 - Verification","Part 7 - Special installations","Section 621 - Initial verification","Section 622 - Periodic inspection"]'::jsonb, 3, 'Section 622 specifically covers requirements for periodic inspection and testing of electrical installations.', 'Regulations', 'Intermediate', '622', NULL, 'inspection-testing'),
  ('reg-4', 'What does regulation 134.1.1 require regarding compliance?', '["Installations must use specific cable types","Every installation shall comply with BS 7671 requirements","Only new installations need comply","Compliance is optional for domestic premises"]'::jsonb, 1, 'Regulation 134.1.1 establishes that every electrical installation shall comply with the requirements of BS 7671.', 'Regulations', 'Intermediate', '134.1.1', NULL, 'inspection-testing'),
  ('reg-5', 'Which regulation addresses safety during verification work?', '["Regulation 610.1","Regulation 611.1","Regulation 612.1","All verification regulations include safety requirements"]'::jsonb, 3, 'Safety requirements are integrated throughout Part 6, with specific safety measures required for each type of verification activity.', 'Regulations', 'Advanced', '610-630', NULL, 'inspection-testing'),
  ('safety-1', 'What is the first step in safe isolation procedure?', '["Switch off the circuit","Identify the circuit to be worked on","Test the voltage indicator","Lock off the isolation point"]'::jsonb, 1, 'The first step is to correctly identify the circuit or equipment to be worked on to ensure the right circuit is isolated.', 'Safety', 'Beginner', NULL, NULL, 'inspection-testing'),
  ('safety-2', 'What PPE is essential when carrying out electrical testing?', '["Safety boots only","Insulated gloves and safety boots","Safety glasses and hard hat","High-visibility jacket"]'::jsonb, 1, 'Insulated gloves and safety boots provide protection against electric shock during testing procedures.', 'Safety', 'Beginner', NULL, NULL, 'inspection-testing'),
  ('safety-3', 'What does GS 38 provide guidance on?', '["Cable installation methods","Electrical test equipment for electricians","Earthing arrangements","Lighting calculations"]'::jsonb, 1, 'GS 38 provides guidance on electrical test equipment for electricians, covering safety requirements for test instruments.', 'Safety', 'Beginner', NULL, NULL, 'inspection-testing'),
  ('safety-4', 'What is the maximum voltage that test probe tips should be exposed when testing live circuits?', '["2mm","4mm","10mm","15mm"]'::jsonb, 0, 'According to GS 38, test probe tips should have no more than 2mm of exposed metal to minimise risk of short circuits.', 'Safety', 'Beginner', NULL, NULL, 'inspection-testing'),
  ('safety-5', 'When should you prove your voltage indicator before use?', '["Once per day","Before and after each use","Weekly","Only when it appears damaged"]'::jsonb, 1, 'Voltage indicators should be proved on a known source before and after each use to ensure they are functioning correctly.', 'Safety', 'Beginner', NULL, NULL, 'inspection-testing'),
  ('special-1', 'In a bathroom, what is the minimum IP rating required for equipment in Zone 1?', '["IPX1","IPX4","IPX5","IPX7"]'::jsonb, 3, 'Equipment in bathroom Zone 1 requires a minimum rating of IPX7 (protected against immersion in water).', 'Special Locations', 'Intermediate', '701.512.2', NULL, 'inspection-testing'),
  ('special-2', 'What is the maximum height of Zone 1 in a bathroom with a shower?', '["2.0m","2.25m","2.5m","3.0m"]'::jsonb, 1, 'Zone 1 in a bathroom extends to 2.25m above the floor level, or the highest fixed shower head if higher.', 'Special Locations', 'Intermediate', '701.32', NULL, 'inspection-testing'),
  ('special-3', 'In agricultural premises, what is the maximum earth fault loop impedance for a 230V circuit?', '["0.37Ω","0.55Ω","1.37Ω","1.84Ω"]'::jsonb, 0, 'Agricultural premises require enhanced protection with maximum Zs of 0.37Ω for 230V circuits due to increased shock risk.', 'Special Locations', 'Intermediate', '705.411.1', NULL, 'inspection-testing'),
  ('periodic-1', 'What factors determine the frequency of periodic inspection and testing?', '["Age of installation only","Type of installation, environment, usage, and maintenance quality","Client budget availability","Local authority requirements"]'::jsonb, 1, 'Inspection frequency depends on installation type, environmental conditions, usage patterns, and quality of maintenance.', 'Periodic Testing', 'Advanced', '622', NULL, 'inspection-testing'),
  ('periodic-2', 'During periodic inspection, what deterioration should be specifically looked for?', '["Equipment age only","Thermal damage, mechanical damage, corrosion, and wear","Aesthetic appearance","Brand compatibility"]'::jsonb, 1, 'Periodic inspection focuses on deterioration from thermal effects, mechanical stress, corrosion, and normal wear and tear.', 'Periodic Testing', 'Advanced', '622', NULL, 'inspection-testing'),
  ('accuracy-1', 'Why is test equipment accuracy critical for regulatory compliance?', '["To impress clients","Inaccurate readings may indicate compliance when installation is actually unsafe","Legal requirements only","Equipment manufacturer requirements"]'::jsonb, 1, 'Inaccurate test equipment could give false compliance readings, potentially leaving unsafe installations energised.', 'Test Equipment', 'Advanced', '612.1', NULL, 'inspection-testing'),
  ('accuracy-2', 'What calibration interval is typically required for electrical test equipment?', '["6 months","12 months","24 months","When it appears to malfunction"]'::jsonb, 1, 'Annual calibration is typically required to maintain accuracy and ensure reliable test results for safety verification.', 'Test Equipment', 'Advanced', '612.1', NULL, 'inspection-testing'),
  ('bs7671-fund-1', 'What does BS 7671 define as "live parts"?', '["Parts that are normally energised","Parts that may become live under fault conditions","Conductors and conductive parts intended to be energised in normal use","Any metallic parts of electrical equipment"]'::jsonb, 2, 'BS 7671 defines live parts as conductors and conductive parts intended to be energised in normal use, including neutral conductors but excluding PEN conductors.', 'Regulations', 'Beginner', '100.2', NULL, 'inspection-testing'),
  ('bs7671-fund-2', 'What is the standard voltage for single-phase domestic supply in the UK?', '["220V","230V","240V","250V"]'::jsonb, 1, 'The standard voltage for single-phase domestic supply in the UK is 230V ±10% (207V to 253V).', 'Regulations', 'Beginner', '312.2', NULL, 'inspection-testing'),
  ('bs7671-fund-3', 'What is the purpose of earthing according to BS 7671?', '["To provide a return path for current","To limit voltage between exposed-conductive-parts and earth","To reduce electromagnetic interference","To improve power factor"]'::jsonb, 1, 'The fundamental purpose of earthing is to limit the voltage that may appear between exposed-conductive-parts and earth.', 'Regulations', 'Beginner', '411.3.1', NULL, 'inspection-testing'),
  ('inspect-visual-new-1', 'What is the minimum IP rating required for a socket outlet installed in a domestic bathroom zone 2?', '["IP20","IP44","IPX4","IPX7"]'::jsonb, 2, 'Socket outlets in bathroom zone 2 must have a minimum rating of IPX4 to protect against water splashing from any direction.', 'Visual Inspection', 'Intermediate', '701.512.3', NULL, 'inspection-testing'),
  ('inspect-visual-new-2', 'During visual inspection, you notice cables buried in walls without mechanical protection. At what depth must cables be installed to comply with BS 7671?', '["Minimum 50mm from surface in safe zones","Minimum 25mm from surface anywhere","No minimum depth if RCD protected","Minimum 100mm from surface"]'::jsonb, 0, 'Cables buried in walls must be installed at a minimum depth of 50mm from the surface when in designated safe zones, or have additional protection.', 'Visual Inspection', 'Intermediate', '522.6.202', NULL, 'inspection-testing'),
  ('inspect-visual-new-3', 'What must be provided at the origin of every installation?', '["RCD protection only","Surge protection device","Means of isolation and switching off for mechanical maintenance","Emergency lighting"]'::jsonb, 2, 'Every installation must have a readily accessible means of isolation at the origin, capable of cutting off the supply to the entire installation.', 'Visual Inspection', 'Beginner', '537.1.2', NULL, 'inspection-testing'),
  ('inspect-visual-new-4', 'During inspection of an outdoor installation, what maximum voltage should be used for socket outlets?', '["400V three-phase","230V single-phase with 30mA RCD","110V centre-tapped transformer","12V extra-low voltage"]'::jsonb, 1, 'Outdoor socket outlets up to 32A must be protected by a 30mA RCD. Reduced voltage systems (110V) are preferred for construction sites.', 'Visual Inspection', 'Intermediate', '411.3.3', NULL, 'inspection-testing'),
  ('inspect-visual-new-5', 'What colour must the insulation of a protective conductor be?', '["Green only","Yellow only","Green-and-yellow stripes","Bare copper acceptable in all cases"]'::jsonb, 2, 'Protective conductors must be identified by green-and-yellow stripes (bicolour combination). This combination must not be used for any other purpose.', 'Visual Inspection', 'Beginner', '514.3.1', NULL, 'inspection-testing'),
  ('inspect-visual-new-6', 'You inspect a consumer unit and find an RCBO protecting a lighting circuit. What is the maximum rated residual operating current for this device?', '["10mA","30mA","100mA","300mA"]'::jsonb, 1, 'For final circuits not exceeding 32A, RCD protection must not exceed 30mA residual operating current for additional protection against electric shock.', 'Visual Inspection', 'Intermediate', '415.1.1', NULL, 'inspection-testing'),
  ('inspect-visual-new-7', 'During visual inspection, what indicates a cable is suitable for direct burial underground?', '["PVC insulation only","Steel wire armoured (SWA) cable","Standard T&E cable in conduit","Any cable over 10mm²"]'::jsonb, 1, 'Cables for direct burial must have appropriate construction, typically steel wire armoured (SWA) cables which provide mechanical protection.', 'Visual Inspection', 'Intermediate', '522.8.10', NULL, 'inspection-testing'),
  ('inspect-visual-new-8', 'What must be installed for every circuit to enable identification?', '["Colour-coded cables","Durable circuit labelling at distribution boards","Written instructions only","Photographic record"]'::jsonb, 1, 'Durable, legible circuit identification labels must be provided at distribution boards to enable circuits to be easily identified.', 'Visual Inspection', 'Beginner', '514.1.1', NULL, 'inspection-testing'),
  ('inspect-visual-new-9', 'You notice a shower circuit protected by a 32A MCB with 4mm² cable. What is wrong with this installation?', '["Nothing, this is acceptable","Cable undersized for protective device rating","MCB rating too low","Should use RCBO instead"]'::jsonb, 1, '4mm² cable has a maximum current-carrying capacity of approximately 27A (depending on installation method). A 32A MCB could allow overload without disconnection.', 'Visual Inspection', 'Intermediate', '433.1.1', NULL, 'inspection-testing'),
  ('inspect-visual-new-10', 'What is the purpose of main equipotential bonding?', '["To provide circuit protection","To reduce earth loop impedance","To connect extraneous-conductive-parts to the main earthing terminal","To improve power quality"]'::jsonb, 2, 'Main equipotential bonding connects extraneous-conductive-parts (gas, water, structural steel) to the main earthing terminal to prevent dangerous voltages.', 'Visual Inspection', 'Beginner', '411.3.1.2', NULL, 'inspection-testing'),
  ('inspect-visual-new-11', 'During inspection, what minimum cross-sectional area should main equipotential bonding conductors be?', '["2.5mm² copper","6mm² copper minimum, half the earthing conductor but not less than 6mm²","10mm² copper minimum","Same size as the largest circuit conductor"]'::jsonb, 1, 'Main bonding conductors must be at least half the cross-sectional area of the earthing conductor, with a minimum of 6mm² and need not exceed 25mm².', 'Visual Inspection', 'Intermediate', '544.1.1', NULL, 'inspection-testing'),
  ('inspect-visual-new-12', 'You inspect a garage consumer unit and notice no surge protection device (SPD). When is SPD required?', '["Always required in all installations","Only in commercial buildings","Where consequences of overvoltage could result in serious injury or loss of life","Never required in domestic installations"]'::jsonb, 2, 'SPDs are required where the installation serves areas with risk of injury/loss of life, or where loss of service would cause significant public safety concerns.', 'Visual Inspection', 'Advanced', '443.4', NULL, 'inspection-testing'),
  ('inspect-visual-new-13', 'What indicates adequate provision for isolation of an installation?', '["Circuit breakers only","Accessible main switch capable of cutting off full load current","Pull-cord switches","RCD as sole means of isolation"]'::jsonb, 1, 'A readily accessible main switch capable of interrupting full load current must be provided at the origin for isolation purposes.', 'Visual Inspection', 'Beginner', '537.2.1', NULL, 'inspection-testing'),
  ('continuity-new-1', 'You measure R1+R2 for a 25m radial circuit and get 0.85Ω. If the circuit uses 2.5mm² live conductors and 1.5mm² CPC, is this acceptable?', '["Yes, within expected range","No, reading too high","Yes, but requires temperature correction","No, indicates broken conductor"]'::jsonb, 0, '2.5mm² copper = 7.41mΩ/m, 1.5mm² copper = 12.1mΩ/m. Expected R1+R2 = (7.41 + 12.1) × 25 = 0.49Ω × 1.2 multiplier = 0.59Ω. 0.85Ω is within acceptable range considering connections.', 'Continuity Testing', 'Advanced', '612.2.1', NULL, 'inspection-testing'),
  ('continuity-new-2', 'When testing ring final circuit continuity, what should be the relationship between the measured values?', '["All readings should be identical","r1 + r2 should equal R1 + R2 ÷ 4","R1 and R2 should be half of r1 and r2","r1 should be twice r2"]'::jsonb, 1, 'For a ring circuit, the resistance measured at each socket (r1+r2) should equal approximately (R1+R2)÷4, confirming ring integrity.', 'Continuity Testing', 'Advanced', '612.2.2', NULL, 'inspection-testing'),
  ('continuity-new-3', 'What is the purpose of measuring continuity of protective conductors?', '["To verify circuit integrity","To ensure a low-resistance path exists for fault current","To measure insulation resistance","To calculate voltage drop"]'::jsonb, 1, 'Continuity testing ensures protective conductors provide a low-resistance path for fault current, enabling rapid disconnection of supply.', 'Continuity Testing', 'Beginner', '612.2.1', NULL, 'inspection-testing'),
  ('continuity-new-4', 'You are testing a ring final circuit and one leg shows significantly higher resistance. What does this indicate?', '["Normal reading due to cable length variations","Possible loose connection or partial break in ring","Incorrect cable size used","RCD interference"]'::jsonb, 1, 'Significantly different resistance in ring circuit legs indicates a loose connection, high-resistance joint, or partial conductor break.', 'Continuity Testing', 'Intermediate', '612.2.2', NULL, 'inspection-testing'),
  ('continuity-new-5', 'What test voltage is typically used for continuity testing?', '["230V AC","500V DC","4-24V DC from test instrument","400V AC"]'::jsonb, 2, 'Continuity testing uses low voltage (typically 4-24V DC) and measures resistance, not high voltage like insulation resistance tests.', 'Continuity Testing', 'Beginner', '612.2.1', NULL, 'inspection-testing'),
  ('continuity-new-6', 'When testing supplementary bonding, what maximum resistance value is acceptable?', '["0.05Ω","0.5Ω if not incorporating exposed-conductive-parts","1.0Ω","5.0Ω"]'::jsonb, 1, 'Supplementary bonding resistance should not exceed 0.05Ω when incorporating exposed-conductive-parts, or calculated value based on RCD rating if not.', 'Continuity Testing', 'Advanced', '415.2.2', NULL, 'inspection-testing'),
  ('continuity-new-7', 'Before conducting continuity tests, what must you ensure?', '["All loads are connected","Supply is isolated and proven dead","RCDs are in test mode","All circuits are energised"]'::jsonb, 1, 'Safe isolation and proving dead is essential before any testing. Continuity tests are conducted with the installation de-energised.', 'Continuity Testing', 'Beginner', '612.1', NULL, 'inspection-testing'),
  ('continuity-new-8', 'You measure 0.15Ω for R2 on a lighting circuit. If R1 (phase conductor) is also 0.15Ω, what can you deduce?', '["Both conductors are the same CSA","This is incorrect, R2 should be higher","Installation is faulty","Test leads not zeroed"]'::jsonb, 0, 'Equal R1 and R2 values indicate both conductors have the same cross-sectional area, which is common in twin and earth cable.', 'Continuity Testing', 'Intermediate', '612.2.1', NULL, 'inspection-testing'),
  ('continuity-new-9', 'What does a very low or zero reading during ring circuit continuity testing indicate?', '["Perfect installation","Possible cross-connection or short circuit","Correct ring formation","Temperature compensation needed"]'::jsonb, 1, 'Unexpectedly low or zero readings may indicate crossed conductors or a short circuit between phase and neutral.', 'Continuity Testing', 'Intermediate', '612.2.2', NULL, 'inspection-testing'),
  ('continuity-new-10', 'When should test leads be nulled (zeroed)?', '["Once per day","Before each test sequence","Only if readings seem incorrect","Never necessary with digital meters"]'::jsonb, 1, 'Test leads should be nulled before each test sequence to eliminate lead resistance from measurements, ensuring accuracy.', 'Continuity Testing', 'Beginner', '612.2.1', NULL, 'inspection-testing'),
  ('continuity-new-11', 'A 40m circuit has R1=0.30Ω and R2=0.50Ω. What is the combined R1+R2 value?', '["0.20Ω","0.80Ω","0.40Ω","1.50Ω"]'::jsonb, 1, 'R1+R2 is the sum of the phase and protective conductor resistances: 0.30Ω + 0.50Ω = 0.80Ω.', 'Continuity Testing', 'Beginner', '612.2.1', NULL, 'inspection-testing'),
  ('continuity-new-12', 'Why might you use the R2 method instead of R1+R2 method for continuity testing?', '["R2 method is always more accurate","When circuit is inaccessible at far end","When phase conductor cannot be isolated","R2 method is no longer permitted"]'::jsonb, 1, 'The R2 method is used when access to the far end is not possible, measuring only the protective conductor resistance.', 'Continuity Testing', 'Intermediate', '612.2.1', NULL, 'inspection-testing'),
  ('ir-new-1', 'You test IR at 500V DC and get 150MΩ. What is the verdict?', '["Fail - below minimum","Pass - above 1MΩ minimum","Retest required","Borderline - needs investigation"]'::jsonb, 1, 'For SELV and PELV circuits, minimum IR is 0.5MΩ. For circuits up to 500V, minimum is 1MΩ. 150MΩ is an excellent pass.', 'Insulation Resistance', 'Beginner', '612.3.2', NULL, 'inspection-testing'),
  ('ir-new-2', 'What test voltage should be used for a 230V single-phase installation?', '["250V DC","500V DC","1000V DC","230V AC"]'::jsonb, 1, 'For installations with nominal voltage up to and including 500V AC, the test voltage should be 500V DC.', 'Insulation Resistance', 'Beginner', '612.3.2', NULL, 'inspection-testing');
INSERT INTO quiz_questions (question_id, question, options, correct_answer, explanation, category, difficulty, regulation, image_url, course)
VALUES
  ('ir-new-3', 'Before insulation resistance testing, what must be disconnected?', '["All earthing conductors","Electronic equipment, surge protectors, and devices that could be damaged","All protective devices","Nothing - test with everything connected"]'::jsonb, 1, 'Sensitive electronic equipment, surge protection devices, and equipment that could be damaged by test voltage must be disconnected or removed.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-new-4', 'You test IR between live conductors and earth, getting 0.8MΩ. What action is required?', '["Pass and issue certificate","Fail - investigate and rectify fault","Acceptable if RCD protected","Retest at higher voltage"]'::jsonb, 1, 'The minimum acceptable IR for circuits up to 500V is 1MΩ. A reading of 0.8MΩ is below minimum and requires investigation.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-new-5', 'What three insulation resistance tests should be conducted on a single-phase installation?', '["Only phase to earth","Phase to earth, neutral to earth only","Phase to neutral, phase to earth, neutral to earth","Phase to neutral only"]'::jsonb, 2, 'Three tests are required: between phase and neutral, phase and earth, and neutral and earth, to verify complete insulation integrity.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-new-6', 'An IR test gives fluctuating readings. What is the most likely cause?', '["Faulty test instrument","Moisture or contamination affecting insulation","Incorrect test voltage","Cable too long"]'::jsonb, 1, 'Fluctuating IR readings typically indicate moisture, contamination, or deteriorating insulation that cannot maintain stable resistance.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-new-7', 'What minimum IR value is required for SELV and PELV circuits?', '["0.25MΩ","0.5MΩ","1.0MΩ","2.0MΩ"]'::jsonb, 1, 'SELV (Separated Extra Low Voltage) and PELV circuits require a minimum insulation resistance of 0.5MΩ when tested at 250V DC.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('ir-new-8', 'Why should IR tests be conducted with all switches and circuit breakers closed?', '["To test the protective devices","To test complete circuit including switchgear","This is incorrect - switches must be open","To prevent damage to equipment"]'::jsonb, 1, 'Testing with switches closed ensures the entire circuit, including switchgear and control equipment, is tested for insulation integrity.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-new-9', 'What voltage should be used to test IR on circuits containing equipment rated at 1000V?', '["500V DC","1000V DC","1500V DC","2500V DC"]'::jsonb, 1, 'For circuits with nominal voltage above 500V up to 1000V, test voltage should be 1000V DC.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('ir-new-10', 'You obtain an IR reading of 300MΩ. How should this be recorded?', '["Exactly as 300MΩ",">299MΩ",">200MΩ",">100MΩ"]'::jsonb, 2, 'Very high readings above 200MΩ are typically recorded as >200MΩ as precise values above this threshold are not significant for compliance.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-new-11', 'What precaution must be taken after completing IR tests?', '["Test equipment at higher voltage","Discharge capacitance to earth before handling conductors","Immediately re-energise the circuit","Test earth loop impedance"]'::jsonb, 1, 'Cables act as capacitors during high-voltage testing. After IR tests, discharge stored energy to earth before handling conductors.', 'Insulation Resistance', 'Beginner', '612.3.2', NULL, 'inspection-testing'),
  ('ir-new-12', 'During periodic inspection, you find IR has dropped from 150MΩ to 2MΩ. What does this indicate?', '["Still acceptable - above 1MΩ minimum","Deterioration - investigate and monitor","Normal aging of installation","Test error - retest only"]'::jsonb, 1, 'While 2MΩ passes minimum requirements, a significant drop from previous readings indicates deterioration requiring investigation.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('efli-new-1', 'A circuit has Ze=0.35Ω and R1+R2=0.45Ω. What is the Zs value?', '["0.10Ω","0.80Ω","0.45Ω","0.35Ω"]'::jsonb, 1, 'Zs = Ze + R1 + R2. Therefore Zs = 0.35Ω + 0.45Ω = 0.80Ω.', 'Earth Fault Loop Impedance', 'Beginner', '612.9', NULL, 'inspection-testing'),
  ('efli-new-2', 'You measure Zs as 1.20Ω for a 32A Type B MCB circuit. The maximum permitted Zs is 1.44Ω. Is this acceptable?', '["Pass - below maximum limit","Fail - too close to limit","Pass but investigate","Retest required"]'::jsonb, 0, 'At 1.20Ω, the circuit is below the 1.44Ω maximum (80% rule) for a 32A Type B MCB, therefore acceptable.', 'Earth Fault Loop Impedance', 'Intermediate', '411.4.9', NULL, 'inspection-testing'),
  ('efli-new-3', 'Why is the 80% multiplier applied to maximum Zs values from BS 7671?', '["To account for measurement uncertainty","To account for conductor temperature rise under fault conditions","To provide additional safety margin","Required by test instrument manufacturers"]'::jsonb, 1, 'The 80% rule accounts for the increase in conductor resistance as temperature rises from ambient (20°C) to fault temperature (~70°C).', 'Earth Fault Loop Impedance', 'Advanced', '411.4.9', NULL, 'inspection-testing'),
  ('efli-new-4', 'What is the maximum disconnection time for a final circuit not exceeding 32A in a TN system?', '["0.1 seconds","0.4 seconds","5 seconds","1 second"]'::jsonb, 1, 'For final circuits up to 32A in TN systems, the maximum disconnection time is 0.4 seconds for automatic disconnection of supply.', 'Earth Fault Loop Impedance', 'Intermediate', '411.3.2.2', NULL, 'inspection-testing'),
  ('efli-new-5', 'You test a circuit protected by a 40A Type C MCB and get Zs=1.10Ω. Is this acceptable if maximum Zs (80% rule) is 0.91Ω?', '["Pass - measurement uncertainty applies","Fail - exceeds maximum permitted value","Pass if RCD protected","Retest at different time"]'::jsonb, 1, 'The measured value of 1.10Ω exceeds the maximum permitted 0.91Ω (80% rule). This is a fail and requires investigation.', 'Earth Fault Loop Impedance', 'Intermediate', '411.4.9', NULL, 'inspection-testing'),
  ('efli-new-6', 'What is the relationship between Zs and fault current?', '["Higher Zs = higher fault current","Lower Zs = higher fault current","Zs does not affect fault current","Zs only affects normal load current"]'::jsonb, 1, 'Fault current If = Uo/Zs. Lower earth fault loop impedance results in higher fault current, enabling faster disconnection.', 'Earth Fault Loop Impedance', 'Intermediate', '411.4.4', NULL, 'inspection-testing'),
  ('efli-new-7', 'When should Zs testing be conducted during commissioning?', '["Before any other tests","After continuity and IR testing, with circuit energised","Only during periodic inspection","Before continuity testing"]'::jsonb, 1, 'Zs testing requires the circuit to be energised and should be conducted after dead tests (continuity and IR) are completed satisfactorily.', 'Earth Fault Loop Impedance', 'Beginner', '612.9', NULL, 'inspection-testing'),
  ('efli-new-8', 'A circuit is protected by a 30mA RCD. What is the maximum Zs permitted?', '["1667Ω","230Ω","50Ω","No specific limit"]'::jsonb, 0, 'For RCD protection, Zs × Ia ≤ 50V. With 30mA RCD: Zs ≤ 50V ÷ 0.03A = 1667Ω. However, protective device must still operate within required time.', 'Earth Fault Loop Impedance', 'Advanced', '411.5.3', NULL, 'inspection-testing'),
  ('efli-new-9', 'What is Ze in the context of earth fault loop impedance?', '["Impedance of the installation","External earth fault loop impedance (supply authority)","Circuit protective conductor resistance","Total earth fault loop impedance"]'::jsonb, 1, 'Ze is the external earth fault loop impedance, measured at the origin before any installation conductors are connected.', 'Earth Fault Loop Impedance', 'Beginner', '612.9', NULL, 'inspection-testing'),
  ('efli-new-10', 'You measure Ze=0.25Ω. Is this typical for a TN-C-S (PME) system?', '["No, too low for TN-C-S","Yes, typical value for TN-C-S","No, indicates TT system","Yes, but only for TN-S"]'::jsonb, 1, 'TN-C-S (PME) systems typically have Ze values of 0.35Ω or lower due to the combined neutral-earth conductor.', 'Earth Fault Loop Impedance', 'Intermediate', '612.9', NULL, 'inspection-testing'),
  ('efli-new-11', 'What happens to Zs when conductor temperature increases during a fault?', '["Zs decreases","Zs increases","Zs remains constant","Zs becomes zero"]'::jsonb, 1, 'Conductor resistance increases with temperature. During a fault, conductors heat up, causing Zs to increase.', 'Earth Fault Loop Impedance', 'Intermediate', '411.4.9', NULL, 'inspection-testing'),
  ('efli-new-12', 'For a 20A Type B MCB, what is the maximum Zs at ambient temperature (80% rule)?', '["1.80Ω","2.30Ω","2.87Ω","3.59Ω"]'::jsonb, 1, 'For a 20A Type B MCB, the maximum Zs at ambient temperature (80% rule) is 2.30Ω.', 'Earth Fault Loop Impedance', 'Intermediate', 'Appendix 3', NULL, 'inspection-testing'),
  ('rcd-new-1', 'You test a 30mA RCD at ×1 rated current (30mA). What is the maximum permitted disconnection time?', '["40ms","300ms","2 seconds","Should not trip at ×1"]'::jsonb, 3, 'At ×1 rated current (IΔn), the RCD should not trip. This confirms it will not nuisance trip under normal leakage conditions.', 'RCD Testing', 'Intermediate', '612.10', NULL, 'inspection-testing'),
  ('rcd-new-2', 'At what test current must a 30mA RCD trip within 40ms?', '["×1 (30mA)","×5 (150mA)","×10 (300mA)","×0.5 (15mA)"]'::jsonb, 1, 'At ×5 rated current, an RCD must disconnect within 40ms to provide additional protection for direct contact.', 'RCD Testing', 'Intermediate', '411.4.9', NULL, 'inspection-testing'),
  ('rcd-new-3', 'What is the purpose of testing RCDs at both ×1 and ×5 rated current?', '["To calibrate the device","To verify it will not nuisance trip (×1) and will provide shock protection (×5)","To measure insulation resistance","To test the mechanical function only"]'::jsonb, 1, '×1 test ensures no nuisance tripping under normal leakage. ×5 test confirms rapid disconnection for shock protection.', 'RCD Testing', 'Intermediate', '612.10', NULL, 'inspection-testing'),
  ('rcd-new-4', 'A 30mA RCD trips in 25ms at ×5 current. What is the verdict?', '["Fail - too fast","Pass - within 40ms limit","Retest required","Replace RCD"]'::jsonb, 1, 'The RCD must trip within 40ms at ×5 rated current. A trip time of 25ms is well within limits and passes.', 'RCD Testing', 'Beginner', '411.4.9', NULL, 'inspection-testing'),
  ('rcd-new-5', 'What is the difference between Type AC and Type A RCDs?', '["No difference","Type A detects pulsating DC and AC, Type AC only AC","Type AC is faster","Type A has higher sensitivity"]'::jsonb, 1, 'Type A RCDs detect both sinusoidal AC and pulsating DC residual currents. Type AC only detects sinusoidal AC.', 'RCD Testing', 'Advanced', '531.3.3', NULL, 'inspection-testing'),
  ('rcd-new-6', 'You test an RCD and it fails to trip at ×5 current. What action is required?', '["Increase test current","Replace or repair RCD - it is faulty","Test again tomorrow","Acceptable if protected by MCB"]'::jsonb, 1, 'Failure to trip at ×5 rated current indicates a faulty RCD that cannot provide shock protection. It must be replaced.', 'RCD Testing', 'Beginner', '612.10', NULL, 'inspection-testing'),
  ('rcd-new-7', 'When should the RCD mechanical test button be operated?', '["Never - only electronic testing allowed","After electrical testing to verify mechanical operation","Instead of electrical testing","Only during installation"]'::jsonb, 1, 'After electrical testing, operate the mechanical test button to verify the tripping mechanism and reset function work correctly.', 'RCD Testing', 'Beginner', '612.10', NULL, 'inspection-testing'),
  ('rcd-new-8', 'What residual current sensitivity is required for additional protection in final circuits up to 32A?', '["10mA","30mA","100mA","300mA"]'::jsonb, 1, 'For additional protection in final circuits not exceeding 32A, RCDs must have a rated residual operating current (IΔn) not exceeding 30mA.', 'RCD Testing', 'Beginner', '415.1.1', NULL, 'inspection-testing'),
  ('rcd-new-9', 'An RCD protecting a socket outlet circuit trips at ×1 rated current. What does this indicate?', '["RCD is too sensitive - investigate for high earth leakage or faulty RCD","Normal operation","Excellent safety margin","Temperature affecting operation"]'::jsonb, 0, 'Tripping at ×1 indicates the RCD is too sensitive, there is excessive earth leakage, or the RCD is faulty.', 'RCD Testing', 'Intermediate', '612.10', NULL, 'inspection-testing'),
  ('rcd-new-10', 'What is the maximum disconnection time for a 100mA RCD at ×5 rated current?', '["40ms","300ms","40ms for Type S","Same as 30mA RCD"]'::jsonb, 0, '100mA and 300mA RCDs must also disconnect within 40ms at ×5 rated current when providing additional protection.', 'RCD Testing', 'Advanced', '411.4.9', NULL, 'inspection-testing'),
  ('rcd-new-11', 'Why must test equipment for RCD testing be appropriate for the type of RCD?', '["All test equipment works on all RCDs","Type A and Type B RCDs require appropriate test waveforms","Only affects accuracy","Manufacturer preference"]'::jsonb, 1, 'Type A RCDs respond to pulsating DC currents, Type B to smooth DC. Test equipment must generate appropriate waveforms.', 'RCD Testing', 'Advanced', '612.10', NULL, 'inspection-testing'),
  ('rcd-new-12', 'What is the purpose of a Time Delayed (Type S) RCD?', '["Faster protection","Discrimination with downstream RCDs to prevent unwanted tripping","Lower sensitivity","For outdoor use only"]'::jsonb, 1, 'Type S (Selective) RCDs have a time delay to allow downstream RCDs to operate first, providing discrimination.', 'RCD Testing', 'Advanced', '531.2.9', NULL, 'inspection-testing'),
  ('polarity-new-1', 'Why is correct polarity critical in lighting circuits?', '["Only affects brightness","Ensures switches are in phase conductor and centre contact of Edison screw lampholders is not live when lamp removed","Not important in modern installations","Only matters for LED lighting"]'::jsonb, 1, 'Correct polarity ensures switches disconnect the phase conductor and the centre contact of ES lampholders is not live, preventing shock risk.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('polarity-new-2', 'During polarity testing, you find a socket outlet wired with phase and neutral reversed. What is the hazard?', '["No hazard - socket will still work","Equipment may be live when switched off if switch is in neutral","Higher electricity bills","RCD will not operate"]'::jsonb, 1, 'Reversed polarity means switches may disconnect neutral instead of phase, leaving equipment live when apparently switched off.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('polarity-new-3', 'What must be verified during polarity testing of single-pole devices?', '["Colour of wires","That single-pole devices are connected in the phase conductor only","Voltage level","Current rating"]'::jsonb, 1, 'Single-pole switching devices (switches, MCBs, fuses) must be connected in the phase conductor to ensure the circuit is dead when switched off.', 'Polarity Testing', 'Beginner', '132.14.1', NULL, 'inspection-testing'),
  ('polarity-new-4', 'You test polarity at a socket outlet and find phase on the right pin instead of the left. What is the issue?', '["Acceptable variation","Incorrect polarity - phase must be on left (looking at socket)","Temperature dependent","Only critical for 3-phase"]'::jsonb, 1, 'BS 1363 sockets must have phase on the right pin (left when viewed from front), neutral left, and earth at top.', 'Polarity Testing', 'Beginner', '612.6', NULL, 'inspection-testing'),
  ('polarity-new-5', 'When must polarity testing be carried out?', '["Only on new installations","During initial verification and after any circuit alterations","Only if faults are suspected","Every 5 years only"]'::jsonb, 1, 'Polarity must be verified during initial commissioning and after any alterations that could affect polarity.', 'Polarity Testing', 'Beginner', '612.6', NULL, 'inspection-testing'),
  ('polarity-new-6', 'What is the consequence of incorrect polarity in an Edison screw (ES) lampholder?', '["Lamp will not illuminate","Centre contact remains live when lamp removed, creating shock risk","Increased energy consumption","No consequence"]'::jsonb, 1, 'Incorrect polarity leaves the centre contact live when the lamp is removed, creating a significant shock risk when changing bulbs.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('polarity-new-7', 'In a three-phase installation, what must be verified during polarity testing?', '["Only phase rotation","Correct connection of phases and phase rotation","Voltage balance only","Earth conductor continuity"]'::jsonb, 1, 'Three-phase polarity testing verifies correct phase sequence (rotation) and that each phase is correctly connected.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('polarity-new-8', 'How can polarity be verified on a lighting circuit?', '["Visual inspection only","Continuity testing with phase and neutral linked at distribution board","IR testing","Cannot be tested"]'::jsonb, 1, 'Polarity is verified by linking phase and neutral at the board and testing continuity to confirm correct connections at accessories.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('polarity-new-9', 'What must be checked regarding polarity in a consumer unit?', '["Only main switch operation","MCBs and single-pole devices connected in phase conductors, correct busbar arrangement","Colour of wires only","Earth bar connections"]'::jsonb, 1, 'Verify all protective devices are in phase conductors, busbar phases are correct, and neutral connections are properly separated from earth.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('polarity-new-10', 'Why is it dangerous to have a switch in the neutral conductor?', '["Increases electricity costs","Equipment remains live when switched off","Causes voltage drop","Affects power factor"]'::jsonb, 1, 'A switch in the neutral leaves the phase conductor and equipment live when "switched off", creating a shock hazard.', 'Polarity Testing', 'Beginner', '132.14.1', NULL, 'inspection-testing'),
  ('polarity-new-11', 'During polarity testing of a ring final circuit, what confirms correct polarity?', '["Equal resistance readings","Continuity confirmed at all socket outlets with phase and neutral correctly identified","RCD operation","IR test results"]'::jsonb, 1, 'Polarity testing confirms phase and neutral are correctly identified and connected at every socket outlet around the ring.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('polarity-new-12', 'What test method confirms polarity without de-energising the circuit?', '["IR testing","Voltage indicator or proving unit to verify phase conductor","Continuity testing","Cannot test live"]'::jsonb, 1, 'A voltage indicator can verify which conductor is the phase on an energised circuit, though dead testing is preferred for initial verification.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pfc-new-1', 'What is prospective fault current (PFC)?', '["Normal operating current","Maximum current that would flow during a short circuit fault","Earth leakage current","Voltage drop under load"]'::jsonb, 1, 'PFC is the maximum current that would flow in the event of a negligible impedance fault (short circuit) between phase and neutral or phase and earth.', 'Prospective Fault Current', 'Beginner', '612.11', NULL, 'inspection-testing'),
  ('pfc-new-2', 'You measure prospective short circuit current (PSCC) as 4.5kA. The protective device has a breaking capacity of 6kA. Is this acceptable?', '["Yes - breaking capacity exceeds PFC","No - insufficient margin","Yes but needs upgrading","No - use higher rated cable"]'::jsonb, 0, 'The protective device breaking capacity (6kA) must exceed the prospective fault current (4.5kA), so this is acceptable.', 'Prospective Fault Current', 'Intermediate', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-new-3', 'Why must PFC be measured at the origin of an installation?', '["To verify supply authority connection","To ensure protective devices have adequate breaking capacity","To calculate voltage drop","For earth electrode testing"]'::jsonb, 1, 'PFC measurement verifies that protective devices can safely interrupt the maximum possible fault current without damage.', 'Prospective Fault Current', 'Intermediate', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-new-4', 'What is the relationship between Ze and prospective earth fault current (PEFC)?', '["PEFC = Uo × Ze","PEFC = Uo ÷ Ze","PEFC = Ze ÷ Uo","No relationship"]'::jsonb, 1, 'Prospective earth fault current is calculated as PEFC = Uo ÷ Ze, where Uo is nominal voltage to earth (230V).', 'Prospective Fault Current', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('pfc-new-5', 'A consumer unit has MCBs rated at 6kA breaking capacity. The measured PSCC is 7kA. What action is required?', '["Acceptable as close to rating","Replace with higher rated devices (e.g., 10kA)","Increase cable sizes","Install RCD protection"]'::jsonb, 1, 'Protective devices with insufficient breaking capacity are extremely dangerous and must be replaced with adequately rated devices.', 'Prospective Fault Current', 'Intermediate', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-new-6', 'When should PFC testing be carried out?', '["Only on new installations","During initial verification and periodic inspection","Only if faults occur","Every 10 years"]'::jsonb, 1, 'PFC must be verified during initial commissioning and subsequent periodic inspections to ensure continued adequacy of protective devices.', 'Prospective Fault Current', 'Beginner', '612.11', NULL, 'inspection-testing'),
  ('pfc-new-7', 'What does the "kA" rating on an MCB represent?', '["Rated current","Maximum breaking capacity (short circuit current)","Trip sensitivity","Normal operating current"]'::jsonb, 1, 'The kA rating indicates the maximum short circuit current the MCB can safely interrupt without damage.', 'Prospective Fault Current', 'Beginner', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-new-8', 'How is prospective short circuit current (PSCC) between phase and neutral calculated?', '["PSCC = Uo ÷ Ze","PSCC = U0 ÷ (Z1 + Z2)","PSCC = 230 ÷ Zs","PSCC = U ÷ R1"]'::jsonb, 1, 'PSCC is calculated using the voltage between conductors (usually 230V) divided by the impedance of phase and neutral conductors (Z1+Z2).', 'Prospective Fault Current', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('pfc-new-9', 'You measure PSCC as 2.3kA at a distribution board. If you measure further down a circuit, what would you expect?', '["Higher PFC due to cable resistance","Lower PFC due to additional circuit impedance","Same value","Cannot predict"]'::jsonb, 1, 'PFC decreases further from the source as circuit impedance increases, reducing the available fault current.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-new-10', 'What is typically the highest PFC location in an installation?', '["At the furthest socket outlet","At the origin (main consumer unit)","At light fittings","Equal throughout"]'::jsonb, 1, 'PFC is highest at the origin where impedance is lowest, and decreases further along circuits as impedance increases.', 'Prospective Fault Current', 'Beginner', '612.11', NULL, 'inspection-testing'),
  ('pfc-new-11', 'BS 88 fuses typically have what breaking capacity?', '["6kA","10kA","80kA","1kA"]'::jsonb, 2, 'BS 88 fuses have very high breaking capacity, typically 80kA, making them suitable for installations with high prospective fault currents.', 'Prospective Fault Current', 'Advanced', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-new-12', 'What could cause PFC to increase over time at an installation?', '["Cable deterioration","Supply authority network improvements reducing source impedance","Addition of more circuits","PFC always decreases over time"]'::jsonb, 1, 'Improvements to the supply network (upgraded transformers, larger cables) reduce source impedance, potentially increasing PFC.', 'Prospective Fault Current', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('functional-new-1', 'What is the purpose of functional testing?', '["To measure electrical values","To verify correct operation of switchgear, controls, and interlocks","To test cable continuity","To measure power consumption"]'::jsonb, 1, 'Functional testing verifies that switchgear, control equipment, interlocks, and protective devices operate correctly under normal conditions.', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-new-2', 'During functional testing of a two-way lighting circuit, what should be verified?', '["Voltage level only","Lights can be controlled from both switch positions correctly","Cable size","IR values"]'::jsonb, 1, 'Functional testing must verify that two-way (and intermediate) switches correctly control lighting from all positions.', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-new-3', 'You test an emergency stop button. What must be verified?', '["Button colour only","Button latches when pressed, immediately cuts power, requires manual reset","Button makes noise","Voltage at terminals"]'::jsonb, 1, 'Emergency stop devices must latch when operated, immediately interrupt power, and require manual reset before restart is possible.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-new-4', 'When testing RCD mechanical test buttons during functional testing, what should happen?', '["Nothing happens","RCD should trip and require manual reset","LED indicator lights up","Alarm sounds"]'::jsonb, 1, 'Operating the RCD test button should cause the RCD to trip immediately and require manual reset to restore supply.', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-new-5', 'During functional testing of a contactor, what must be verified?', '["Coil resistance value","Clean contact operation, correct control voltage, smooth engagement/disengagement","Current consumption only","Physical size"]'::jsonb, 1, 'Functional testing verifies contactors operate smoothly at correct voltage, engage and disengage cleanly without arcing or sticking.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-new-6', 'What should be checked during functional testing of a time delay relay?', '["Operates at correct time delay and returns to normal state","Physical appearance only","Mounting position","Cannot be tested"]'::jsonb, 0, 'Time delay relays must operate at the correct delay setting and return to their normal state when power is removed.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-new-7', 'During functional testing of a motor starter with overload protection, what must operate correctly?', '["Start/stop controls, overload trip, and reset functions","Motor speed only","Colour of buttons","Noise level"]'::jsonb, 0, 'Verify start and stop controls work correctly, overload protection trips at the correct setting, and reset functions properly.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-new-8', 'You test a bathroom pull-cord light switch. What must be verified functionally?', '["Cord material only","Switch operates correctly, cord is secure, appropriate IP rating","Cord length only","Colour of cord"]'::jsonb, 1, 'Functional testing verifies the switch operates correctly from the cord, cord is securely attached, and IP rating is appropriate for the zone.', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-new-9', 'What should be tested during functional testing of interlocked socket outlets?', '["Voltage level","Interlock prevents access to live parts, correct operation sequence","Socket colour","Current rating"]'::jsonb, 1, 'Interlocks must prevent access to live parts in the correct sequence and operate smoothly without mechanical faults.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-new-10', 'During functional testing of a PIR (motion sensor) lighting control, what should be verified?', '["Sensor detects motion correctly and operates light, time and sensitivity settings work","Sensor appearance only","Installation height","Cable connections only"]'::jsonb, 0, 'Functional testing verifies the PIR detects motion in the intended coverage area, operates the lighting, and adjustable settings function correctly.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-new-11', 'What must be verified during functional testing of a dimmer switch?', '["Full range of dimming operates smoothly without flickering or buzzing","Switch colour","Cable size","IR values"]'::jsonb, 0, 'Dimmer switches must provide smooth dimming across the full range without excessive noise, flickering, or overheating.', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-new-12', 'During functional testing of a shower isolation pull switch, what must be confirmed?', '["Switch operates correctly, clearly indicates on/off position, accessible","Switch is red colour","Cord is exactly 1m long","Neon indicator brightness"]'::jsonb, 0, 'Isolation switches must operate correctly, clearly indicate on/off position, and be accessible within the correct zone.', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-new-13', 'What should be tested on AFDDs (Arc Fault Detection Devices) during functional testing?', '["Only test button operation","Test button operates device and causes trip, manual reset required","Cannot be tested","Visual inspection only"]'::jsonb, 1, 'AFDD functional testing includes operating the test button to verify the device trips and requires manual reset.', 'Functional Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('cont-exp-1', 'When testing ring circuit continuity, all readings at socket outlets should be within what percentage of each other?', '["±5%","±10%","±20%","±50%"]'::jsonb, 0, 'Socket outlet readings should be within ±0.05Ω of each other, indicating a properly connected ring with no breaks or interconnections.', 'Continuity Testing', 'Intermediate', '612.2.2', NULL, 'inspection-testing'),
  ('cont-exp-2', 'What is the purpose of measuring end-to-end resistance before the cross-connection test?', '["To calculate cable length","To verify individual conductor continuity and identify any breaks","To test insulation","To measure earth resistance"]'::jsonb, 1, 'End-to-end testing verifies each conductor is continuous before performing cross-connection tests to detect ring configuration.', 'Continuity Testing', 'Intermediate', '612.2.2', NULL, 'inspection-testing'),
  ('cont-exp-3', 'A radial lighting circuit is 35m long using 1.5mm² twin and CPC. What would be typical R1+R2?', '["0.1Ω to 0.2Ω","0.5Ω to 1.0Ω","1.5Ω to 2.5Ω","3.0Ω to 4.0Ω"]'::jsonb, 1, 'For 1.5mm² with 1.0mm² CPC over 35m, expect R1+R2 approximately 0.7Ω to 0.9Ω depending on temperature and exact cable specification.', 'Continuity Testing', 'Advanced', '612.2.1', NULL, 'inspection-testing'),
  ('cont-exp-4', 'Why must continuity tests be performed before energising a new installation?', '["To comply with regulations only","To verify protective conductor integrity before live testing","To measure cable resistance","To test RCD operation"]'::jsonb, 1, 'Continuity testing confirms protective conductors are continuous and correctly connected before applying voltage, ensuring safety.', 'Continuity Testing', 'Beginner', '612.2', NULL, 'inspection-testing'),
  ('cont-exp-5', 'When performing R1+R2 test on a radial circuit, where should the link be made?', '["At any socket outlet","At the distribution board between phase and CPC","At the midpoint of the circuit","At the furthest point of the circuit"]'::jsonb, 1, 'Phase and CPC are linked at the distribution board, then tested at the furthest point to measure total circuit resistance.', 'Continuity Testing', 'Intermediate', '612.2.1', NULL, 'inspection-testing'),
  ('cont-exp-6', 'A ring circuit test shows end-to-end resistances of 0.6Ω, 0.6Ω, and 0.6Ω for L, N, and CPC. What can be concluded?', '["Ring is broken","Normal readings indicating equal conductor lengths","Interconnection between rings","Test equipment fault"]'::jsonb, 1, 'Equal end-to-end resistances indicate all three conductors are the same length and continuous, as expected in a properly installed ring.', 'Continuity Testing', 'Intermediate', '612.2.2', NULL, 'inspection-testing'),
  ('cont-exp-7', 'What is the maximum acceptable resistance for main protective bonding conductors?', '["0.05Ω","0.1Ω","0.5Ω","1.0Ω"]'::jsonb, 0, 'Main protective bonding conductors should have resistance not exceeding 0.05Ω to ensure effective equipotential bonding.', 'Continuity Testing', 'Intermediate', '612.2.1', NULL, 'inspection-testing'),
  ('cont-exp-8', 'During ring circuit testing, one leg shows much higher resistance than the other. What does this indicate?', '["Normal due to cable routing differences","Possible high-resistance joint or partial break in that leg","Acceptable variation","Test lead error"]'::jsonb, 1, 'Significantly different leg resistances indicate a fault such as a poor connection or partially damaged conductor in the higher-resistance leg.', 'Continuity Testing', 'Advanced', '612.2.2', NULL, 'inspection-testing'),
  ('cont-exp-9', 'Why is it important to use the same type of cable throughout a ring circuit?', '["For aesthetic reasons only","To ensure consistent conductor resistance and prevent imbalance","Not important - any cable can be used","To comply with colour coding"]'::jsonb, 1, 'Using the same cable type ensures both legs have similar resistance, properly sharing the load and preventing overload of one leg.', 'Continuity Testing', 'Intermediate', '612.2.2', NULL, 'inspection-testing'),
  ('cont-exp-10', 'What connection method is used for the R1+R2 test at socket outlets?', '["Phase to earth","Phase to neutral","Neutral to earth","All three conductors together"]'::jsonb, 0, 'R1+R2 test measures resistance between phase (R1) and protective conductor (R2) at each point to verify earthing effectiveness.', 'Continuity Testing', 'Beginner', '612.2.1', NULL, 'inspection-testing'),
  ('cont-exp-11', 'You test a socket ring and find R1+R2 values decreasing towards the midpoint then increasing. What does this confirm?', '["Ring is broken","Ring is correctly wired with no breaks","Interconnection present","Incorrect cable size used"]'::jsonb, 1, 'R1+R2 values lowest at the midpoint (approximately 1/4 of origin value) and increasing towards the ends confirms a properly connected ring.', 'Continuity Testing', 'Advanced', '612.2.2', NULL, 'inspection-testing'),
  ('cont-exp-12', 'What is the significance of nulling test leads before continuity testing?', '["It calibrates the meter","It removes test lead resistance from subsequent measurements","It tests battery condition","It is not necessary"]'::jsonb, 1, 'Nulling (zeroing) test leads compensates for their resistance, ensuring accurate measurements of actual circuit resistance.', 'Continuity Testing', 'Intermediate', '612.2', NULL, 'inspection-testing'),
  ('cont-exp-13', 'When testing supplementary bonding, what is the maximum acceptable resistance?', '["0.01Ω","0.05Ω","0.1Ω","0.5Ω"]'::jsonb, 1, 'Supplementary bonding connections should not exceed 0.05Ω to ensure effective equipotential zone in special locations.', 'Continuity Testing', 'Intermediate', '415.2', NULL, 'inspection-testing'),
  ('cont-exp-14', 'A newly installed ring shows correct end-to-end values, but all socket R1+R2 readings are identical. What is the likely fault?', '["Perfect installation","Ring interconnected or spur wired as ring extension","Cable size incorrect","Test equipment malfunction"]'::jsonb, 1, 'Identical R1+R2 readings at all points suggest incorrect wiring, possibly a spur connected as a ring continuation rather than a true ring.', 'Continuity Testing', 'Advanced', '612.2.2', NULL, 'inspection-testing'),
  ('cont-exp-15', 'What formula is used to calculate expected R1+R2 value for a radial circuit?', '["R1+R2 = (mV/A + mΩ/m) × L","R1+R2 = (R1 × R2) ÷ (R1 + R2)","R1+R2 = (mΩ/m for R1 + mΩ/m for R2) × L ÷ 1000","R1+R2 = Total length ÷ conductor CSA"]'::jsonb, 2, 'R1+R2 = (resistance per meter of phase conductor + resistance per meter of CPC) × circuit length in meters ÷ 1000.', 'Continuity Testing', 'Advanced', '612.2.1', NULL, 'inspection-testing'),
  ('cont-exp-16', 'Why might continuity readings vary slightly between tests on the same circuit?', '["Test equipment is faulty","Temperature changes affecting conductor resistance","Circuit has developed a fault","Voltage fluctuation"]'::jsonb, 1, 'Conductor resistance varies with temperature. Small variations between tests are normal due to ambient or conductor temperature changes.', 'Continuity Testing', 'Intermediate', '612.2', NULL, 'inspection-testing'),
  ('cont-exp-17', 'What is the recommended test current range for low-resistance ohmmeters used in continuity testing?', '["0.1mA to 1mA","4mA to 24mA","200mA minimum","1A to 10A"]'::jsonb, 1, 'GS38 and BS 7671 require continuity testing using current between 4mA and 24mA to avoid damage to electronic components.', 'Continuity Testing', 'Intermediate', '612.2', NULL, 'inspection-testing'),
  ('cont-exp-18', 'During protective conductor continuity testing, what indicates a satisfactory result?', '["Infinite resistance","Low resistance reading (typically < 0.5Ω for most circuits)","Reading above 1.0Ω","Zero resistance"]'::jsonb, 1, 'Protective conductors should show low but measurable resistance, typically well below 0.5Ω, confirming continuity without short circuits.', 'Continuity Testing', 'Beginner', '612.2.1', NULL, 'inspection-testing'),
  ('cont-exp-19', 'A ring circuit shows end-to-end phase resistance of 0.8Ω but CPC resistance of 1.2Ω. What does this indicate?', '["Acceptable - CPC is smaller CSA than phase","Ring fault - all conductors should be identical","Depends on cable type used","Test error - retest required"]'::jsonb, 0, 'CPC in twin and earth cable is typically smaller CSA than phase conductor, so higher CPC resistance is normal and acceptable.', 'Continuity Testing', 'Advanced', '612.2.2', NULL, 'inspection-testing'),
  ('ir-exp-1', 'Why must lamps be removed or switches closed during insulation resistance testing?', '["To save energy","To test the complete circuit wiring including switched conductors","To prevent lamp damage","Not necessary - lamps can remain"]'::jsonb, 1, 'Closing switches or removing lamps ensures all wiring, including switched conductors, is included in the insulation test.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-2', 'What action is required if insulation resistance falls to 0.5MΩ during a periodic inspection?', '["Pass - above SELV minimum","Investigate fault immediately - circuit unsafe","Add RCD protection","Acceptable for older installations"]'::jsonb, 1, 'For 230V circuits, minimum is 1MΩ. A reading of 0.5MΩ indicates serious insulation deterioration requiring immediate investigation.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-3', 'When testing IR on a three-phase distribution board, how many tests are typically required?', '["3 tests","6 tests","9 tests","12 tests"]'::jsonb, 1, 'Six tests: L1-L2, L1-L3, L2-L3, L1-E, L2-E, L3-E to fully verify insulation between all conductors and earth.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-4', 'An IR test on a heating circuit gives 2MΩ when cold but drops to 0.8MΩ when warm. What does this indicate?', '["Normal behaviour","Element insulation breakdown under heat - investigate and replace","Test equipment error","Acceptable for heating circuits"]'::jsonb, 1, 'Insulation resistance should not significantly decrease with heat. Falling below 1MΩ when warm indicates deteriorating insulation.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-5', 'What is the purpose of testing between neutral and earth during IR testing?', '["Not required by BS 7671","To verify neutral is not connected to earth in the circuit","To test neutral conductor insulation","Only required for TT systems"]'::jsonb, 2, 'Testing neutral-earth verifies neutral conductor insulation integrity and detects any insulation faults on the neutral.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-6', 'PIR testing shows all circuits above 50MΩ except one at 1.5MΩ. What action is appropriate?', '["Pass all circuits - all above minimum","Investigate the 1.5MΩ circuit for potential deterioration","Fail entire installation","No action required"]'::jsonb, 1, 'While 1.5MΩ passes minimum requirements, it is significantly lower than other circuits and warrants investigation for early fault detection.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-7', 'What voltage should be used for IR testing on 110V reduced voltage site equipment?', '["250V DC","500V DC","1000V DC","110V DC"]'::jsonb, 1, '110V equipment falls under the up to 500V category, requiring 500V DC test voltage per BS 7671:2018+A2:2022.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-8', 'Why is 500V DC used for IR testing rather than 230V AC?', '["DC is safer","DC applies higher stress to insulation, revealing weaknesses","AC cannot be measured accurately","BS 7671 preference only"]'::jsonb, 1, 'DC test voltage higher than nominal voltage stresses insulation to reveal deterioration that might not appear under normal operating voltage.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-9', 'An outdoor lighting circuit shows IR of 0.9MΩ in wet weather but 25MΩ when dry. What is the problem?', '["Acceptable variation","Moisture ingress through damaged seals or cable entries","Normal for outdoor circuits","Test equipment fault"]'::jsonb, 1, 'Significant IR reduction in wet conditions indicates water ingress through compromised cable glands, damaged fittings, or inadequate IP rating.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-10', 'When can circuits be tested collectively for IR rather than individually?', '["Never - must always test individually","During initial verification if no electronic equipment present","Only on domestic installations","Any time to save time"]'::jsonb, 1, 'Collective testing is permitted during initial verification when no sensitive equipment is connected. Individual testing helps locate faults.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing');
INSERT INTO quiz_questions (question_id, question, options, correct_answer, explanation, category, difficulty, regulation, image_url, course)
VALUES
  ('ir-exp-11', 'What minimum IR is required for circuits exceeding 1000V nominal voltage?', '["0.5MΩ","1.0MΩ","5.0MΩ","10MΩ"]'::jsonb, 1, 'BS 7671 Table 61 specifies minimum 1.0MΩ for all circuits above 500V, though higher values are typically expected for HV.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-12', 'Why should pilot lamps and indicator lights be disconnected before IR testing?', '["They consume power","They provide leakage paths giving false low readings","They might blow","Not necessary to disconnect"]'::jsonb, 1, 'Pilot lamps and indicators contain neon lamps or LEDs with resistors that provide current paths, artificially lowering IR readings.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-13', 'What is the effect of long cable lengths on insulation resistance readings?', '["No effect","Longer cables show lower IR due to increased surface area","Longer cables show higher IR","Only affects continuity, not IR"]'::jsonb, 1, 'Longer cables have more insulation surface area, providing more potential leakage paths, typically resulting in slightly lower IR readings.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-14', 'A socket circuit shows 0.7MΩ phase-earth. You disconnect a computer and retest getting 150MΩ. What does this confirm?', '["Computer is faulty","Computer filter capacitors were providing leakage path","Initial test was incorrect","Socket circuit has a fault"]'::jsonb, 1, 'Computers contain EMI filter capacitors connected between phase/neutral and earth, providing legitimate leakage paths during IR testing.', 'Insulation Resistance', 'Intermediate', '612.3.1', NULL, 'inspection-testing'),
  ('ir-exp-15', 'What is the minimum IR for a 24V DC SELV circuit tested at 250V DC?', '["0.25MΩ","0.5MΩ","1.0MΩ","2.0MΩ"]'::jsonb, 1, 'SELV circuits require minimum 0.5MΩ when tested at 250V DC according to BS 7671:2018+A2:2022 Table 61.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-16', 'How long should the test button be held when proving an IR tester before use?', '["Momentary press","Until reading stabilises, typically 5-10 seconds","30 seconds minimum","Not necessary to hold"]'::jsonb, 1, 'Proving the tester on a known source requires holding until reading stabilises to verify the tester is functioning correctly.', 'Insulation Resistance', 'Beginner', '612.1', NULL, 'inspection-testing'),
  ('ir-exp-17', 'An installation has multiple identical circuits. One shows IR of 80MΩ while others show >200MΩ. Is further investigation needed?', '["No - all pass minimum requirements","Yes - investigate why one circuit differs from others","Only if it drops below 10MΩ","Retest only"]'::jsonb, 1, 'Significant variation between identical circuits suggests potential early deterioration or differences in installation quality requiring investigation.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-18', 'What safety precaution must be observed after completing IR tests on long cable runs?', '["Immediately re-energise","Discharge cable capacitance to earth before touching","Test again at lower voltage","No special precautions needed"]'::jsonb, 1, 'Long cables store charge during high-voltage IR testing. Always discharge to earth before handling to prevent electric shock.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-19', 'During IR testing, the reading climbs slowly from 0.5MΩ to 1.2MΩ over 60 seconds. What does this indicate?', '["Test equipment fault","Capacitive charging effect - normal for long circuits","Circuit failure","Incorrect test voltage"]'::jsonb, 1, 'Slowly rising readings indicate capacitive charging of long cable runs. Always wait for reading to stabilise before recording.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-20', 'What is the recommended minimum IR for new installations, even though 1MΩ is the regulatory minimum?', '["2MΩ","10MΩ","50MΩ","100MΩ"]'::jsonb, 2, 'New installations should typically achieve >50MΩ. Readings just above 1MΩ suggest potential workmanship issues even if compliant.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-21', 'An IR test between phase and neutral reads 0.2MΩ. What is the most likely cause?', '["Acceptable for phase-neutral test","Neon indicator or equipment left connected across phase-neutral","Cable too long","Test equipment malfunction"]'::jsonb, 1, 'Very low phase-neutral IR typically indicates equipment or indicators left connected, as cable insulation alone should give much higher values.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-22', 'What temperature correction factor should be applied to IR test results?', '["Correct all results to 20°C","No temperature correction for IR - record at ambient","Correct to operating temperature","Only correct if below 0°C"]'::jsonb, 1, 'Unlike earth fault loop impedance, insulation resistance is recorded at ambient temperature without correction factors applied.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-23', 'Why might an IR reading appear to fall after the test voltage is first applied?', '["Insulation is failing","Dielectric absorption effect - normal initially","Test equipment fault","Voltage drop"]'::jsonb, 1, 'Initial IR readings may fall due to dielectric absorption as insulation polarises. Readings should stabilise after 15-60 seconds.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-24', 'Can RCDs remain connected during IR testing?', '["Yes - they must remain in circuit","No - RCDs must be short-circuited or removed to prevent damage","Only on TT systems","Only Type AC RCDs"]'::jsonb, 1, 'RCDs must be short-circuited or bypassed during IR testing as the test voltage can damage the RCD electronics and cause false low readings.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-25', 'A circuit IR test shows >200MΩ phase-earth but only 5MΩ neutral-earth. What is indicated?', '["Pass - both above minimum","Neutral insulation fault or neutral-earth connection downstream","Normal variation","Test error"]'::jsonb, 1, 'Low neutral-earth reading suggests insulation fault on neutral or an inadvertent neutral-earth connection in the circuit.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-26', 'What is the minimum IR required for fire alarm cables tested at 500V DC?', '["0.5MΩ","1.0MΩ","5.0MΩ","50MΩ"]'::jsonb, 0, 'Fire alarm systems often use SELV or separated circuits. BS 5839 specifies minimum 0.5MΩ for alarm system circuits.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-exp-27', 'When testing a new installation, you get IR readings of 1.2MΩ. What action should be taken?', '["Pass and certify - above minimum","Investigate cause of low readings before certification","Fail installation","Add RCD protection"]'::jsonb, 1, 'New installations should achieve >50MΩ. Readings barely above minimum suggest workmanship issues, moisture, or equipment left connected.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('rcd-exp-1', 'A 30mA RCD trips at 27mA during ramp testing. Is this acceptable?', '["Fail - below minimum 30mA","Pass - within 50% to 100% of rated current","Borderline - retest required","Fail - must trip at exactly 30mA"]'::jsonb, 1, 'RCDs must trip between 50% (15mA) and 100% (30mA) of rated current. 27mA is within acceptable limits.', 'RCD Testing', 'Intermediate', '612.13.2', NULL, 'inspection-testing'),
  ('rcd-exp-2', 'What is the maximum disconnection time for a 30mA RCD at × 1 rated current?', '["40ms","300ms","500ms","1000ms"]'::jsonb, 1, 'At ×1 rated current (30mA), standard RCDs should disconnect within 300ms. At ×5 (150mA), limit is 40ms.', 'RCD Testing', 'Intermediate', '612.13.2', NULL, 'inspection-testing'),
  ('rcd-exp-3', 'What angle (0° or 180°) should be tested when verifying RCD operation?', '["Only 0°","Only 180°","Both 0° and 180°","Angle is not relevant"]'::jsonb, 2, 'RCDs must be tested at both 0° and 180° phase angles as tripping time can vary depending on the waveform position.', 'RCD Testing', 'Advanced', '612.13.2', NULL, 'inspection-testing'),
  ('rcd-exp-4', 'An S-type (time-delayed) RCD shows 130ms disconnection time at ×1. Is this acceptable?', '["Fail - too slow","Pass - S-type designed for selective coordination","Borderline","Test error"]'::jsonb, 1, 'S-type (selective/time-delayed) RCDs have intentional delay for discrimination. 130-500ms at ×1 is typical and acceptable.', 'RCD Testing', 'Advanced', '612.13.2', NULL, 'inspection-testing'),
  ('rcd-exp-5', 'Why should RCD testing be performed with no load connected?', '["To save energy","Load current affects RCD trip time and may damage equipment","Load prevents RCD operation","Not necessary - can test under load"]'::jsonb, 1, 'Load current adds to test current, affecting trip time. Unexpected disconnection under test could damage equipment or cause data loss.', 'RCD Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('rcd-exp-6', 'A Type A RCD is installed protecting an EV charging point. The test button operates correctly. Is any further testing required?', '["No - test button confirms function","Yes - must perform half-cycle and full electrical testing","Only during initial verification","Only if it fails to trip"]'::jsonb, 1, 'Test button only confirms basic mechanical function. Full electrical testing at ×1/2, ×1, and ×5 with trip time measurement is required.', 'RCD Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('rcd-exp-7', 'What is the purpose of the ×1/2 rated current (15mA for 30mA RCD) test?', '["To verify RCD trips at low current","To verify RCD does NOT trip at this current","To measure trip time","Not a required test"]'::jsonb, 1, 'The ×1/2 test confirms the RCD does NOT trip below its rated sensitivity, avoiding nuisance tripping while ensuring proper margin.', 'RCD Testing', 'Intermediate', '612.13.2', NULL, 'inspection-testing'),
  ('rcd-exp-8', 'A 100mA RCD protecting a fire alarm circuit takes 250ms to trip at ×1. Is this compliant?', '["Pass - within 300ms limit","Fail - too slow for fire protection","Depends on type of RCD","Borderline - retest"]'::jsonb, 0, 'For general purpose RCDs, 300ms at ×1 rated current is the maximum. 250ms is acceptable for this 100mA device.', 'RCD Testing', 'Intermediate', '612.13.2', NULL, 'inspection-testing'),
  ('rcd-exp-9', 'What does it indicate if an RCD fails to trip during ×5 testing but operates correctly at ×1?', '["Normal operation","RCD malfunction - investigate and replace","Test equipment fault","Acceptable variation"]'::jsonb, 1, 'RCD should trip faster at higher currents. Failure at ×5 but operation at ×1 indicates a fault in the RCD mechanism.', 'RCD Testing', 'Advanced', '612.13.2', NULL, 'inspection-testing'),
  ('rcd-exp-10', 'Can multiple RCDs in series both be tested using the same test procedure?', '["Yes - test both simultaneously","Yes - but test downstream RCD first, then upstream","No - can only test one at a time","Not permitted to have RCDs in series"]'::jsonb, 1, 'Test downstream (load side) RCDs first to verify they trip before upstream devices, confirming proper discrimination.', 'RCD Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('rcd-exp-11', 'A Type AC RCD is protecting a heat pump. Is this compliant with BS 7671:2018+A2:2022?', '["Yes - Type AC suitable for all applications","No - Type A or Type F required for electronic loads","Yes if under 30mA","Depends on heat pump manufacturer"]'::jsonb, 1, 'Electronic equipment like heat pumps can produce DC residual current. Type A minimum (or Type F/B) is required for such loads.', 'RCD Testing', 'Advanced', '531.3.3', NULL, 'inspection-testing'),
  ('rcd-exp-12', 'During periodic inspection, an RCD that previously tripped at 25ms now trips at 35ms at ×5. What action is required?', '["Pass - still within 40ms limit","Monitor and note deterioration - consider replacement","Fail and replace immediately","No action - normal aging"]'::jsonb, 1, 'While still compliant, increasing trip time indicates mechanical wear. Note the trend and consider replacement to prevent future failure.', 'RCD Testing', 'Advanced', '612.13.2', NULL, 'inspection-testing'),
  ('rcd-exp-13', 'What minimum number of RCD tests are required during periodic inspection?', '["Test button only","×1/2 and test button","×1 and ×5 with trip times, plus test button","All tests including ×1/2, ×1, ×5, and test button"]'::jsonb, 2, 'Periodic inspection requires ×1 and ×5 tests with trip time measurement, plus operation of test button. ×1/2 optional but recommended.', 'RCD Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('rcd-exp-14', 'An RCD protecting socket outlets in a bathroom does not trip during ×1 testing. What is the most appropriate action?', '["Reset and retest","Replace RCD immediately - safety critical location","Check test equipment","Acceptable if test button works"]'::jsonb, 1, 'Failed RCD in bathroom is a serious safety issue. Replace immediately as it will not provide fault protection, creating shock risk.', 'RCD Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('pol-exp-1', 'What is the primary safety reason for correct polarity?', '["Equipment efficiency","Ensures switches disconnect phase conductor, making circuit dead when off","Prevents RCD operation","Reduces voltage drop"]'::jsonb, 1, 'Correct polarity ensures single-pole switching devices disconnect the phase conductor, making the circuit dead and safe when switched off.', 'Polarity Testing', 'Beginner', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-2', 'During polarity testing of a ring circuit, what should be verified at each socket?', '["Voltage level only","Phase on right pin, neutral on left, earth at top (viewed from front)","Earth continuity only","Socket is secure"]'::jsonb, 1, 'BS 1363 sockets must have phase on right (left when viewed from front), neutral on left (right from front), earth at top.', 'Polarity Testing', 'Beginner', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-3', 'A lighting circuit has the switch in the neutral. What safety risk does this create?', '["Lights will not work","Circuit remains live when switched off - shock risk","Excessive voltage drop","RCD will trip"]'::jsonb, 1, 'Neutral switching leaves the phase conductor live to the lampholder even when switched off, creating serious electric shock hazard.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-4', 'How is polarity verified on a de-energised installation?', '["Visual inspection only","Link phase-neutral at board, test continuity at outlets","Cannot test when de-energised","Insulation resistance test"]'::jsonb, 1, 'De-energised polarity testing: link phase and neutral at the board, then test continuity to verify correct connections at accessories.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-5', 'In a three-phase installation, what does phase sequence (rotation) testing verify?', '["Voltage level","Correct L1-L2-L3 order for motor rotation direction","Earth integrity","Insulation resistance"]'::jsonb, 1, 'Phase sequence testing ensures L1-L2-L3 are connected in correct order so three-phase motors rotate in the intended direction.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-6', 'What terminal should the phase conductor connect to in an ES (Edison Screw) lampholder?', '["Outer threaded shell","Centre contact","Either terminal","Earth terminal"]'::jsonb, 1, 'Phase must connect to the centre contact so the threaded shell remains at neutral potential, reducing shock risk when changing lamps.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-7', 'During live polarity testing, you find phase on the left pin of a socket (viewed from front). What action is required?', '["Acceptable variation","Reverse phase and neutral - incorrect polarity","No action if earth is correct","Replace socket only"]'::jsonb, 1, 'Phase on left pin (right from front) is reversed polarity. Phase and neutral must be corrected to prevent safety hazards.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-8', 'Why is polarity testing particularly important in locations with metal accessories?', '["Metal looks better","Incorrect polarity can make metal accessories live under fault","Metal affects test readings","Not particularly important"]'::jsonb, 1, 'Reversed polarity combined with insulation fault can make metal switch plates or accessories live, creating serious shock hazard.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-9', 'How can polarity be verified at the distribution board?', '["Visual inspection only","Verify all protective devices in phase conductors, check busbar connections","Continuity test","Not necessary at distribution board"]'::jsonb, 1, 'Verify MCBs/fuses are in phase conductors, check busbar phase connections, and confirm neutral bar is separate from earth.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-10', 'A double-pole switch controls a water heater. Is polarity testing still required?', '["No - double-pole disconnects both conductors","Yes - verify connections are correct even if both poles switch","Only if visible damage","Not applicable to double-pole devices"]'::jsonb, 1, 'Even with double-pole switching, verify correct phase/neutral identification to ensure proper connections throughout the installation.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-11', 'During alterations to a ring circuit, when must polarity be re-verified?', '["Not necessary if only adding sockets","After any alteration that could affect polarity or connections","Only during periodic inspection","Never needs re-verification"]'::jsonb, 1, 'Any alteration affecting conductors or connections requires polarity re-verification to ensure safe configuration is maintained.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-12', 'What polarity fault can cause a three-phase motor to run in reverse?', '["Phase-neutral swap","Incorrect phase sequence (rotation)","Missing earth","Low voltage"]'::jsonb, 1, 'Swapping any two phases reverses the phase sequence, causing three-phase motors to rotate backwards, potentially damaging equipment.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-13', 'When testing polarity on a new installation using the R1+R2 method, what reading indicates correct polarity?', '["Infinite resistance","Continuity (low resistance) between phase and CPC at outlets","Zero resistance","Same as insulation resistance"]'::jsonb, 1, 'With phase-CPC linked at board, continuity at outlets confirms phase conductor is correctly identified and connected.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-14', 'A lighting circuit has two-way switching. How should polarity testing be conducted?', '["Test only at one switch position","Test with switches in all positions to verify correct connections","Not applicable to two-way circuits","Visual inspection only"]'::jsonb, 1, 'Test two-way switching in all positions to verify correct connections and ensure phase conductor is properly controlled.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-15', 'What voltage indicator should be used for live polarity testing?', '["Neon screwdriver","Approved voltage indicator to GS38","Multimeter on any setting","Any volt stick"]'::jsonb, 1, 'Live testing requires an approved voltage indicator complying with GS38, with fused leads and finger guards for safety.', 'Polarity Testing', 'Intermediate', 'GS38', NULL, 'inspection-testing'),
  ('pol-exp-16', 'In a caravan park supply pillar, why is correct socket polarity critical?', '["For billing purposes","Reversed polarity affects entire caravan installation safety","To prevent voltage drop","Not particularly critical"]'::jsonb, 1, 'Incorrect polarity at caravan inlet results in reversed polarity throughout the caravan, affecting all switches and creating serious safety hazards.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-17', 'An immersion heater has a single-pole isolator. Which conductor must the isolator switch?', '["Neutral only","Phase conductor only","Either phase or neutral","Both phase and neutral"]'::jsonb, 1, 'Single-pole isolation must disconnect the phase conductor to ensure the circuit is dead and safe when isolated.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-18', 'How many polarity tests are required for a typical three-phase distribution board with 12 single-phase circuits?', '["3 tests - one per phase","12 tests - one per circuit","One test at the incoming supply","No testing required if new board"]'::jsonb, 1, 'Each circuit must be individually tested to verify correct phase-neutral identification and single-pole device connections.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-19', 'During periodic inspection, you find multiple circuits with reversed polarity. What is the most likely cause?', '["Supply authority error","Incorrect connections at distribution board or meter position","Cable deterioration","Normal variation"]'::jsonb, 1, 'Multiple reversed circuits indicate systematic error, most likely at the distribution board where phase/neutral are swapped.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-20', 'What is the consequence of reversed polarity on a socket outlet circuit protected by a Type AC RCD?', '["RCD will not operate","RCD still operates but switches remain live when off","No safety consequence","RCD trips immediately"]'::jsonb, 1, 'RCD detects imbalance regardless of polarity, but reversed polarity means appliance switches may not fully isolate live conductor.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-21', 'Can polarity testing be omitted if all installation work was visually inspected?', '["Yes - visual inspection is sufficient","No - testing must verify connections are correct","Only on new installations","Only if certified by electrician"]'::jsonb, 1, 'Visual inspection alone cannot verify internal connections. Polarity testing must be performed to confirm correct connections.', 'Polarity Testing', 'Beginner', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-22', 'At what points should polarity be verified in a radial lighting circuit?', '["Only at the distribution board","At all switches and lampholders/ceiling roses","Only at the first and last light","Not required for lighting circuits"]'::jsonb, 1, 'Verify polarity at all switches (ensuring phase switching) and lighting points (ensuring centre contact is phased for ES lampholders).', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-23', 'A DIY installation has phase and neutral correctly connected but earth and neutral are swapped. What is the danger?', '["None - earth and neutral are both at low potential","Protective conductor carries load current, bonding becomes ineffective","Only affects testing","Acceptable in some situations"]'::jsonb, 1, 'Swapped neutral-earth results in load current on protective conductor, voltage on earthed metalwork, and ineffective fault protection.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-24', 'When must polarity testing be performed in the testing sequence?', '["First test to be performed","After continuity testing but before insulation testing","Last test after all others","Any time during testing"]'::jsonb, 1, 'Polarity testing follows continuity (confirming conductors are continuous) but precedes insulation and live tests.', 'Polarity Testing', 'Intermediate', '612', NULL, 'inspection-testing'),
  ('pol-exp-25', 'In an older installation with no CPC, how can polarity be verified?', '["Cannot be tested","Use live testing with voltage indicator at each point","Not required for installations without CPC","Visual inspection only"]'::jsonb, 1, 'Installations without CPC require live polarity testing using an approved voltage indicator to verify phase-neutral orientation.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-26', 'What should be checked for polarity at a cooker control unit?', '["Nothing - not applicable","Switch in phase conductor, correct socket polarity if present","Earth connection only","Voltage level only"]'::jsonb, 1, 'Verify the switch pole is in phase conductor and, if the unit includes a socket, that socket polarity is correct.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-27', 'Why is polarity testing required even when all cables are correctly colour-coded?', '["Colour coding may be non-standard","Cables can be mis-terminated despite correct colours","Not required if colours correct","Only for insurance purposes"]'::jsonb, 1, 'Correct cable colours do not guarantee correct termination. Physical testing verifies connections are made correctly at all points.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-28', 'How does incorrect polarity affect appliance RCD plugs?', '["No effect on operation","May prevent proper RCD operation or cause nuisance tripping","Improves safety","Only affects older RCD types"]'::jsonb, 1, 'Some RCD plugs are polarity-sensitive. Reversed polarity can affect their operation or cause spurious tripping.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-29', 'What polarity aspect should be verified in a commercial kitchen installation?', '["Nothing specific","All isolators switch phase, socket polarity correct, three-phase sequence verified","Earth only","Visual check sufficient"]'::jsonb, 1, 'Commercial kitchens require careful polarity verification: isolators in phase, sockets correct, three-phase motors properly sequenced.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-30', 'An FCU (Fused Connection Unit) has a neon indicator. What polarity aspect is critical?', '["No polarity requirements","Fuse must be in phase conductor so circuit is dead when fuse removed","Fuse can be in either conductor","Only earth connection matters"]'::jsonb, 1, 'The fuse must be in the phase conductor so removing it makes the load side dead, ensuring safety when servicing equipment.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-31', 'During polarity testing using continuity method, what indicates a correctly wired socket outlet?', '["No continuity between phase and CPC","Continuity between linked phase-neutral and the phase pin","High resistance reading","Infinite resistance"]'::jsonb, 1, 'With phase-neutral linked at board, continuity to phase pin at socket confirms phase conductor is correctly identified and connected.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pol-exp-32', 'What safety device is minimally affected by reversed polarity but still requires correction?', '["MCB","RCD","AFDD","Fuse"]'::jsonb, 1, 'RCDs detect imbalance regardless of polarity, but reversed polarity must still be corrected to ensure switches properly isolate phase.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('inspect-visual-adv-1', 'You inspect a TT system installation. What must be present that is not required in TN systems?', '["Main switch","Earth electrode and 30mA RCD protection for all circuits","Surge protection","Isolation transformer"]'::jsonb, 1, 'TT systems require an earth electrode and RCD protection (typically 30mA) on all circuits because fault loop impedance is higher than TN systems.', 'Visual Inspection', 'Advanced', '411.5.2', NULL, 'inspection-testing'),
  ('inspect-visual-adv-2', 'During inspection of a consumer unit, you notice mixed neutral and earth connections. What is the hazard?', '["Increased voltage drop","Creates parallel earth paths, RCD malfunction, and potential shock risk","No hazard if connections are tight","Only affects power factor"]'::jsonb, 1, 'Mixed neutral and earth connections create dangerous parallel paths, prevent RCD operation, and can result in exposed metalwork becoming live.', 'Visual Inspection', 'Advanced', '411.3.1', NULL, 'inspection-testing'),
  ('inspect-visual-adv-3', 'What minimum cable burial depth is required for cables without additional mechanical protection?', '["300mm","450mm for general applications, 750mm under roads","100mm","600mm all applications"]'::jsonb, 1, 'Cables without additional protection require burial at minimum 450mm depth (general) or 750mm under roads subject to vehicular traffic.', 'Visual Inspection', 'Advanced', '522.8.10', NULL, 'inspection-testing'),
  ('inspect-visual-adv-4', 'You inspect a cable run through a timber joist. What protection is required?', '["No protection needed","Cable must be 50mm from edge or have mechanical protection against nail penetration","Only fire barrier needed","Cable clips every 100mm"]'::jsonb, 1, 'Cables in joists must be at least 50mm from the edge or protected by steel plates or capping to prevent damage from nails/screws.', 'Visual Inspection', 'Intermediate', '522.6.204', NULL, 'inspection-testing'),
  ('inspect-visual-adv-5', 'During inspection of a swimming pool installation, what is the minimum zone requirement for electrical equipment?', '["No special requirements","IPX4 minimum in Zone 1, IPX5 for Zone 0","All equipment must be 3m from pool edge","Only 12V equipment permitted"]'::jsonb, 1, 'Swimming pool zones have specific IP rating requirements: Zone 0 requires IPX8, Zone 1 requires IPX4 or IPX5 (depending on jet usage).', 'Visual Inspection', 'Advanced', '702.410.3.4', NULL, 'inspection-testing'),
  ('continuity-calc-1', 'A 35m circuit uses 1.5mm² phase conductor and 1.0mm² CPC. Calculate expected R1+R2 (copper resistivity 18.1mΩ/m for 1mm²).', '["0.52Ω","1.15Ω","0.80Ω","1.45Ω"]'::jsonb, 1, '1.5mm² = 12.1mΩ/m, 1.0mm² = 18.1mΩ/m. R1+R2 = (12.1 + 18.1) × 35m × 1.2 multiplier = 1.27Ω. Answer 1.15Ω is within acceptable range.', 'Continuity Testing', 'Advanced', '612.2.1', NULL, 'inspection-testing'),
  ('continuity-calc-2', 'Ring circuit: R1 end-to-end = 0.40Ω, R2 end-to-end = 0.65Ω. What should r1+r2 be at socket midpoint?', '["0.26Ω","1.05Ω","0.52Ω","0.13Ω"]'::jsonb, 0, 'At midpoint: r1+r2 = (R1+R2)÷4 = (0.40+0.65)÷4 = 1.05÷4 = 0.26Ω (approximately, allowing for socket connections).', 'Continuity Testing', 'Advanced', '612.2.2', NULL, 'inspection-testing'),
  ('continuity-calc-3', 'You measure R2 as 0.75Ω on a 50m lighting circuit. Is this acceptable for 1.5mm² CPC?', '["No, too high","Yes, within expected range for 1.5mm²","Indicates broken conductor","Needs temperature correction"]'::jsonb, 1, '1.5mm² copper = 12.1mΩ/m. Expected R2 = 12.1 × 50 × 1.2 = 0.73Ω. Measured 0.75Ω is acceptable considering connections.', 'Continuity Testing', 'Advanced', '612.2.1', NULL, 'inspection-testing'),
  ('continuity-calc-4', 'What is the maximum resistance for supplementary bonding between exposed-conductive-parts protected by a 30mA RCD?', '["0.05Ω","1667Ω","50V ÷ 30mA = 1667Ω (but must verify adequate ADS)","0.5Ω"]'::jsonb, 2, 'For RCD protection: R ≤ 50V ÷ IΔn. With 30mA RCD: 50÷0.03 = 1667Ω. However, automatic disconnection must still be verified.', 'Continuity Testing', 'Advanced', '415.2.2', NULL, 'inspection-testing'),
  ('ir-fault-1', 'IR test shows 0.3MΩ phase to earth. What is the most likely cause?', '["Normal reading","Moisture ingress, damaged insulation, or connected equipment","Cable too long","Test voltage too low"]'::jsonb, 1, 'IR below 1MΩ indicates insulation failure, commonly caused by moisture, physical damage, or equipment left connected during testing.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-fault-2', 'IR reads 0.8MΩ with all loads disconnected. After disconnecting one circuit, reading improves to 150MΩ. What does this indicate?', '["Test instrument fault","The disconnected circuit has low insulation resistance","Temperature effect","Normal variation"]'::jsonb, 1, 'Significant improvement when isolating one circuit clearly identifies that circuit as having the insulation fault.', 'Insulation Resistance', 'Intermediate', '612.3.2', NULL, 'inspection-testing'),
  ('ir-fault-3', 'You test IR on a new installation and get 50MΩ. After one week, it drops to 5MΩ. What could cause this?', '["Normal settling","Moisture ingress through cable glands or damaged insulation","Temperature change only","Acceptable variation"]'::jsonb, 1, 'Rapid deterioration indicates moisture ingress, likely through poorly sealed cable glands, incomplete sealing, or damaged cable.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('efli-scenario-1', 'Ze = 0.30Ω, circuit length 45m using 2.5mm²/1.5mm² cable. Expected Zs?', '["0.50Ω","1.20Ω","0.30Ω","1.78Ω"]'::jsonb, 1, 'R1+R2 for 2.5/1.5mm² = 19.5mΩ/m. Over 45m: 0.88Ω. Zs = Ze + R1+R2 = 0.30 + 0.88 = 1.18Ω (approximately 1.20Ω).', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('efli-scenario-2', 'You measure Zs = 0.85Ω on a 16A Type B circuit. Maximum permitted (80%) is 2.87Ω. What is the fault current?', '["16A","271A","80A","160A"]'::jsonb, 1, 'Fault current If = U0 ÷ Zs = 230V ÷ 0.85Ω = 271A. This exceeds 5× rated current (80A) ensuring fast disconnection.', 'Earth Fault Loop Impedance', 'Advanced', '411.4.4', NULL, 'inspection-testing'),
  ('efli-scenario-3', 'A distribution circuit protected by 63A Type C MCB shows Zs = 0.45Ω. Maximum (80%) = 0.46Ω. Acceptable?', '["Pass - just within limit","Borderline - investigate but may pass considering measurement uncertainty","Fail - too close","Retest required"]'::jsonb, 1, 'At 0.45Ω vs 0.46Ω limit, this is borderline. Considering measurement uncertainty (±5%), engineer judgment required but likely acceptable.', 'Earth Fault Loop Impedance', 'Advanced', '411.4.9', NULL, 'inspection-testing'),
  ('efli-scenario-4', 'TT system with Ze = 85Ω. What protection is essential for all circuits?', '["Type B MCBs only","30mA RCD on all circuits (TT systems cannot meet ADS by overcurrent alone)","Larger cable sizes","Surge protection"]'::jsonb, 1, 'TT systems have high Ze values making overcurrent protection alone insufficient. 30mA RCDs are essential for automatic disconnection.', 'Earth Fault Loop Impedance', 'Advanced', '411.5.2', NULL, 'inspection-testing'),
  ('rcd-scenario-1', 'A 30mA RCD trips at 28mA during ×1 test. What action is required?', '["Pass - very sensitive is good","Fail - RCD too sensitive, likely to cause nuisance tripping","Normal operation","Test again"]'::jsonb, 1, 'The RCD should NOT trip at ×1 rated current (×0.5 to ×1). Tripping at 28mA indicates excessive sensitivity and potential nuisance tripping.', 'RCD Testing', 'Advanced', '612.10', NULL, 'inspection-testing'),
  ('rcd-scenario-2', 'Time-delayed (Type S) RCD shows 50ms trip time at ×5. Maximum permitted is 130ms. Verdict?', '["Fail - too fast for Type S","Pass - within 130ms limit for Type S at ×1, but check if deliberate delay is functioning","Retest needed","Replace RCD"]'::jsonb, 1, 'Type S RCDs have intentional delay for discrimination. 50ms at ×5 is fast - verify this is within the device specification for selectivity.', 'RCD Testing', 'Advanced', '531.2.9', NULL, 'inspection-testing'),
  ('rcd-scenario-3', 'You test an RCD protecting EV charging point. What type is required for DC fault detection?', '["Type AC","Type A","Type B (detects smooth DC residual current)","Any 30mA RCD"]'::jsonb, 2, 'EV charging requires Type B RCD protection to detect smooth DC residual currents that Type A and AC cannot detect.', 'RCD Testing', 'Advanced', '722.531.2.101', NULL, 'inspection-testing'),
  ('rcd-scenario-4', 'An RCD shows 35ms at ×1, 15ms at ×5. What is the issue?', '["Perfect operation","Should not trip at ×1 - RCD is faulty or excessive earth leakage present","Normal for Type A","Temperature dependent"]'::jsonb, 1, 'The RCD should not trip at ×1 rated current. Tripping at 35ms indicates a fault with the RCD or excessive earth leakage in the installation.', 'RCD Testing', 'Advanced', '612.10', NULL, 'inspection-testing'),
  ('polarity-special-1', 'In a caravan installation, what additional polarity check is essential?', '["Only socket polarity","Inlet connector polarity to prevent reversed supply connection","Light switches only","No special requirements"]'::jsonb, 1, 'Caravan inlet polarity must be verified to ensure mains connection cannot result in reversed polarity throughout the installation.', 'Polarity Testing', 'Advanced', '721.514.1', NULL, 'inspection-testing'),
  ('polarity-special-2', 'You test polarity on a three-phase motor circuit. Phase sequence is L2-L3-L1 instead of L1-L2-L3. What happens?', '["Motor will not start","Motor will rotate in reverse direction","No effect","Motor will overheat"]'::jsonb, 1, 'Incorrect phase sequence (rotation) causes three-phase motors to rotate in reverse direction, potentially damaging connected equipment.', 'Polarity Testing', 'Advanced', '612.6', NULL, 'inspection-testing'),
  ('polarity-special-3', 'During polarity testing of lighting with metal switch plates, why is correct polarity critical?', '["Aesthetics only","Ensures metal switch plate will not become live if neutral/earth fault occurs","Improves light output","Reduces electricity costs"]'::jsonb, 1, 'Correct polarity ensures switches are in the phase conductor, preventing metal accessories becoming live under fault conditions.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pfc-calc-1', 'Ze = 0.20Ω measured at origin. What is the prospective earth fault current (PEFC)?', '["46A","1150A","230A","4600A"]'::jsonb, 1, 'PEFC = U0 ÷ Ze = 230V ÷ 0.20Ω = 1150A. This determines the minimum breaking capacity required for protective devices.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-calc-2', 'PSCC measured as 8.5kA at origin. Protective devices are rated 6kA. What must be done?', '["Acceptable - close enough","Replace all devices with minimum 10kA rated devices","Add RCD protection","Increase cable sizes"]'::jsonb, 1, 'Protective devices must have breaking capacity exceeding PFC. 6kA devices are inadequate for 8.5kA - replace with 10kA minimum rated devices.', 'Prospective Fault Current', 'Intermediate', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-calc-3', 'Phase-neutral loop impedance = 0.15Ω. Calculate PSCC (phase-neutral fault current).', '["230A","1533A","460A","3066A"]'::jsonb, 1, 'PSCC = U0 ÷ Zloop = 230V ÷ 0.15Ω = 1533A. This is the maximum fault current between phase and neutral.', 'Prospective Fault Current', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('functional-adv-1', 'During functional testing of a photovoltaic (PV) system, what must be verified?', '["Panel voltage only","Isolators operate correctly, AC and DC sides, RCD protection, emergency shutdown","Panel cleanliness","Cable colours"]'::jsonb, 1, 'PV systems require verification of AC and DC isolation, RCD protection, emergency shutdown procedures, and correct operation of all switchgear.', 'Functional Testing', 'Advanced', '712.537.2.1.5', NULL, 'inspection-testing'),
  ('functional-adv-2', 'Testing a fire alarm system call point. What must operate correctly?', '["Break-glass operates, alarm sounds, indicator lights, system records activation","Colour of housing only","Mounting height","Glass type"]'::jsonb, 0, 'Call points must operate the alarm when activated, provide visual indication, and the control panel must log the activation correctly.', 'Functional Testing', 'Advanced', '560.7.1', NULL, 'inspection-testing'),
  ('functional-adv-3', 'During functional testing of an RCBO, what must be verified that an RCD alone would not cover?', '["Nothing additional","Overcurrent protection operates at correct rating in addition to RCD function","Only RCD function","Only overcurrent function"]'::jsonb, 1, 'RCBOs combine RCD and MCB. Functional testing must verify both overcurrent protection and residual current detection operate correctly.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('regs-safety-1', 'What is the maximum touch voltage permitted under fault conditions in normal environments?', '["230V","50V AC","120V","25V AC"]'::jsonb, 1, 'BS 7671 limits touch voltage to 50V AC in normal environments to prevent dangerous electric shock under fault conditions.', 'Regulations', 'Intermediate', '411.3.2.2', NULL, 'inspection-testing'),
  ('regs-safety-2', 'A lighting circuit trips intermittently. What systematic approach should you take?', '["Replace all lamps","Isolate, test IR, check connections, test individual lights, measure Zs","Reset and ignore","Replace MCB immediately"]'::jsonb, 1, 'Systematic fault-finding: isolate safely, test insulation resistance, inspect connections for loose/damaged wiring, test individual loads.', 'Safety', 'Intermediate', '612.1', NULL, 'inspection-testing'),
  ('regs-safety-3', 'What is the minimum cross-sectional area for a protective conductor in a circuit with 70A overcurrent protection?', '["6mm²","10mm²","Calculated using adiabatic equation or tables","16mm²"]'::jsonb, 2, 'CPC size is determined by adiabatic equation S = √(I²t)/k or BS 7671 Table 54.7, considering fault current and disconnection time.', 'Regulations', 'Advanced', '543.1.3', NULL, 'inspection-testing'),
  ('regs-safety-4', 'When is an Electrical Installation Certificate (EIC) required?', '["Only for commercial buildings","For new installations and additions/alterations creating new circuits","Only every 10 years","Never required for domestic"]'::jsonb, 1, 'An EIC is required for new installations and additions/alterations to existing installations that include new circuits.', 'Regulations', 'Beginner', '631.1', NULL, 'inspection-testing'),
  ('regs-safety-5', 'What documentation must be provided to the person ordering electrical work?', '["Invoice only","Electrical Installation Certificate and/or Minor Works Certificate as appropriate","Guarantee only","No documentation required"]'::jsonb, 1, 'Upon completion, appropriate certification (EIC, MEIWC, or EIC for additions/alterations) must be provided to the person ordering work.', 'Regulations', 'Beginner', '631.1', NULL, 'inspection-testing'),
  ('regs-safety-6', 'What is the maximum interval between periodic inspections for a commercial office?', '["5 years","10 years","Recommended 5 years but determined by risk assessment","1 year"]'::jsonb, 2, 'Commercial premises typically 5 years, but actual interval determined by competent person considering type of installation, use, and maintenance.', 'Regulations', 'Intermediate', '622.1', NULL, 'inspection-testing'),
  ('regs-safety-7', 'Under Building Regulations, who can self-certify domestic electrical work in England?', '["Any electrician","Registered competent person scheme members (e.g., NICEIC, NAPIT)","Only electrical engineers","No one can self-certify"]'::jsonb, 1, 'Only electricians registered with competent person schemes can self-certify notifiable electrical work, avoiding Building Control notification.', 'Regulations', 'Intermediate', 'Part P', NULL, 'inspection-testing'),
  ('regs-safety-8', 'What Schedule of Test Results must accompany an EIC?', '["Photos only","Schedule of Inspections and Schedule of Test Results","Site diary","Material list"]'::jsonb, 1, 'EIC must be accompanied by Schedule of Inspections and Schedule of Test Results documenting all verification tests performed.', 'Regulations', 'Beginner', '631.1', NULL, 'inspection-testing');
INSERT INTO quiz_questions (question_id, question, options, correct_answer, explanation, category, difficulty, regulation, image_url, course)
VALUES
  ('special-bath-1', 'What is the maximum socket outlet rating permitted in a domestic bathroom?', '["13A socket with RCD","Shaver socket (BS EN 61558-2-5) only","6A socket in zone 3","No restriction"]'::jsonb, 1, 'Only shaver sockets complying with BS EN 61558-2-5 are permitted in bathrooms. Standard 13A sockets are prohibited.', 'Visual Inspection', 'Advanced', '701.512.3', NULL, 'inspection-testing'),
  ('special-bath-2', 'Supplementary bonding in bathrooms: when can it be omitted?', '["Never - always required","When all circuits have 30mA RCD protection and main bonding is satisfactory","Only in new builds","If plastic pipes used"]'::jsonb, 1, 'Supplementary bonding can be omitted if all circuits have 30mA RCD additional protection and main equipotential bonding is effective.', 'Visual Inspection', 'Advanced', '701.415.2', NULL, 'inspection-testing'),
  ('special-bath-3', 'An electric shower is rated at 9.5kW, 230V. What minimum cable size is required if installed using clipped direct method (current-carrying capacity)?', '["4mm²","6mm²","10mm² (9.5kW ÷ 230V = 41.3A, requires cable rated >41.3A)","16mm²"]'::jsonb, 2, '9.5kW ÷ 230V = 41.3A. Clipped direct: 10mm² has capacity ~57A (depending on installation method), 6mm² insufficient at ~38A.', 'Visual Inspection', 'Advanced', '523.1', NULL, 'inspection-testing'),
  ('special-bath-4', 'What is bathroom Zone 0?', '["Area above bath","Interior of bath/shower basin (direct water contact)","60cm around bath","Entire bathroom"]'::jsonb, 1, 'Zone 0 is the interior of the bath or shower basin where equipment is subject to submersion or direct water jets.', 'Visual Inspection', 'Intermediate', '701.32', NULL, 'inspection-testing'),
  ('special-bath-5', 'An extractor fan is installed in bathroom zone 1. What minimum IP rating is required?', '["IP20","IPX4 (protection against water splashing)","IP65","IPX7"]'::jsonb, 1, 'Equipment in bathroom zone 1 must have minimum IPX4 protection against water splashing from any direction.', 'Visual Inspection', 'Intermediate', '701.512.2', NULL, 'inspection-testing'),
  ('special-outdoor-1', 'For outdoor socket outlets, what is the maximum RCD rating for additional protection?', '["100mA","30mA","300mA","10mA"]'::jsonb, 1, 'Outdoor socket outlets up to 32A must be protected by 30mA RCD for additional protection against direct and indirect contact.', 'Visual Inspection', 'Intermediate', '411.3.3', NULL, 'inspection-testing'),
  ('special-outdoor-2', 'In agricultural installations, what maximum disconnection time is required for circuits?', '["0.4 seconds","0.2 seconds (due to increased shock risk)","5 seconds","1 second"]'::jsonb, 1, 'Agricultural installations require 0.2 seconds maximum disconnection due to reduced body resistance from wet conditions and animal contact.', 'Earth Fault Loop Impedance', 'Advanced', '705.411.1', NULL, 'inspection-testing'),
  ('special-outdoor-3', 'What RCD protection is required for socket outlets in agricultural locations?', '["30mA for all socket outlets","30mA for up to 32A, 100mA for higher ratings with supplementary bonding","No RCD needed","300mA is sufficient"]'::jsonb, 0, 'All socket outlets in agricultural installations must have 30mA RCD protection due to increased risk of electric shock.', 'Visual Inspection', 'Advanced', '705.422.7', NULL, 'inspection-testing'),
  ('special-caravan-1', 'What is the maximum protective device rating for a caravan pitch supply?', '["32A","16A maximum for caravan pitch outlets","20A","10A"]'::jsonb, 1, 'Caravan pitch socket outlets must be protected by overcurrent device not exceeding 16A rating.', 'Visual Inspection', 'Advanced', '708.553.1.15', NULL, 'inspection-testing'),
  ('special-caravan-2', 'In a caravan installation, what is the maximum disconnection time for final circuits?', '["0.4 seconds","5 seconds","0.4s but 30mA RCD required for all socket outlets","1 second"]'::jsonb, 2, 'Caravan final circuits require 0.4s disconnection, and all socket outlets must have 30mA RCD additional protection.', 'Earth Fault Loop Impedance', 'Advanced', '721.411.1', NULL, 'inspection-testing'),
  ('special-ev-1', 'What type of RCD is required for Mode 3 EV charging installations?', '["Type AC","Type A minimum, Type B recommended for DC fault detection","Any 30mA RCD","No RCD required"]'::jsonb, 1, 'EV charging requires minimum Type A RCD, but Type B is recommended to detect smooth DC residual currents from EV chargers.', 'RCD Testing', 'Advanced', '722.531.2.101', NULL, 'inspection-testing'),
  ('special-ev-2', 'What additional device must be provided for EV charging installations?', '["Nothing additional","PEN fault protection device (where PME supply)","Surge arrestor","Isolation transformer"]'::jsonb, 1, 'EV charging on PME supplies requires PEN fault protection to prevent dangerous voltages on the vehicle if PEN conductor fails.', 'Visual Inspection', 'Advanced', '722.411.4.1', NULL, 'inspection-testing'),
  ('special-ev-3', 'An EV charger installation has Zs = 0.75Ω and is RCD protected. What is the maximum permitted Zs?', '["1667Ω","Must still meet maximum Zs for overcurrent device (additional protection)","5Ω","No limit with RCD"]'::jsonb, 1, 'Even with RCD protection, Zs must not exceed the maximum value for the protective device to ensure discrimination and fault clearance.', 'Earth Fault Loop Impedance', 'Advanced', '411.4.9', NULL, 'inspection-testing'),
  ('testing-equipment-1', 'Before using test equipment, what must be verified?', '["Battery level only","Calibration date valid, proving unit confirms operation, no damage","Brand name","Colour coding"]'::jsonb, 1, 'Test equipment must be within calibration period, proven to be functioning correctly using a proving unit, and visually inspected for damage.', 'Safety', 'Intermediate', '612.1', NULL, 'inspection-testing'),
  ('testing-equipment-2', 'What is the purpose of a proving unit?', '["To charge test equipment","To verify voltage indicators and test equipment are functioning correctly before and after use","To measure current","To test RCDs"]'::jsonb, 1, 'A proving unit confirms voltage indicators and test equipment are working correctly before proving dead and after testing.', 'Safety', 'Beginner', '612.1', NULL, 'inspection-testing'),
  ('testing-equipment-3', 'When testing a circuit live for Zs, what safety precautions are essential?', '["No precautions needed","Ensure supply is stable, use insulated probes, verify test leads, inform occupants of brief supply interruption","Disconnect all loads","Test in darkness"]'::jsonb, 1, 'Live Zs testing requires stable supply, good quality insulated test leads, and warning occupants of brief interruption during test.', 'Safety', 'Intermediate', '612.9', NULL, 'inspection-testing'),
  ('advanced-calc-1', 'A 100A single-phase supply has maximum demand of 85A. What is the diversity factor?', '["85%","118% (100÷85)","15%","1.85"]'::jsonb, 0, 'Diversity factor = (Maximum demand ÷ Connected load) × 100. If demand is 85A on 100A supply, diversity is 85%.', 'Regulations', 'Advanced', 'Appendix 1', NULL, 'inspection-testing'),
  ('advanced-calc-2', 'Calculate voltage drop: 230V supply, 45A load, 35m cable, R=0.80mΩ/m. Is 4% limit met?', '["Yes, VD = 1.26V (0.55%)","No, VD = 1.26V (0.55%) - well within 4% (9.2V) limit","Yes, VD = 12.6V","No, exceeds limit"]'::jsonb, 1, 'VD = I × R × L = 45A × 0.80mΩ/m × 35m = 1.26V. Percentage = (1.26÷230)×100 = 0.55%. Well within 4% (9.2V) limit.', 'Regulations', 'Advanced', '525', NULL, 'inspection-testing'),
  ('advanced-calc-3', 'A circuit has fault current of 1500A and disconnects in 0.1s. Using k=115 for copper/PVC, calculate minimum CPC.', '["1.5mm²","1.38mm² → use 1.5mm² (S = √(I²t)/k)","2.5mm²","4mm²"]'::jsonb, 1, 'Adiabatic equation: S = √(I²t)/k = √(1500² × 0.1)/115 = √225000/115 = 1.38mm². Use next standard size: 1.5mm².', 'Regulations', 'Advanced', '543.1.3', NULL, 'inspection-testing'),
  ('fault-finding-1', 'RCD trips when a specific circuit is energised. IR test shows 2MΩ. What is the likely cause?', '["Low IR - replace cable","Earth leakage current exceeding 30mA despite adequate IR (capacitive coupling, connected equipment)","Faulty RCD","High Zs"]'::jsonb, 1, 'Good IR (2MΩ) but RCD tripping indicates cumulative earth leakage current >30mA from cable capacitance, filters, or equipment.', 'RCD Testing', 'Advanced', '531.2', NULL, 'inspection-testing'),
  ('fault-finding-2', 'MCB trips immediately when switched on, but IR test shows >200MΩ. What could cause this?', '["Low IR","Short circuit at load, faulty appliance, or loose connection causing arcing","Normal operation","Temperature"]'::jsonb, 1, 'Good IR but immediate tripping indicates a short circuit or very low resistance fault when circuit is energised, possibly in connected load.', 'Safety', 'Advanced', '612', NULL, 'inspection-testing'),
  ('fault-finding-3', 'Lighting circuit works but measured Zs is higher than expected. R1+R2 test is correct. What is the issue?', '["High Ze (external impedance issue) - verify earth connection at origin","Test leads faulty","Normal variation","Temperature effect"]'::jsonb, 0, 'If R1+R2 is correct but Zs is high, Ze is elevated. Check main earthing terminal connection and supply earth.', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('fault-finding-4', 'Ring circuit shows equal phase and neutral resistances end-to-end, but one socket has very high r1+r2. What does this indicate?', '["Correct - normal variation","Broken CPC ring at or near that socket location","Faulty test equipment","Temperature effect"]'::jsonb, 1, 'Equal R1 and R2 indicate intact rings, but high r1+r2 at one point indicates broken CPC ring between that socket and the board.', 'Continuity Testing', 'Advanced', '612.2.2', NULL, 'inspection-testing'),
  ('certification-1', 'When is a Minor Electrical Installation Works Certificate (MEIWC) appropriate?', '["Any electrical work","Additions/alterations not extending to new circuits (e.g., additional socket on existing circuit)","New installations only","Replacement consumer units"]'::jsonb, 1, 'MEIWC is used for additions/alterations to existing circuits that do not include new circuits. New circuits require EIC.', 'Regulations', 'Intermediate', '631.4', NULL, 'inspection-testing'),
  ('certification-2', 'Who can sign an Electrical Installation Certificate as "Designer"?', '["Anyone","The person responsible for design of the installation or circuit","Only electrical engineers","Building owner"]'::jsonb, 1, 'The Designer section must be signed by the person responsible for the design, who may be the same person as the installer/inspector.', 'Regulations', 'Intermediate', '631.1', NULL, 'inspection-testing'),
  ('certification-3', 'What Condition Report code indicates immediate danger?', '["C3","C1 - Danger present, immediate remedial action required","C2","FI"]'::jsonb, 1, 'C1 indicates danger present requiring immediate remedial action. Installation cannot be used until rectified.', 'Regulations', 'Intermediate', '631.5', NULL, 'inspection-testing'),
  ('certification-4', 'On an EICR, what does classification C2 mean?', '["Satisfactory","Potentially dangerous - urgent remedial action required","Minor fault - improvement recommended","Dangerous - do not use"]'::jsonb, 1, 'C2 indicates potentially dangerous condition requiring urgent remedial action to prevent danger.', 'Regulations', 'Intermediate', '631.5', NULL, 'inspection-testing'),
  ('certification-5', 'What does classification C3 on an EICR indicate?', '["Dangerous","Improvement recommended (not compliant with current standards but not dangerous)","Satisfactory","Urgent action needed"]'::jsonb, 1, 'C3 indicates improvement recommended. Installation does not comply with current standards but is not currently dangerous.', 'Regulations', 'Intermediate', '631.5', NULL, 'inspection-testing'),
  ('protection-1', 'What is the operating principle of an AFDD (Arc Fault Detection Device)?', '["Detects overcurrent","Detects characteristic arc fault signatures and disconnects supply","Detects earth leakage","Detects overvoltage"]'::jsonb, 1, 'AFDDs detect dangerous arc faults (series and parallel) that may not trip conventional protective devices, preventing electrical fires.', 'Visual Inspection', 'Advanced', '421.1.7', NULL, 'inspection-testing'),
  ('protection-2', 'Where are AFDDs now required in domestic installations?', '["All circuits","AC final circuits rated up to 32A supplying socket outlets (recommended for fire risk reduction)","Only lighting circuits","Not required"]'::jsonb, 1, 'AFDDs are recommended (not mandatory) for socket outlet circuits up to 32A to provide protection against fire caused by arc faults.', 'Visual Inspection', 'Advanced', '421.1.7', NULL, 'inspection-testing'),
  ('protection-3', 'What is the purpose of an SPD (Surge Protection Device)?', '["Protect against overcurrent","Limit transient overvoltages and divert surge currents","Detect earth faults","Provide isolation"]'::jsonb, 1, 'SPDs protect equipment from transient overvoltages (lightning, switching) by limiting voltage and diverting surge current to earth.', 'Visual Inspection', 'Advanced', '443.4', NULL, 'inspection-testing'),
  ('protection-4', 'An SPD has a protection level (Up) of 1.5kV. What does this mean?', '["Maximum operating voltage","Maximum voltage that will appear at terminals when conducting surge current","Trip sensitivity","Energy rating"]'::jsonb, 1, 'Protection level (Up) is the maximum voltage that will appear at the SPD terminals when limiting a surge, protecting connected equipment.', 'Visual Inspection', 'Advanced', '443.4', NULL, 'inspection-testing'),
  ('special-pool-1', 'What is Zone 0 in a swimming pool location?', '["Area outside the pool","Interior of the pool basin","Area within 2m of the pool edge","Changing room area"]'::jsonb, 1, 'Zone 0 is the interior of the pool basin where water is normally present. Only specific SELV equipment (max 12V AC) can be installed.', 'Visual Inspection', 'Advanced', '702.410.3.4.1', NULL, 'inspection-testing'),
  ('special-pool-2', 'What is the maximum voltage allowed for equipment in Zone 0 of a swimming pool?', '["25V AC","12V AC","50V AC","110V AC"]'::jsonb, 1, 'Zone 0 permits only SELV equipment with a maximum of 12V AC rms or 30V ripple-free DC, with the safety source located outside zones 0, 1, and 2.', 'Visual Inspection', 'Advanced', '702.410.3.4.1', NULL, 'inspection-testing'),
  ('special-pool-3', 'What is the extent of Zone 1 in a swimming pool installation?', '["Limited to pool basin only","From floor to 2.5m above floor, within 2m from edge of Zone 0","Entire pool hall","Changing rooms only"]'::jsonb, 1, 'Zone 1 extends from the floor to 2.5m above, within a horizontal distance of 2m from the edge of Zone 0.', 'Visual Inspection', 'Advanced', '702.32', NULL, 'inspection-testing'),
  ('special-pool-4', 'What type of RCD protection is required for socket outlets in Zone 2 of a pool installation?', '["No RCD required","300mA RCD","30mA RCD","RCD protection not permitted"]'::jsonb, 2, 'All socket outlets in Zone 2 must be protected by a 30mA RCD and be at least 1.25m from Zone 1.', 'RCD Testing', 'Advanced', '702.53', NULL, 'inspection-testing'),
  ('special-pool-5', 'Can a standard 230V socket outlet be installed in Zone 1 of a pool?', '["Yes, with 30mA RCD","Yes, if IP rating is adequate","No, not permitted in Zone 1","Yes, if RCBO protected"]'::jsonb, 2, 'Socket outlets are not permitted in Zone 0 or Zone 1. They may only be installed in Zone 2 or outside the zones.', 'Visual Inspection', 'Advanced', '702.53', NULL, 'inspection-testing'),
  ('special-medical-1', 'What defines a Group 2 medical location?', '["Ordinary patient treatment rooms","Locations where loss of supply could cause danger to life","Storage areas","Staff facilities"]'::jsonb, 1, 'Group 2 medical locations are where loss of supply could cause danger to life, such as operating theatres, intensive care units.', 'Visual Inspection', 'Advanced', '710.1', NULL, 'inspection-testing'),
  ('special-medical-2', 'What is the purpose of a medical IT system in Group 2 medical locations?', '["To provide internet connectivity","To ensure continuity of supply during a first fault to earth","To reduce energy costs","To power medical computers"]'::jsonb, 1, 'Medical IT systems provide unearthed supply allowing continuation of treatment during a first earth fault, protecting patients from electric shock.', 'Visual Inspection', 'Advanced', '710.411.6', NULL, 'inspection-testing'),
  ('special-medical-3', 'What is the maximum earth leakage current for a socket outlet in a Group 2 medical location patient environment?', '["0.5mA","3.5mA","10mA","30mA"]'::jsonb, 0, 'Socket outlets in the patient environment of Group 2 locations must have earth leakage current not exceeding 0.5mA to protect cardiac patients.', 'Visual Inspection', 'Advanced', '710.413.1.5', NULL, 'inspection-testing'),
  ('special-pv-1', 'Why is DC isolation required for photovoltaic installations?', '["To reduce energy costs","To allow safe maintenance and firefighting by disconnecting DC side","To improve efficiency","Not required"]'::jsonb, 1, 'DC isolation allows safe disconnection of the PV array from the inverter for maintenance and provides firefighter safety.', 'Visual Inspection', 'Advanced', '712.537.2.1', NULL, 'inspection-testing'),
  ('special-pv-2', 'Where must DC cables from PV arrays to inverters be installed?', '["Any convenient route","As short as practicable, avoiding living areas, minimising fire risk","Underground only","In metal conduit"]'::jsonb, 1, 'DC cables should be as short as practicable, avoiding living areas where possible to minimise fire risk from DC arc faults.', 'Visual Inspection', 'Advanced', '712.522.8.3', NULL, 'inspection-testing'),
  ('special-pv-3', 'What type of RCD protection is NOT suitable for PV installations?', '["Type B RCD","Type AC RCD","Type A RCD","Type F RCD"]'::jsonb, 1, 'Type AC RCDs are unsuitable as inverters produce DC residual currents. Type A minimum required, Type B preferred for smooth DC component.', 'RCD Testing', 'Advanced', '712.531.2', NULL, 'inspection-testing'),
  ('special-pv-4', 'At what voltage do PV DC circuits pose a direct contact shock risk?', '["12V DC","50V DC","120V DC and above","1000V DC"]'::jsonb, 2, 'PV DC circuits at 120V DC and above pose direct contact electric shock risk and require appropriate protection measures.', 'Visual Inspection', 'Advanced', '712.410.3.6', NULL, 'inspection-testing'),
  ('special-pv-5', 'Why are overvoltage protection devices (SPDs) recommended for PV installations?', '["To reduce electricity bills","To protect against lightning-induced surges to DC and AC sides","To improve efficiency","To comply with warranty requirements only"]'::jsonb, 1, 'SPDs protect against lightning and switching overvoltages on both DC (PV side) and AC (grid connection) sides of the system.', 'Visual Inspection', 'Advanced', '712.443', NULL, 'inspection-testing'),
  ('special-ag-1', 'Why are agricultural premises considered high-risk locations?', '["High energy costs","Presence of livestock, conductive surfaces, moisture, corrosive substances","Remote locations only","Large power requirements"]'::jsonb, 1, 'Agricultural premises present increased risks due to livestock, conductive surfaces (metal structures), moisture, dust, and corrosive substances.', 'Visual Inspection', 'Intermediate', '705.1', NULL, 'inspection-testing'),
  ('special-ag-2', 'What is the maximum disconnection time for socket outlets in agricultural locations?', '["5 seconds","0.4 seconds","0.2 seconds","1 second"]'::jsonb, 1, 'Socket outlets up to 32A in agricultural premises require 30mA RCD protection and 0.4 second maximum disconnection time.', 'RCD Testing', 'Intermediate', '705.411.1', NULL, 'inspection-testing'),
  ('special-ag-3', 'What supplementary equipotential bonding is required in livestock areas?', '["None required","Bonding of exposed and extraneous conductive parts accessible to livestock","Main bonding only","Bonding of metal structures only"]'::jsonb, 1, 'All exposed and extraneous conductive parts accessible to livestock must be bonded to reduce shock risk from lower body resistance of animals.', 'Continuity Testing', 'Intermediate', '705.415.2.1', NULL, 'inspection-testing'),
  ('special-caravan-1', 'What is the maximum rating of overcurrent protective device for a caravan pitch socket?', '["6A","16A","32A","63A"]'::jsonb, 1, 'Caravan pitch socket outlets must be rated at 16A maximum and protected by individual 30mA RCD.', 'Visual Inspection', 'Intermediate', '708.553.1.13', NULL, 'inspection-testing'),
  ('special-caravan-2', 'What type of cable must be used for the connection between caravan pitch socket and caravan?', '["Standard flex","H07RN-F flexible cable minimum","PVC twin and earth","Any outdoor cable"]'::jsonb, 1, 'Connections must use flexible cable type H07RN-F or equivalent to withstand outdoor conditions, movement, and mechanical stress.', 'Visual Inspection', 'Intermediate', '721.521.2', NULL, 'inspection-testing'),
  ('special-caravan-3', 'What protective measures must be provided for caravan pitch supplies?', '["MCB protection only","30mA RCD and overcurrent protection","Main switch only","Isolator only"]'::jsonb, 1, 'Each caravan pitch supply must have individual 30mA RCD protection and appropriate overcurrent protection (typically 16A MCB).', 'RCD Testing', 'Intermediate', '708.415.1', NULL, 'inspection-testing'),
  ('special-ev-1', 'What type of RCD is required for electric vehicle charging points?', '["Type AC","Type A or Type B","Type S","No RCD required"]'::jsonb, 1, 'EV charging requires Type A RCD minimum (Type B preferred) due to DC leakage currents from vehicle charging systems.', 'RCD Testing', 'Advanced', '722.531.2', NULL, 'inspection-testing'),
  ('special-ev-2', 'What is the minimum rating for a domestic EV charging point circuit?', '["16A","20A","32A","40A"]'::jsonb, 2, 'Domestic EV charging points typically require 32A dedicated circuit for Mode 3 charging (7.4kW single-phase).', 'Visual Inspection', 'Advanced', '722.311', NULL, 'inspection-testing'),
  ('special-ev-3', 'Why must EV charging points have their own dedicated circuit?', '["To reduce costs","To prevent prolonged high loads affecting other circuits and ensure safe operation","Not required","For aesthetic reasons"]'::jsonb, 1, 'Dedicated circuits prevent prolonged high loads affecting other circuits and ensure proper cable sizing for continuous current demand.', 'Visual Inspection', 'Intermediate', '722.311', NULL, 'inspection-testing'),
  ('special-ev-4', 'What earthing arrangement check is essential for EV charging installations?', '["No earth required","Verify TN-S or TN-C-S system and Ze; TT systems require special consideration","Visual check only","Earth electrode resistance only"]'::jsonb, 1, 'TN-S or TN-C-S preferred. TT systems require careful assessment. PME earth may require additional protection due to lost neutral risks.', 'Earth Fault Loop Impedance', 'Advanced', '722.411', NULL, 'inspection-testing'),
  ('calc-advanced-1', 'A 100m three-phase circuit uses 10mm² copper cable. With mV/A/m of 4.4, what is voltage drop at 40A per phase?', '["8.8V","17.6V","35.2V","44V"]'::jsonb, 1, 'Voltage drop = mV/A/m × Load × Length / 1000 = 4.4 × 40 × 100 / 1000 = 17.6V. Must not exceed 5% of 400V (20V).', 'Regulations', 'Advanced', '525', NULL, 'inspection-testing'),
  ('calc-advanced-2', 'A circuit has R1 = 0.8Ω and R2 = 1.3Ω at 20°C. What is R1+R2 at 70°C conductor temperature?', '["2.1Ω","2.52Ω","2.73Ω","3.15Ω"]'::jsonb, 1, 'Apply correction factor: (230 + 70) / (230 + 20) = 1.2. R1+R2 at 70°C = 2.1 × 1.2 = 2.52Ω for copper conductors.', 'Continuity Testing', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('calc-advanced-3', 'A 16mm² cable carries 60A. With grouped in conduit (0.7) and 35°C ambient (0.94), what is effective capacity?', '["73A","91A","65.8A","58A"]'::jsonb, 2, 'Nominal capacity 100A × 0.7 (grouping) × 0.94 (temperature) = 65.8A. This exceeds 60A load, so acceptable.', 'Regulations', 'Advanced', '523', NULL, 'inspection-testing'),
  ('calc-advanced-4', 'Calculate the design current for a 15kW three-phase motor at 400V with 0.85 power factor and 90% efficiency:', '["25.3A","28.3A","30.6A","35A"]'::jsonb, 1, 'Ib = P / (√3 × U × pf × efficiency) = 15000 / (√3 × 400 × 0.85 × 0.9) = 28.3A per phase.', 'Regulations', 'Advanced', '311', NULL, 'inspection-testing'),
  ('calc-advanced-5', 'A TN-S system has Ze = 0.35Ω. Circuit R1+R2 = 0.85Ω. What is maximum Zs value?', '["0.35Ω","0.85Ω","1.20Ω","2.40Ω"]'::jsonb, 2, 'Zs = Ze + (R1 + R2) = 0.35 + 0.85 = 1.20Ω. This must not exceed the maximum Zs for the protective device.', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('calc-advanced-6', 'A 50m ring final circuit uses 2.5/1.5mm² cable. R1 end-to-end = 1.48Ω, Rn = 1.48Ω, R2 = 2.46Ω. Is ring intact?', '["No, values too high","Yes, values indicate complete ring","Cannot determine","No, R2 should equal R1"]'::jsonb, 1, 'End-to-end readings approximately correct for 100m total length. R1+R2 at origin should be (1.48+2.46)/4 = 0.985Ω indicating intact ring.', 'Continuity Testing', 'Advanced', '612.2.2', NULL, 'inspection-testing'),
  ('calc-advanced-7', 'Calculate minimum CSA for a 12kW shower, 45m run, max 5% drop on 230V, copper conductors (18mV/A/m):', '["6mm²","10mm²","16mm²","25mm²"]'::jsonb, 1, 'I = 12000/230 = 52.2A. Max drop = 11.5V. CSA = mV/A/m × I × L / drop = 18 × 52.2 × 45 / 11.5 = 3667, so 10mm² minimum.', 'Regulations', 'Advanced', '525', NULL, 'inspection-testing'),
  ('fault-complex-1', 'An RCD trips only when a specific appliance is used. All others work. What is the likely fault?', '["RCD is faulty","Earth fault in the specific appliance or its supply flex","Circuit overload","Main earthing fault"]'::jsonb, 1, 'If RCD trips only with one appliance, the fault is in that appliance (earth leakage to exposed conductive parts or flex damage).', 'RCD Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('fault-complex-2', 'Lights dim when large load switches on. Circuit breakers do not trip. What is probable cause?', '["Inadequate earthing","Excessive voltage drop due to undersized cables or loose connections","RCD fault","Overload"]'::jsonb, 1, 'Dimming without tripping indicates excessive voltage drop from undersized cables, loose connections, or high Ze causing voltage drop.', 'Visual Inspection', 'Intermediate', '525', NULL, 'inspection-testing'),
  ('fault-complex-3', 'MCB trips immediately on closing. Insulation test shows >200MΩ. What should be investigated?', '["Insulation resistance","Short circuit between live conductors (L-N fault)","Earth fault","Overload"]'::jsonb, 1, 'High insulation but immediate MCB trip indicates L-N short circuit. Insulation test between L-N would not detect this.', 'Continuity Testing', 'Advanced', '612', NULL, 'inspection-testing'),
  ('fault-complex-4', 'A socket ring shows R1+R2 at midpoint is same as at origin. What fault exists?', '["Perfect ring circuit","Broken ring - operating as radial circuit","Excellent conductivity","No fault detected"]'::jsonb, 1, 'In intact ring, midpoint R1+R2 should be approximately 1/4 of origin value. Equal readings indicate broken ring.', 'Continuity Testing', 'Advanced', '612.2.2', NULL, 'inspection-testing'),
  ('fault-complex-5', 'Measured Zs = 1.8Ω, Ze = 0.4Ω. Circuit R1+R2 = 0.6Ω. What is indicated?', '["All values correct","High resistance in circuit protective conductor (poor connection)","Test equipment fault","Normal readings"]'::jsonb, 1, 'Zs should equal Ze + (R1+R2) = 1.0Ω. Measured 1.8Ω indicates additional resistance (poor connection) in protective conductor.', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('fault-complex-6', 'Insulation test reads 0.5MΩ on one circuit. After disconnecting loads, reads >200MΩ. What was the issue?', '["Cable fault","Connected equipment with filters/capacitors affecting reading","Moisture ingress","Test equipment error"]'::jsonb, 1, 'Equipment containing filters, capacitors, or surge protectors create alternative current paths giving false low readings.', 'Insulation Testing', 'Intermediate', '612.3.1', NULL, 'inspection-testing'),
  ('fault-complex-7', 'Socket outlets on one circuit show no voltage. MCB has not tripped. Test between L-N at board shows 230V. Fault location?', '["Supply fault","Open circuit in live conductor between board and sockets","Earth fault","Neutral fault"]'::jsonb, 1, 'Voltage at board but not at outlets with MCB closed indicates open circuit (broken conductor or loose connection) in live conductor.', 'Continuity Testing', 'Intermediate', '612', NULL, 'inspection-testing'),
  ('fault-complex-8', 'RCD trips randomly, especially in wet weather. What is most likely cause?', '["Overload","Moisture ingress causing earth leakage in cables or equipment","MCB fault","Voltage fluctuation"]'::jsonb, 1, 'Random RCD trips worsening in wet weather indicate moisture ingress causing earth leakage current exceeding RCD threshold.', 'RCD Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('amd2-1', 'Under Amendment 2:2022, what is required for consumer units in escape routes?', '["Metal construction","Metal construction or enclosed in cabinet/enclosure constructed of non-combustible material","Plastic acceptable","Fire alarm integration"]'::jsonb, 1, 'Consumer units in escape routes must be metal or enclosed in non-combustible material to prevent fire spread.', 'Regulations', 'Advanced', '421.1.201', NULL, 'inspection-testing'),
  ('amd2-2', 'What does Amendment 2:2022 require regarding arc fault protection in new domestic installations?', '["Mandatory for all circuits","AFDDs recommended (not mandatory) for socket outlet circuits","Not mentioned","Required for lighting only"]'::jsonb, 1, 'AFDDs are recommended for AC final circuits ≤32A supplying socket outlets in domestic premises to reduce fire risk.', 'Visual Inspection', 'Advanced', '421.1.7', NULL, 'inspection-testing'),
  ('amd2-3', 'Under Amendment 2:2022, when are SPDs required in buildings?', '["All installations","Where consequences of overvoltage could result in serious injury, loss of life, or public service disruption","Domestic only","Commercial only"]'::jsonb, 1, 'SPDs required when overvoltage consequences are serious. Risk assessment determines necessity based on building and supply characteristics.', 'Visual Inspection', 'Advanced', '443.4', NULL, 'inspection-testing'),
  ('amd2-4', 'What update does Amendment 2:2022 make to socket outlet circuits in TT systems?', '["No changes","30mA RCD protection now mandatory for all socket outlets","RCD not required","Applies to outdoor sockets only"]'::jsonb, 1, 'All socket outlets rated up to 32A in TT systems must have 30mA RCD protection for enhanced shock protection.', 'RCD Testing', 'Advanced', '411.3.3', NULL, 'inspection-testing'),
  ('practical-1', 'Customer reports electric shock from washing machine. What immediate action should you take?', '["Test the machine","Disconnect machine, test RCD operation, inspect earthing arrangements","Replace machine","Check water supply"]'::jsonb, 1, 'Immediate safety priority: disconnect appliance, verify RCD protection works, check earthing and bonding before any repairs.', 'RCD Testing', 'Intermediate', '131.3', NULL, 'inspection-testing'),
  ('practical-2', 'During EICR, you find aluminium cables terminated in brass terminals. What code?', '["C1 - Danger present","C2 - Potentially dangerous","C3 - Improvement recommended","Satisfactory"]'::jsonb, 1, 'Dissimilar metals create galvanic corrosion, increasing resistance and fire risk. Code C2 requires remedial action.', 'Visual Inspection', 'Intermediate', '526.2', NULL, 'inspection-testing'),
  ('practical-3', 'You discover no main protective bonding to gas or water. Property has TN-C-S supply. What code?', '["C3","C2","C1 - Danger present, requires immediate action","Satisfactory"]'::jsonb, 2, 'Missing main protective bonding in TN-C-S system presents immediate danger of electric shock and must be coded C1.', 'Continuity Testing', 'Intermediate', '411.3.1.2', NULL, 'inspection-testing'),
  ('practical-4', 'Installing shower circuit. Customer wants to use existing 6mm² cable run. Shower is 9.5kW. Is this acceptable?', '["Yes, 6mm² suitable","No, requires minimum 10mm² depending on installation method and length","Yes with RCD","No, minimum 16mm² required"]'::jsonb, 1, '9.5kW = 41.3A. Depending on installation method, 6mm² may be inadequate. Must check current-carrying capacity and voltage drop.', 'Regulations', 'Advanced', '523', NULL, 'inspection-testing'),
  ('practical-5', 'Testing new bathroom installation. Socket outlet discovered 1.8m from bath edge. What action?', '["Acceptable","Remove socket immediately - not permitted within 3m of bath/shower","Add RCD protection","Install IP rated socket"]'::jsonb, 1, 'Socket outlets not permitted within 3m horizontal distance of Zone 1 boundary (bath/shower) in bathrooms.', 'Visual Inspection', 'Intermediate', '701.512.3', NULL, 'inspection-testing'),
  ('practical-6', 'Customer wants socket outlet in garage. Existing circuit has 20A Type B MCB. Is socket addition acceptable?', '["Yes, if diversity allows","Must check circuit load, cable capacity, Zs, and voltage drop before adding","No, needs dedicated circuit","Yes, always acceptable"]'::jsonb, 1, 'Must verify circuit capacity, cable sizing, earth fault loop impedance, and voltage drop meet requirements for additional load.', 'Regulations', 'Intermediate', '134.1.1', NULL, 'inspection-testing'),
  ('practical-7', 'Installing outdoor lighting. Customer does not want RCD protection as it "keeps tripping". What is your response?', '["Install without RCD as requested","Explain RCD is mandatory for outdoor circuits; investigate tripping cause","Use higher rated RCD","Install MCB only"]'::jsonb, 1, 'RCD protection mandatory for outdoor circuits. Must educate customer and investigate root cause of tripping rather than remove safety feature.', 'RCD Testing', 'Intermediate', '411.3.3', NULL, 'inspection-testing'),
  ('practical-8', 'EICR reveals kitchen sockets not RCD protected. Installation pre-dates 17th Edition. What code?', '["Satisfactory - complied when installed","C3 - Improvement recommended","C2 - Potentially dangerous, recommend upgrade","C1 - Danger present"]'::jsonb, 2, 'While compliant when installed, lack of RCD protection presents increased risk. Code C2 appropriate for potentially dangerous situation.', 'RCD Testing', 'Intermediate', '411.3.3', NULL, 'inspection-testing'),
  ('polarity-advanced-1', 'During polarity testing, you find phase connected to neutral terminal at socket. What are the safety implications?', '["None - circuit still works","Serious shock risk - appliances energised even when switched off","Minor issue","Only affects efficiency"]'::jsonb, 1, 'Reversed polarity means appliances remain live when switched off, presenting serious electric shock and fire risk.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('polarity-advanced-2', 'What is the correct method to verify polarity of a lighting circuit?', '["Visual inspection only","Test between phase and CPC at each point, verify switching in phase conductor","Check voltage at switches","Continuity test only"]'::jsonb, 1, 'Polarity verified by testing phase-CPC continuity and confirming all single-pole devices break phase conductor only.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('polarity-advanced-3', 'Why must polarity testing verify centre contact of ES lampholders connects to phase?', '["For efficiency","To minimise shock risk during lamp changes","For aesthetics","Not required"]'::jsonb, 1, 'Centre contact must be phase so it is isolated when lamp removed, reducing shock risk. Outer thread remains earthed.', 'Polarity Testing', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('pfc-advanced-1', 'Why must prospective fault current (PFC) be measured during testing?', '["To calculate energy bills","To verify protective devices can safely interrupt maximum fault current","To set RCD sensitivity","Not required for domestic"]'::jsonb, 1, 'PFC determines maximum current during short circuit. Protective devices must have adequate breaking capacity (kA rating) to interrupt safely.', 'PFC Testing', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('pfc-advanced-2', 'PFC measured at 8kA. Consumer unit has 6kA rated MCBs. What action required?', '["Acceptable","Replace MCBs with minimum 10kA rated devices","Add RCD protection","Reduce supply current"]'::jsonb, 1, 'MCB breaking capacity must exceed maximum PFC. 6kA MCBs inadequate for 8kA PFC - immediate replacement required.', 'PFC Testing', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('pfc-advanced-3', 'What is typical PFC range for TN-S supply in domestic installation?', '["0.1kA to 0.5kA","1kA to 3kA","10kA to 16kA","20kA to 30kA"]'::jsonb, 1, 'TN-S supplies typically provide 1-3kA PFC. TN-C-S generally higher. Important for selecting adequate protective device ratings.', 'PFC Testing', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('pfc-advanced-4', 'Where should PFC be measured in an installation?', '["At consumer unit main switch only","At origin (consumer unit) and at furthest points of circuits","Not required","At socket outlets only"]'::jsonb, 1, 'Measure PFC at origin for main protective device rating, and at circuit ends to verify adequate fault current for ADS.', 'PFC Testing', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('functional-advanced-1', 'What must be verified during functional testing of a three-phase motor control circuit?', '["Motor runs only","Correct rotation, overload operation, emergency stop, interlocks","Voltage only","Current draw"]'::jsonb, 1, 'Functional testing must verify correct rotation direction, overload protection, emergency stops, and safety interlock operation.', 'Functional Testing', 'Advanced', '612.13.1', NULL, 'inspection-testing'),
  ('functional-advanced-2', 'During functional testing, an isolator is found to be stiff. What action?', '["Leave as is","Record as defect requiring maintenance/replacement","Apply lubricant","Not relevant to testing"]'::jsonb, 1, 'Stiff operation indicates wear or fault affecting isolation function. Must be recorded and addressed for safe operation.', 'Functional Testing', 'Intermediate', '612.13.2', NULL, 'inspection-testing'),
  ('functional-advanced-3', 'What should be verified during functional testing of socket outlets?', '["Voltage present only","Correct polarity, secure fixing, earth continuity, shutter operation","Colour of faceplate","Brand compatibility"]'::jsonb, 1, 'Functional testing verifies polarity, mechanical security, earth continuity, protective shutter operation, and switch function.', 'Functional Testing', 'Intermediate', '612.13.1', NULL, 'inspection-testing'),
  ('functional-advanced-4', 'Why must emergency lighting systems be functionally tested?', '["Not required","To verify operation on mains failure and adequate battery duration","For aesthetics","Once per year only"]'::jsonb, 1, 'Emergency lighting must be tested to ensure automatic operation on supply failure and adequate battery backup for safe evacuation.', 'Functional Testing', 'Intermediate', '612.13.1', NULL, 'inspection-testing'),
  ('cert-advanced-1', 'What is the maximum time period for periodic inspection of a commercial installation?', '["1 year","5 years","10 years","No maximum - risk-based"]'::jsonb, 1, 'Commercial/industrial installations typically require inspection every 5 years, though higher-risk environments may need more frequent inspection.', 'Regulations', 'Intermediate', '621.1', NULL, 'inspection-testing'),
  ('cert-advanced-2', 'An EICR is required for property rental. Who can issue this document?', '["Any electrician","Qualified and competent person (typically 2391 or equivalent qualified)","Property owner","Letting agent"]'::jsonb, 1, 'EICR must be completed by qualified, competent persons with appropriate inspection and testing qualifications (typically 2391 level).', 'Regulations', 'Intermediate', '621.2', NULL, 'inspection-testing'),
  ('cert-advanced-3', 'What must be provided to the client upon completion of electrical installation work?', '["Invoice only","Electrical Installation Certificate, minor works certificate, or EICR as appropriate","Verbal confirmation","Photograph of work"]'::jsonb, 1, 'Appropriate certification (EIC for new work, MWC for minor works, EICR for inspections) must be provided to demonstrate compliance.', 'Regulations', 'Intermediate', '631.1', NULL, 'inspection-testing'),
  ('cert-advanced-4', 'When is a Minor Electrical Installation Works Certificate appropriate?', '["All work","Additions/alterations not requiring design calculations (e.g., socket addition to existing circuit)","New installations only","Never required"]'::jsonb, 1, 'Minor Works Certificates cover simple additions/alterations to existing circuits not requiring design work or new circuits.', 'Regulations', 'Intermediate', '631.4', NULL, 'inspection-testing'),
  ('cert-advanced-5', 'What must be recorded on Schedule of Test Results for each circuit?', '["Voltage only","All relevant test results: continuity, insulation, polarity, Zs, RCD operation","Visual observations only","Cable colour"]'::jsonb, 1, 'Schedule of Test Results must record all applicable test results for each circuit to demonstrate compliance with BS 7671.', 'Regulations', 'Intermediate', '631.2', NULL, 'inspection-testing'),
  ('advanced-calc-1', 'A 3-phase 400V motor with 0.8 power factor draws 50A per phase. What is the total real power consumption?', '["20 kW","27.7 kW","34.6 kW","40 kW"]'::jsonb, 1, 'P = √3 × V × I × PF = 1.732 × 400 × 50 × 0.8 = 27,712W = 27.7kW', 'Calculations', 'Advanced', 'Appendix 4', NULL, 'inspection-testing'),
  ('advanced-calc-2', 'Cable run: 45m, voltage drop limit 3%, load 32A at 230V. Maximum resistance per metre?', '["0.076 Ω/m","0.038 Ω/m","0.108 Ω/m","0.054 Ω/m"]'::jsonb, 1, 'Max VD = 230V × 0.03 = 6.9V. Total R = 6.9V ÷ 32A = 0.216Ω. R per metre = 0.216Ω ÷ (45m × 2) = 0.0024Ω/m × 2 conductors = 0.038Ω/m', 'Calculations', 'Advanced', '525', NULL, 'inspection-testing');
INSERT INTO quiz_questions (question_id, question, options, correct_answer, explanation, category, difficulty, regulation, image_url, course)
VALUES
  ('medical-1', 'In medical locations Group 2 (operating theatres), what is the maximum touch voltage limit?', '["50V AC","25V AC","12V AC","6V AC"]'::jsonb, 1, 'Medical locations Group 2 require reduced touch voltage limit of 25V AC due to increased patient vulnerability during procedures.', 'Special Locations', 'Advanced', '710.411.3.2.2', NULL, 'inspection-testing'),
  ('medical-2', 'Medical IT systems for Group 2 locations must incorporate what device?', '["RCD with 10mA sensitivity","Insulation Monitoring Device (IMD)","AFDD protection","RCBO on all circuits"]'::jsonb, 1, 'Medical IT systems use Insulation Monitoring Devices to continuously monitor insulation resistance without disconnecting supply, critical for life-support equipment.', 'Special Locations', 'Advanced', '710.411.6', NULL, 'inspection-testing'),
  ('solar-1', 'PV system DC isolator must be positioned where in relation to the inverter?', '["At the inverter DC input only","On both DC and AC sides, accessible without going near PV array","Adjacent to the PV array only","Inside the consumer unit"]'::jsonb, 1, 'DC isolators required on both sides for safety, positioned to allow isolation without approaching energised PV array.', 'Special Locations', 'Intermediate', '712.537.2.1.5', NULL, 'inspection-testing'),
  ('solar-2', 'What is the minimum DC insulation resistance for a PV installation at 500V test?', '["0.25 MΩ","0.5 MΩ","1.0 MΩ","2.0 MΩ"]'::jsonb, 2, 'PV systems require minimum 1.0 MΩ insulation resistance when tested at 500V DC for safety and proper operation.', 'Testing', 'Advanced', '712.612.2', NULL, 'inspection-testing'),
  ('ev-charging-1', 'EV charging point installed outdoors requires what RCD protection?', '["Type AC 30mA","Type A 30mA","Type B or Type A with 6mA DC protection","No RCD required if Type 2 socket"]'::jsonb, 2, 'EV chargers can produce DC fault current. Type B RCD or Type A with additional 6mA DC protection required per Amendment 2.', 'Special Locations', 'Advanced', '722.531.2', NULL, 'inspection-testing'),
  ('ev-charging-2', 'Mode 3 EV charging point must have what minimum supply rating?', '["13A single phase","16A single phase","32A single or three phase","63A three phase"]'::jsonb, 2, 'Mode 3 charging typically requires 32A minimum for effective fast charging, available in single or three-phase configurations.', 'Special Locations', 'Intermediate', '722.311', NULL, 'inspection-testing'),
  ('swimming-pool-1', 'In swimming pool Zone 0, what is the maximum voltage permitted?', '["230V AC","50V AC","25V AC","12V AC SELV only"]'::jsonb, 3, 'Zone 0 (inside pool basin) permits only 12V AC SELV with transformer outside zones 0, 1, and 2.', 'Special Locations', 'Advanced', '702.410.3.4', NULL, 'inspection-testing'),
  ('swimming-pool-2', 'Supplementary bonding in Zone 1 of swimming pools must connect which items?', '["Only metal pool structure","All extraneous-conductive-parts and exposed-conductive-parts","Just the main earthing terminal","Pool lighting only"]'::jsonb, 1, 'Zone 1 requires supplementary bonding of all extraneous and exposed conductive parts to minimise potential differences in wet conditions.', 'Special Locations', 'Advanced', '702.415.2', NULL, 'inspection-testing'),
  ('testing-advanced-1', 'When testing RCD with test button does not trip the device, what should you do?', '["Replace RCD immediately and inform customer","Test using RCD tester at rated current and half-rated current","Increase test current until it trips","Record as fail and move to next circuit"]'::jsonb, 0, 'Mechanical test button failure indicates serious RCD fault. Replace immediately as electronic testing cannot verify mechanical integrity.', 'Testing', 'Advanced', '643.10', NULL, 'inspection-testing'),
  ('testing-advanced-2', 'Earth fault loop impedance increases significantly when measured at full operating temperature. What does this indicate?', '["Normal behaviour due to conductor resistance change","Possible high resistance joint or poor connection","Excessive cable length","Incorrect MCB rating"]'::jsonb, 1, 'While some increase is normal, significant increase suggests high resistance connection that worsens with heat, posing fire risk.', 'Testing', 'Advanced', '643.7', NULL, 'inspection-testing'),
  ('fault-finding-adv-1', 'RCD trips only when specific 230V appliance is used on multiple different circuits. Most likely cause?', '["Faulty RCD","Appliance has earth leakage fault","Circuit overload","Incorrect wiring"]'::jsonb, 1, 'If same appliance trips RCD on multiple circuits, appliance has earth leakage current exceeding RCD rating (typically >30mA).', 'Fault Finding', 'Advanced', '531.2', NULL, 'inspection-testing'),
  ('fault-finding-adv-2', 'Voltage measured between neutral and earth at socket outlet is 8V with no load. What is the most likely cause?', '["Normal voltage drop","Broken neutral conductor","Parallel neutral earth paths","TT system normal operation"]'::jsonb, 1, '8V between N-E with no load suggests broken neutral upstream causing supply voltage to appear via earth path. Dangerous fault requiring immediate attention.', 'Fault Finding', 'Advanced', '411.4', NULL, 'inspection-testing'),
  ('design-1', 'Office building with 200 × 18W LED fittings. Applying diversity, what is the design current?', '["15.7A (100% of load)","11.7A (75% diversity)","9.4A (60% diversity)","7.8A (50% diversity)"]'::jsonb, 1, 'Lighting circuits typically apply 75% diversity for commercial installations. Total load = 3,600W ÷ 230V = 15.7A × 0.75 = 11.7A', 'Design', 'Advanced', 'Appendix 4', NULL, 'inspection-testing'),
  ('design-2', 'Industrial workshop requires 12 × 16A socket outlets. What minimum size radial circuit breaker is required?', '["32A MCB Type B","50A MCB Type B","63A MCB Type C","80A MCB Type C"]'::jsonb, 1, 'Industrial sockets: first 10 at 100% + remainder at 40%. (10 × 16A) + (2 × 16A × 0.4) = 160 + 12.8 = 172.8A diversity applied = 50A adequate.', 'Design', 'Advanced', 'Appendix 4', NULL, 'inspection-testing'),
  ('cables-advanced-1', 'Cable installed in loft space with insulation touching: 70mm thermal insulation, 30°C ambient. What derating factors apply?', '["Ca × Cg only","Ca × Cg × Ci","Cg × Ci only","No derating required"]'::jsonb, 1, 'Must apply all relevant factors: Ca (ambient), Cg (grouping if applicable), and Ci (thermal insulation), multiplied together.', 'Cable Selection', 'Advanced', '523.9', NULL, 'inspection-testing'),
  ('cables-advanced-2', '10mm² twin and earth cable clipped direct has current-carrying capacity of 63A at 30°C. What is capacity at 45°C ambient?', '["63A (unchanged)","52A","48A","44A"]'::jsonb, 2, 'Correction factor for 45°C ambient = 0.76. New capacity = 63A × 0.76 = 47.88A ≈ 48A', 'Cable Selection', 'Advanced', '523.1', NULL, 'inspection-testing'),
  ('protection-advanced-1', 'AFDD (Arc Fault Detection Device) is mandatory for which circuit types under Amendment 2?', '["All final circuits","Single-phase AC final circuits ≤32A in certain premises","Only lighting circuits","Three-phase circuits only"]'::jsonb, 1, 'Amendment 2:2022 requires AFDDs for single-phase AC final circuits up to 32A in houses (HMOs), student accommodation, and care homes.', 'Protection', 'Advanced', '421.1.7', NULL, 'inspection-testing'),
  ('protection-advanced-2', 'SPD (Surge Protection Device) Type 2 must be installed when building has what risk factor?', '["All buildings require SPDs","Buildings in high lightning areas or with sensitive equipment","Only commercial buildings","Agricultural buildings only"]'::jsonb, 1, 'SPD Type 2 required where risk assessment identifies: high lightning activity (AQ > 25), sensitive equipment, or external incoming services from overhead lines.', 'Protection', 'Advanced', '443.4', NULL, 'inspection-testing'),
  ('earthing-advanced-1', 'TT system with Ra = 85Ω and RCD rated 30mA. What is maximum earth fault current?', '["230V ÷ 85Ω = 2.7A","50V ÷ 85Ω = 0.59A","150V ÷ 85Ω = 1.76A","Depends on load current"]'::jsonb, 0, 'Maximum earth fault current = supply voltage ÷ Ra = 230V ÷ 85Ω = 2.7A, well above RCD rating ensuring operation.', 'Earthing', 'Advanced', '411.5', NULL, 'inspection-testing'),
  ('earthing-advanced-2', 'PME (TN-C-S) supply prohibited for what installation types?', '["Any domestic installation","Caravans, boats, petrol stations, mobile structures","Commercial buildings only","Industrial installations"]'::jsonb, 1, 'PME prohibited where lost neutral could create dangerous potential on exposed metalwork: caravans, boats, petrol stations, and certain mobile installations.', 'Earthing', 'Advanced', '411.4', NULL, 'inspection-testing'),
  ('amendment2-1', 'Under Amendment 2, consumer units in domestic premises must meet what fire resistance standard?', '["Non-combustible or enclosed in cabinet meeting BS EN 61439-3","Metal enclosure only","Any material acceptable","Fire-resistant plastic to BS 5839"]'::jsonb, 0, 'Amendment 2 requires consumer units to be non-combustible or enclosed in cabinet/enclosure complying with BS EN 61439-3 for fire safety.', 'Regulations', 'Advanced', '421.1.201', NULL, 'inspection-testing'),
  ('amendment2-2', 'Amendment 2 requirements for AFDD protection apply to which new installations?', '["All installations from 2022","New or altered dwellings, HMOs, student accommodation, care homes","Commercial buildings only","Agricultural installations"]'::jsonb, 1, 'AFDD requirements apply specifically to: single-phase AC final circuits ≤32A in dwellings, HMOs, student accommodation, and care homes.', 'Regulations', 'Advanced', '421.1.7', NULL, 'inspection-testing'),
  ('practical-scenario-1', 'Customer requests outdoor hot tub installation. Main concerns from BS 7671 perspective?', '["Circuit capacity only","Zone classification, RCD protection, bonding, IP rating","Cable size only","Consumer unit location"]'::jsonb, 1, 'Hot tubs create special location requiring: zone classification (similar to bathrooms), 30mA RCD, supplementary bonding, appropriate IP ratings for equipment.', 'Special Locations', 'Advanced', '702', NULL, 'inspection-testing'),
  ('practical-scenario-2', 'EICR reveals mixed RCD types protecting circuits with EV charger. What action required?', '["No action if RCDs test correctly","Replace Type AC RCDs with Type A or B","Add additional Type AC RCD","Remove RCD protection"]'::jsonb, 1, 'EV chargers produce DC fault current. Must use Type B RCD or Type A with additional 6mA DC protection. Type AC RCDs ineffective for DC faults.', 'Testing', 'Advanced', '722.531.2', NULL, 'inspection-testing'),
  ('domestic-circuits-1', 'Cooker circuit protected by 32A MCB Type B. Maximum floor area the circuit can serve per diversity?', '["Unlimited - based on load only","100m²","50m²","Diversity does not apply to cooker circuits"]'::jsonb, 0, 'Cooker circuit sizing based on load diversity calculation: first 10A + 30% remainder + 5A if socket outlet, not floor area.', 'Design', 'Intermediate', 'Appendix 4', NULL, 'inspection-testing'),
  ('domestic-circuits-2', 'Kitchen with 5 × 13A double socket outlets on 32A ring. Customer wants to add 3 more doubles. Acceptable?', '["Yes, ring can support unlimited sockets","No, exceeds socket limit per ring","Assess floor area and load - likely acceptable if within 100m²","Yes, if RCD protected"]'::jsonb, 2, 'Ring circuits limited to 100m² floor area, unlimited sockets. Assess whether 8 doubles likely to exceed 32A with diversity applied. Usually acceptable in domestic kitchen.', 'Design', 'Intermediate', 'Appendix 15', NULL, 'inspection-testing'),
  ('cables-fire-1', 'Fire alarm cable in escape route must meet what standard?', '["Standard PVC cable acceptable","LSF cable (Low Smoke and Fume)","FP200 or equivalent fire-resistant cable","Any red-coloured cable"]'::jsonb, 2, 'Fire alarm circuits in escape routes require fire-resistant cables (FP200, FP400, or equivalent) to maintain circuit integrity during fire.', 'Cable Selection', 'Advanced', '560.8', NULL, 'inspection-testing'),
  ('cables-fire-2', 'Emergency lighting circuit requires cable with what fire performance?', '["Standard twin and earth","Fire-resistant cable maintaining integrity for specified duration","LSF cable only","Armoured cable"]'::jsonb, 1, 'Emergency lighting must operate during fire evacuation, requiring fire-resistant cables maintaining circuit integrity (typically 1-3 hours).', 'Cable Selection', 'Advanced', '560.8', NULL, 'inspection-testing'),
  ('harmonics-1', 'Excessive harmonic currents in installation primarily affect which conductor?', '["Phase conductors","Earth conductor","Neutral conductor","CPC"]'::jsonb, 2, 'Third harmonic and multiples (triplen harmonics) from non-linear loads add arithmetically in neutral, potentially overloading it even when phase currents balanced.', 'Design', 'Advanced', '523.6.3', NULL, 'inspection-testing'),
  ('harmonics-2', 'Office with many computer workstations (>50% harmonic-producing loads). What neutral conductor sizing required?', '["Same size as phase conductors","Neutral may be reduced","Neutral must be same size or larger than phase","Neutral not required"]'::jsonb, 2, 'Where harmonic-producing loads exceed 35% of total, neutral may carry more current than phases due to triplen harmonics. Neutral must not be reduced.', 'Design', 'Advanced', '523.6.3', NULL, 'inspection-testing'),
  ('voltage-drop-1', '50m cable run, 32A load. Voltage drop = 9V. Does this comply for lighting circuit?', '["Yes, within 3% limit (6.9V)","No, exceeds 3% limit (6.9V)","Yes, within 5% limit (11.5V)","Acceptable if RCD protected"]'::jsonb, 1, 'Lighting circuits limited to 3% = 6.9V. Measured 9V exceeds limit. However, if this is not lighting, 5% (11.5V) may apply.', 'Calculations', 'Intermediate', '525', NULL, 'inspection-testing'),
  ('voltage-drop-2', 'How is voltage drop measured during periodic inspection?', '["Calculate from cable data","Measure at origin and end of circuit under load","Use earth fault loop impedance tester","Not required during EICR"]'::jsonb, 1, 'Voltage drop verified by measuring voltage at origin and circuit endpoint simultaneously with circuit under normal load. Difference must not exceed limits.', 'Testing', 'Intermediate', '643.11', NULL, 'inspection-testing'),
  ('busbar-systems-1', 'Busbar trunking system for industrial installation requires what protection?', '["No protection needed - self-protected","Overcurrent and short-circuit protection at origin","RCD protection only","Fuses in each busbar tap-off"]'::jsonb, 1, 'Busbar systems require overcurrent protection at origin. Tap-off points may have additional protection depending on connected load.', 'Protection', 'Advanced', '521.4', NULL, 'inspection-testing'),
  ('data-cables-1', 'Data cables (Cat 6) installed in same trunking as 230V power cables. Acceptable?', '["Yes, no segregation required","No, requires minimum separation or physical barrier","Yes, if screened data cables used","Yes, if power cables RCD protected"]'::jsonb, 1, 'Band I (telecoms/data) and Band II (LV power) circuits require segregation: separate enclosure, minimum spacing, or fixed partition per BS 7671.', 'Design', 'Intermediate', '528.1', NULL, 'inspection-testing'),
  ('data-cables-2', 'Minimum separation distance when data and power cables run parallel without barrier?', '["50mm","100mm","150mm","300mm"]'::jsonb, 3, 'Band I and Band II cables require 300mm separation when run in parallel without physical barrier to prevent interference and maintain safety.', 'Design', 'Intermediate', '528.1', NULL, 'inspection-testing'),
  ('maintenance-1', 'Installation supplies life-support equipment. Maximum EICR interval?', '["10 years","5 years","1 year or as recommended by competent person","6 months"]'::jsonb, 2, 'Critical installations (life-support, safety systems) require inspection at intervals determined by risk assessment, typically annually or more frequently.', 'Testing', 'Intermediate', '621.1', NULL, 'inspection-testing'),
  ('maintenance-2', 'Industrial installation with harsh environment (chemicals, heat, moisture). Recommended EICR interval?', '["10 years (standard)","5 years","3 years or less based on risk assessment","1 year"]'::jsonb, 2, 'Harsh environments accelerate deterioration. Risk assessment typically recommends 1-3 year intervals for inspection and testing.', 'Testing', 'Intermediate', '621.1', NULL, 'inspection-testing'),
  ('lighting-design-1', 'LED lighting circuit requires what special consideration for MCB selection?', '["Higher rated MCB needed","Type D MCB recommended","Consider inrush current; Type B usually adequate","No special consideration"]'::jsonb, 2, 'LED drivers may have higher inrush current at switch-on. Consider total inrush of multiple fittings, but Type B typically adequate for most installations.', 'Design', 'Intermediate', '533.2', NULL, 'inspection-testing'),
  ('lighting-design-2', 'External lighting circuit controlled by photocell. Additional protection requirements?', '["None beyond standard circuit protection","30mA RCD as outdoor circuit","AFDD protection","Reduced MCB rating"]'::jsonb, 1, 'Outdoor circuits require 30mA RCD protection regardless of control method. Photocell does not eliminate this requirement.', 'Design', 'Intermediate', '411.3.3', NULL, 'inspection-testing'),
  ('generators-1', 'Standby generator installation requires what earthing arrangement when connected to TN-S system?', '["Use existing DNO earth","Separate TT earth electrode for generator","PME earthing","No earth required for standby supply"]'::jsonb, 1, 'Standby generators require independent earth electrode (TT system) to ensure safe operation when mains supply lost. Cannot rely on DNO earth.', 'Earthing', 'Advanced', '551.6', NULL, 'inspection-testing'),
  ('generators-2', 'Changeover switch between mains and generator must prevent what scenario?', '["Generator overload","Parallel operation (both sources connected)","Voltage drop","Neutral current"]'::jsonb, 1, 'Changeover must be break-before-make to prevent paralleling generator with mains supply, which could cause damage, instability, or safety hazards.', 'Design', 'Advanced', '551.6', NULL, 'inspection-testing'),
  ('isolation-1', 'Motor control circuit requires how many isolation points?', '["One at motor","One at motor, one at control panel","Isolation at motor sufficient if locally controlled","No isolation required"]'::jsonb, 1, 'Motors require isolation at both motor location and control position for safe maintenance, preventing unexpected re-energisation.', 'Protection', 'Intermediate', '537.2', NULL, 'inspection-testing'),
  ('parallel-cables-1', 'Two 10mm² cables in parallel to increase current capacity. What factors must be considered?', '["Simply double the capacity","Equal length, equal impedance, appropriate termination","Different cable routes acceptable","One cable can be smaller CSA"]'::jsonb, 1, 'Parallel cables must have equal length, equal impedance, same installation method, and proper terminations to ensure balanced current sharing.', 'Cable Selection', 'Advanced', '523.7', NULL, 'inspection-testing'),
  ('agricultural-1', 'Agricultural installation socket outlets require what RCD rating?', '["100mA","30mA","300mA","No RCD required"]'::jsonb, 1, 'Agricultural installations are high-risk environments. Socket outlets require 30mA RCD protection due to increased shock risk from moisture and livestock.', 'Special Locations', 'Intermediate', '705.411.1', NULL, 'inspection-testing'),
  ('agricultural-2', 'Supplementary bonding in agricultural buildings must include which items?', '["Only electrical equipment","All metal structural parts, equipment, and extraneous parts","Just water pipes","Animal housing only"]'::jsonb, 1, 'Agricultural locations require extensive supplementary bonding of all accessible metalwork, structural parts, and equipment to protect livestock and personnel.', 'Special Locations', 'Intermediate', '705.415.2.1', NULL, 'inspection-testing'),
  ('discrimination-1', 'Consumer unit has 80A main switch, 32A MCB for ring final, 6A MCB for lighting. MCB characteristics for discrimination?', '["All Type B acceptable","Main Type C, circuits Type B","All same type - discrimination by rating","Lighting Type D to prevent nuisance tripping"]'::jsonb, 2, 'Discrimination achieved primarily through different ratings with same MCB type. Type B typical for domestic. Main switch rating coordinates with circuit MCBs.', 'Protection', 'Intermediate', '536', NULL, 'inspection-testing'),
  ('low-voltage-1', 'SELV circuit maximum voltage under fault conditions?', '["50V AC","120V AC","25V AC","Must not exceed ELV limits"]'::jsonb, 3, 'SELV (Separated Extra Low Voltage) must not exceed ELV limits (50V AC / 120V DC) even under single fault condition, ensuring safety.', 'Protection', 'Intermediate', '414.1', NULL, 'inspection-testing'),
  ('temporary-installations-1', 'Temporary electrical installation for construction site requires EICR interval of?', '["10 years","5 years","3 months","1 month or after modification"]'::jsonb, 2, 'Construction sites are harsh, high-risk environments. Installations require inspection every 3 months minimum due to rapid deterioration and frequent changes.', 'Testing', 'Intermediate', '621.1', NULL, 'inspection-testing'),
  ('bathroom-zones-1', 'IP rating required for light fitting in Zone 1 (above bath/shower)?', '["IPX4 (splash-proof)","IPX5 (jet-proof)","IPX7 (immersion-proof)","IP20 (standard)"]'::jsonb, 0, 'Zone 1 requires minimum IPX4 (splash-proof) due to exposure to water splashing from showering/bathing.', 'Special Locations', 'Beginner', '701.512.2', NULL, 'inspection-testing'),
  ('functional-switching-1', 'Functional switching device must be rated for what duty?', '["Normal load current only","Isolation duty","Emergency switching","Fault current interruption"]'::jsonb, 0, 'Functional switches (standard light switches, etc.) rated only for normal load switching, not isolation or fault interruption.', 'Protection', 'Beginner', '537.5', NULL, 'inspection-testing'),
  ('insulation-res-50', 'When testing insulation resistance on circuits with SPDs, what precaution must be taken?', '["Test at 250V DC only","Disconnect or short out SPDs before testing","Increase test voltage to 1000V","No precautions necessary"]'::jsonb, 1, 'Surge Protection Devices (SPDs) can be damaged by insulation resistance test voltages. They must be disconnected or shorted out before testing.', 'Insulation Resistance', 'Advanced', '612.3.2', NULL, 'inspection-testing'),
  ('efli-30', 'What is the main purpose of measuring Zs (earth fault loop impedance)?', '["To check cable quality","To verify automatic disconnection of supply will occur within required time","To measure voltage drop","To test RCD operation"]'::jsonb, 1, 'Zs measurement verifies that under fault conditions, sufficient current will flow to operate the protective device within the disconnection time required by BS 7671.', 'Earth Fault Loop Impedance', 'Beginner', '411.4.4', NULL, 'inspection-testing'),
  ('efli-31', 'Maximum Zs for a 32A Type B MCB protecting a socket circuit is?', '["1.44Ω","0.87Ω","1.09Ω","2.30Ω"]'::jsonb, 0, 'For 32A Type B MCB, maximum Zs is 1.44Ω to ensure 5-second disconnection for socket circuits (0.4s for fixed equipment).', 'Earth Fault Loop Impedance', 'Intermediate', 'Appendix 3', NULL, 'inspection-testing'),
  ('efli-32', 'If measured Zs exceeds maximum permitted value, what actions should be taken?', '["Accept and note on certificate","Investigate cause - check connections, bonding, supply Ze","Increase MCB rating","Install RCD protection only"]'::jsonb, 1, 'High Zs must be investigated. Check all connections, bonding, and measure Ze. Poor connections or inadequate earthing must be corrected.', 'Earth Fault Loop Impedance', 'Intermediate', '411.4.4', NULL, 'inspection-testing'),
  ('efli-33', 'What is the relationship between Zs and fault current (If)?', '["If = Zs × Uo","If = Uo ÷ Zs","If = Zs ÷ Uo","No relationship"]'::jsonb, 1, 'Fault current If = Uo ÷ Zs. Lower impedance results in higher fault current, which operates protective devices faster.', 'Earth Fault Loop Impedance', 'Advanced', '411.4.4', NULL, 'inspection-testing'),
  ('efli-34', 'Why must Zs testing be carried out with bonding connected?', '["To protect the tester","To simulate actual fault conditions with parallel earth paths","To avoid RCD tripping","It is not necessary"]'::jsonb, 1, 'Bonding creates parallel earth paths which reduce overall impedance. Testing with bonding connected gives the true Zs value under fault conditions.', 'Earth Fault Loop Impedance', 'Intermediate', '411.4.2', NULL, 'inspection-testing'),
  ('efli-35', 'When testing Zs on RCD-protected circuits, which method should be used?', '["Standard high-current test","No-trip or low-current test","Insulation resistance test","Continuity test only"]'::jsonb, 1, 'RCD-protected circuits require no-trip or low-current Zs testing to prevent the RCD operating during the test.', 'Earth Fault Loop Impedance', 'Intermediate', '612.9', NULL, 'inspection-testing'),
  ('efli-36', 'What correction factor is applied to measured Zs for conductor temperature?', '["Measured value × 1.5","Measured value × 0.8","No correction needed","Depends on ambient temperature"]'::jsonb, 1, 'Conductors at ambient temperature have lower resistance than at operating temperature. Apply 0.8 correction factor (or measure at operating temperature).', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('efli-37', 'A circuit has measured Zs of 1.2Ω. After applying 0.8 multiplier, maximum permitted is 1.15Ω. Result is?', '["Pass - 1.2Ω is acceptable","Fail - 0.96Ω exceeds limit","Fail - corrected value 1.5Ω exceeds limit","Pass after temperature correction"]'::jsonb, 0, 'Measured Zs (1.2Ω) is compared to maximum permitted (1.15Ω). The 1.2Ω exceeds 1.15Ω slightly, but if conductors are cold, actual Zs when hot would be higher. However, typically measured values are compared directly to published limits which include the 0.8 factor already. This is a borderline case requiring investigation.', 'Earth Fault Loop Impedance', 'Advanced', '612.9', NULL, 'inspection-testing'),
  ('efli-38', 'What does Ze represent in earth fault loop impedance?', '["Circuit protective conductor resistance","External earth fault loop impedance (supply network)","Total earth fault loop impedance","Phase conductor resistance"]'::jsonb, 1, 'Ze is the external earth fault loop impedance from the installation origin back through the supply network to the source.', 'Earth Fault Loop Impedance', 'Beginner', '612.6', NULL, 'inspection-testing'),
  ('efli-39', 'Typical maximum Ze for TN-S supply system is?', '["200Ω","0.8Ω","21Ω","1667Ω"]'::jsonb, 1, 'TN-S systems typically have Ze not exceeding 0.8Ω. Values higher than this may indicate supply network problems.', 'Earth Fault Loop Impedance', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('efli-40', 'If Ze is measured at 0.5Ω and R1+R2 for a circuit is 0.3Ω, what is the expected Zs?', '["0.2Ω","0.8Ω","0.15Ω","1.5Ω"]'::jsonb, 1, 'Zs = Ze + R1 + R2. Therefore Zs = 0.5Ω + 0.3Ω = 0.8Ω. This is the calculated value to compare with measured Zs.', 'Earth Fault Loop Impedance', 'Intermediate', '612.9', NULL, 'inspection-testing'),
  ('efli-41', 'What safety precaution is essential before conducting Zs tests?', '["Isolate all circuits","Wear insulated gloves","Inform occupants - test creates temporary earth fault","Disconnect main earth"]'::jsonb, 2, 'Zs testing creates a deliberate earth fault causing high current flow momentarily. Inform occupants, ensure RCDs may trip, and be prepared for circuit interruption.', 'Earth Fault Loop Impedance', 'Beginner', '612.9', NULL, 'inspection-testing'),
  ('efli-42', 'Maximum permitted Zs for a 6A Type B MCB protecting lighting circuit is?', '["9.58Ω","7.67Ω","6.14Ω","4.37Ω"]'::jsonb, 1, 'For 6A Type B MCB, maximum Zs is 7.67Ω to ensure 5-second disconnection time (BS 7671 Appendix 3).', 'Earth Fault Loop Impedance', 'Intermediate', 'Appendix 3', NULL, 'inspection-testing'),
  ('efli-43', 'When is R1+R2 testing preferred over direct Zs measurement?', '["Never","On new installations before energisation","Only for lighting circuits","When RCDs are present"]'::jsonb, 1, 'R1+R2 testing (continuity) is performed on new installations before energisation. Ze is measured separately and Zs calculated (Ze + R1+R2).', 'Earth Fault Loop Impedance', 'Intermediate', '612.9', NULL, 'inspection-testing'),
  ('efli-44', 'A 40A Type B MCB has maximum Zs of 1.15Ω. Measured value is 1.18Ω. What action is required?', '["Accept - within tolerance","Fail - investigate and remedy","Change to Type C MCB","Install RCD"]'::jsonb, 1, 'Measured Zs (1.18Ω) exceeds maximum permitted (1.15Ω). This must be investigated and remedied - check connections, bonding, and Ze.', 'Earth Fault Loop Impedance', 'Intermediate', '411.4.4', NULL, 'inspection-testing'),
  ('efli-45', 'What is the typical Zs value considered excellent for a TN system?', '["Below 0.5Ω","1.0Ω to 2.0Ω","Below 2.0Ω","Any value below maximum permitted"]'::jsonb, 0, 'Zs values below 0.5Ω are considered excellent in TN systems, ensuring rapid fault clearance and robust earth fault protection.', 'Earth Fault Loop Impedance', 'Intermediate', '411.4.4', NULL, 'inspection-testing'),
  ('efli-46', 'Why might measured Zs be lower than calculated (Ze + R1+R2)?', '["Test equipment error","Parallel earth paths from bonding","Incorrect Ze measurement","Faulty protective device"]'::jsonb, 1, 'Measured Zs is often lower than calculated due to parallel earth paths created by bonding to services (gas, water). This is beneficial for fault clearance.', 'Earth Fault Loop Impedance', 'Advanced', '411.4.2', NULL, 'inspection-testing'),
  ('efli-47', 'For a Type C 20A MCB, maximum Zs for 0.4s disconnection is?', '["2.30Ω","1.15Ω","1.84Ω","2.76Ω"]'::jsonb, 1, 'Type C 20A MCB has maximum Zs of 1.15Ω for 0.4s disconnection (socket circuits, fixed equipment). For 5s disconnection: 2.30Ω.', 'Earth Fault Loop Impedance', 'Advanced', 'Appendix 3', NULL, 'inspection-testing'),
  ('efli-48', 'What indicates a deteriorating earth fault loop path?', '["Increasing Zs readings over successive tests","Decreasing Zs readings","Constant Zs readings","No change in readings"]'::jsonb, 0, 'Increasing Zs values over time indicate deteriorating connections, corrosion, or problems in the earth fault path requiring investigation.', 'Earth Fault Loop Impedance', 'Intermediate', '612.9', NULL, 'inspection-testing'),
  ('efli-49', 'When performing Zs test at the origin, what value is actually measured?', '["R1+R2","Ze (external impedance)","Zs of entire installation","Loop impedance of supply only"]'::jsonb, 1, 'Zs test at the origin (before any circuit protective conductors) measures Ze - the external earth fault loop impedance from the supply network.', 'Earth Fault Loop Impedance', 'Intermediate', '612.6', NULL, 'inspection-testing'),
  ('efli-50', 'What is the consequence of excessive Zs on circuit protection?', '["Faster disconnection times","Insufficient fault current to operate protective device within required time","Improved protection","No effect on protection"]'::jsonb, 1, 'High Zs limits fault current (If = Uo÷Zs), potentially preventing the protective device from operating within required disconnection time - dangerous fault.', 'Earth Fault Loop Impedance', 'Advanced', '411.4.4', NULL, 'inspection-testing'),
  ('pfc-16', 'What is the minimum acceptable breaking capacity for domestic MCBs?', '["3kA","6kA","10kA","16kA"]'::jsonb, 1, 'Domestic MCBs typically have minimum 6kA breaking capacity (6000A), suitable for most UK domestic installations.', 'Prospective Fault Current', 'Beginner', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-17', 'Where should PFC be measured in an installation?', '["Only at the origin","At the origin and at the furthest point of each circuit","Every socket outlet","Distribution boards only"]'::jsonb, 1, 'PFC should be measured at the origin (highest) and furthest points of circuits to verify protective device breaking capacity is adequate.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-18', 'A protective device has 4.5kA breaking capacity. Measured PEFC is 5.2kA. What action is required?', '["No action - acceptable","Replace with device rated at least 6kA","Install RCD","Reduce circuit length"]'::jsonb, 1, 'Protective device breaking capacity must exceed PFC. 4.5kA device is inadequate for 5.2kA fault current - replace with 6kA minimum rated device.', 'Prospective Fault Current', 'Intermediate', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-19', 'What does PSCC stand for in electrical testing?', '["Protective Short Circuit Current","Prospective Short Circuit Current","Primary Supply Circuit Current","Phase Single Circuit Current"]'::jsonb, 1, 'PSCC is Prospective Short Circuit Current - the maximum current that would flow in a phase-to-neutral or phase-to-phase fault.', 'Prospective Fault Current', 'Beginner', '612.11', NULL, 'inspection-testing'),
  ('pfc-20', 'What does PEFC stand for in electrical testing?', '["Phase Earth Fault Current","Protective Equipment Fault Current","Prospective Earth Fault Current","Primary Earthing Fault Calculation"]'::jsonb, 2, 'PEFC is Prospective Earth Fault Current - the maximum current that would flow in a phase-to-earth fault.', 'Prospective Fault Current', 'Beginner', '612.11', NULL, 'inspection-testing'),
  ('pfc-21', 'Measured Ze = 0.25Ω, supply voltage = 230V. Calculate PEFC.', '["57.5A","920A","575A","9200A"]'::jsonb, 1, 'PEFC = U0 ÷ Ze = 230V ÷ 0.25Ω = 920A. This is the prospective earth fault current at the origin.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-22', 'A TN-C-S supply typically has higher or lower PFC than TN-S?', '["Significantly higher","Significantly lower","Similar values","Cannot be compared"]'::jsonb, 2, 'TN-C-S and TN-S supplies typically have similar PFC values, both dependent on transformer rating and supply cable impedance.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-23', 'Why is PFC typically lower at distribution boards compared to the origin?', '["Protective devices reduce it","Cable impedance between origin and board increases total impedance","RCDs limit fault current","Voltage drop effects"]'::jsonb, 1, 'Cable impedance between the origin and distribution boards adds to total circuit impedance, reducing the prospective fault current.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-24', 'What is the typical PFC range at the origin of a UK domestic installation?', '["100A to 500A","1kA to 3kA","5kA to 16kA","20kA to 50kA"]'::jsonb, 1, 'Typical UK domestic installations have PFC at origin of 1kA to 3kA, though this varies with supply characteristics.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-25', 'An installation has Ze = 0.15Ω. Supply is upgraded with lower impedance. New Ze = 0.10Ω. Effect on PEFC?', '["PEFC decreases","PEFC increases by 50%","No change","PEFC increases by 33%"]'::jsonb, 1, 'Lower Ze means higher fault current. PEFC = 230V÷0.15Ω = 1533A. New PEFC = 230V÷0.10Ω = 2300A. This is a 50% increase.', 'Prospective Fault Current', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('pfc-26', 'Which type of protective device has the highest breaking capacity?', '["MCB Type B 6kA","BS 88 Fuse 80kA","RCBO 6kA","MCB Type C 10kA"]'::jsonb, 1, 'BS 88 fuses have the highest breaking capacity, typically 80kA, making them suitable for industrial installations with very high PFC.', 'Prospective Fault Current', 'Advanced', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-27', 'When testing PFC, which probe configuration is used for PEFC measurement?', '["Phase to neutral","Phase to phase","Phase to earth","Neutral to earth"]'::jsonb, 2, 'PEFC (Prospective Earth Fault Current) is measured between phase and earth conductors.', 'Prospective Fault Current', 'Beginner', '612.11', NULL, 'inspection-testing'),
  ('pfc-28', 'When testing PFC, which probe configuration is used for PSCC measurement?', '["Phase to earth","Phase to neutral","Neutral to earth","Any two conductors"]'::jsonb, 1, 'PSCC (Prospective Short Circuit Current) is measured between phase and neutral conductors (single phase).', 'Prospective Fault Current', 'Beginner', '612.11', NULL, 'inspection-testing'),
  ('pfc-29', 'In a three-phase system, where is the highest PFC typically measured?', '["Neutral to earth","Phase to phase","Phase to neutral","All equal"]'::jsonb, 1, 'Phase-to-phase faults typically produce the highest fault current in three-phase systems due to higher voltage (400V vs 230V) and lower impedance path.', 'Prospective Fault Current', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('pfc-30', 'What safety precaution is essential during PFC testing?', '["Isolate all circuits","Ensure test leads rated for high current","Disconnect main earth","Test at reduced voltage"]'::jsonb, 1, 'PFC testing involves very high currents momentarily. Test leads, probes, and connections must be rated for high current to prevent burns or equipment damage.', 'Prospective Fault Current', 'Beginner', '612.11', NULL, 'inspection-testing'),
  ('pfc-31', 'An MCB is marked with "6000" on its front. What does this indicate?', '["Maximum load current 6000A","Breaking capacity 6000A (6kA)","Voltage rating","Resistance value"]'::jsonb, 1, 'The marking indicates breaking capacity in amperes. 6000A = 6kA - the maximum fault current the MCB can safely interrupt.', 'Prospective Fault Current', 'Beginner', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-32', 'If PFC at origin is 2.5kA and at end of a circuit is 1.8kA, what does this indicate?', '["Fault in the circuit","Normal - cable impedance reduces PFC along circuit length","Test equipment error","Inadequate protection"]'::jsonb, 1, 'This is normal. Cable impedance increases along the circuit length, reducing prospective fault current further from the source.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-33', 'Why must PFC be recorded on electrical certificates?', '["Insurance purposes only","To verify protective devices have adequate breaking capacity","For voltage drop calculations","Building regulations requirement only"]'::jsonb, 1, 'Recording PFC verifies protective devices can safely interrupt maximum fault current, ensuring protection adequacy now and for future comparisons.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-34', 'A consumer unit has mixed MCBs: some 6kA, some 10kA. PFC at origin is 7kA. What action is required?', '["No action - 10kA devices protect 6kA devices","Replace all 6kA devices with 10kA minimum","Install current limiting devices","Acceptable if backed up by main fuse"]'::jsonb, 1, 'All protective devices must individually have breaking capacity exceeding PFC. 6kA devices are inadequate for 7kA fault current - replace with 10kA devices.', 'Prospective Fault Current', 'Advanced', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-35', 'What happens if a protective device operates under fault current exceeding its breaking capacity?', '["Device operates normally","Device may fail to clear fault, risk of fire or explosion","Device trips but can be reset","Only aesthetic damage"]'::jsonb, 1, 'If fault current exceeds breaking capacity, the device may fail catastrophically - unable to clear the fault, with risk of fire, explosion, or equipment destruction.', 'Prospective Fault Current', 'Advanced', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-36', 'In a TT system, why is PFC typically lower than TN systems?', '["Lower supply voltage","Higher earth electrode resistance increases impedance","Protective devices limit current","Longer cable runs"]'::jsonb, 1, 'TT systems rely on earth electrodes which have much higher resistance (typically >20Ω) than TN earthing, significantly reducing PFC.', 'Prospective Fault Current', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('pfc-37', 'How often should PFC be tested during periodic inspection?', '["Not required","Every inspection - to verify protective device adequacy","Only on initial verification","Only if devices are changed"]'::jsonb, 1, 'PFC must be measured during every periodic inspection to verify protective devices remain adequate and to detect changes in supply characteristics.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-38', 'Measured PSCC = 3.2kA, PEFC = 2.8kA. Which value determines required breaking capacity?', '["PEFC only","PSCC only","Higher value (PSCC)","Average of both"]'::jsonb, 2, 'The higher PFC value (PSCC at 3.2kA) determines the minimum required breaking capacity, as this represents the worst-case fault scenario.', 'Prospective Fault Current', 'Intermediate', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-39', 'A supply is changed from overhead lines to underground cables. Expected effect on PFC?', '["PFC decreases significantly","PFC increases due to lower cable impedance","No change","Depends on cable length only"]'::jsonb, 1, 'Underground cables typically have lower impedance than overhead lines, potentially increasing PFC. Protective device breaking capacity must be verified.', 'Prospective Fault Current', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('pfc-40', 'Ze = 0.20Ω, R1+R2 = 0.30Ω. What is the PEFC at the circuit endpoint?', '["460A","767A","1150A","230A"]'::jsonb, 0, 'PEFC = U0 ÷ (Ze + R1+R2) = 230V ÷ (0.20Ω + 0.30Ω) = 230V ÷ 0.50Ω = 460A at circuit endpoint.', 'Prospective Fault Current', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('pfc-41', 'What is "let-through energy" (I²t) in relation to protective devices?', '["Operating time of device","Energy passed through device during fault clearance","Device rating","Load current capacity"]'::jsonb, 1, 'Let-through energy (I²t) is the energy passed through a protective device before it clears a fault. Lower I²t means better protection for cables and equipment.', 'Prospective Fault Current', 'Advanced', '434.5.2', NULL, 'inspection-testing'),
  ('pfc-42', 'Which device type typically provides the fastest fault current limitation?', '["MCB","BS 88 HRC fuse","MCCB","Rewireable fuse"]'::jsonb, 1, 'BS 88 HRC (High Rupturing Capacity) fuses provide excellent current limitation and very low let-through energy, offering superior protection.', 'Prospective Fault Current', 'Advanced', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-43', 'Origin has PFC of 12kA. All MCBs are 6kA rated. What is the most appropriate solution?', '["Install 16kA MCBs","Install energy-limiting device (e.g. BS88 fuse) before consumer unit","Accept as satisfactory","Install RCDs"]'::jsonb, 1, 'Install current-limiting device such as BS88 fuse at origin to reduce downstream PFC to within MCB breaking capacity (below 6kA).', 'Prospective Fault Current', 'Advanced', '434.5.1', NULL, 'inspection-testing');
INSERT INTO quiz_questions (question_id, question, options, correct_answer, explanation, category, difficulty, regulation, image_url, course)
VALUES
  ('pfc-44', 'What is "back-up protection" in relation to PFC?', '["Redundant protective devices","Upstream device with higher breaking capacity protecting downstream devices","RCD protection","Emergency backup supply"]'::jsonb, 1, 'Back-up protection occurs when an upstream device with high breaking capacity (e.g. BS88 fuse) provides fault clearance if downstream device cannot interrupt fault current.', 'Prospective Fault Current', 'Advanced', '536.1', NULL, 'inspection-testing'),
  ('pfc-45', 'PFC testing involves passing what level of current through the circuit?', '["Full fault current","Very low test current (no actual fault created)","Half rated current","100A test current"]'::jsonb, 1, 'Modern PFC testers use very low test currents and calculate PFC from impedance measurements - no actual high fault current flows.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-46', 'Why is PFC highest at the origin of the installation?', '["Most devices located there","Lowest total impedance - shortest path to supply","Highest voltage","Main earth connection located there"]'::jsonb, 1, 'PFC is highest at origin because total impedance is lowest - shortest electrical path back to supply source.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-47', 'An installation has Ze = 0.12Ω. Supply authority upgrades local transformer. New Ze = 0.08Ω. Recommended action?', '["No action needed","Remeasure PFC and verify breaking capacity remains adequate","Reduce circuit lengths","Install RCDs"]'::jsonb, 1, 'Lower Ze means higher PFC. Must remeasure PFC and verify all protective devices still have adequate breaking capacity for new higher fault current.', 'Prospective Fault Current', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('pfc-48', 'Industrial installation has PFC of 35kA at origin. Most appropriate main protective device?', '["MCB Type C 10kA","BS 88 fuse 80kA or higher","RCBO 6kA","MCB Type D 16kA"]'::jsonb, 1, 'Very high PFC (35kA) requires BS 88 HRC fuses with 80kA+ breaking capacity. Standard MCBs are inadequate.', 'Prospective Fault Current', 'Advanced', '434.5.1', NULL, 'inspection-testing'),
  ('pfc-49', 'What is the consequence of not measuring PFC during initial verification?', '["No consequence - not essential","Cannot verify protective device breaking capacity is adequate","Affects only warranty","Only required for commercial installations"]'::jsonb, 1, 'Without PFC measurement, cannot verify protective devices can safely interrupt maximum fault current - potentially dangerous and non-compliant installation.', 'Prospective Fault Current', 'Intermediate', '612.11', NULL, 'inspection-testing'),
  ('pfc-50', 'Calculate PSCC if phase-neutral loop impedance is 0.18Ω and supply voltage is 230V.', '["41.4A","414A","1278A","12,780A"]'::jsonb, 2, 'PSCC = U0 ÷ Zloop(L-N) = 230V ÷ 0.18Ω = 1278A. This is the prospective short circuit current between phase and neutral.', 'Prospective Fault Current', 'Advanced', '612.11', NULL, 'inspection-testing'),
  ('functional-21', 'When functionally testing a two-way lighting circuit, what must be verified?', '["Lighting operates from switch position only","Lighting can be controlled independently from both switch positions","Both switches must be operated simultaneously","Only one switch controls the light"]'::jsonb, 1, 'Two-way switching must allow independent control from both switch positions - light can be turned on or off from either location.', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-22', 'What must be checked during functional testing of isolators in bathrooms?', '["Only operation","Accessible from outside zones, clear on/off indication, secure mounting","RCD protection","Voltage rating only"]'::jsonb, 1, 'Bathroom isolators must be accessible from outside zones, provide clear on/off indication, operate correctly, and be securely mounted.', 'Functional Testing', 'Intermediate', '701.55.1', NULL, 'inspection-testing'),
  ('functional-23', 'During functional testing of a cooker control unit, what should be verified?', '["Switch operates, socket outlet functions, secure mounting, correct rating","Colour only","Socket outlet only","Switch operation only"]'::jsonb, 0, 'Functional testing of cooker control unit verifies switch operation, socket outlet function, secure mounting, correct rating, and appropriate connection.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-24', 'What must be functionally tested on a distribution board?', '["Colour coding only","All MCBs/RCBOs operate, main switch functions, RCDs trip on test, labels present","Only main switch","Visual appearance"]'::jsonb, 1, 'Functional testing includes verifying all protective devices operate correctly, main switch functions, RCD test buttons work, and proper labelling present.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-25', 'When testing an immersion heater switch, what specific check is important?', '["Colour of faceplate","Neon indicator illuminates when on, switch operates correctly","Cable size only","Position on wall"]'::jsonb, 1, 'Immersion heater switches should have working neon indicators showing on/off status, with positive switching action verified during functional test.', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-26', 'What must be tested when verifying operation of undervoltage protection devices?', '["Device prevents restart after voltage loss without manual reset","Voltage measurement only","Current rating","Visual inspection only"]'::jsonb, 0, 'Undervoltage protection must prevent automatic restart after supply loss, requiring manual reset for safety - this must be functionally verified.', 'Functional Testing', 'Advanced', '536.3', NULL, 'inspection-testing'),
  ('functional-27', 'During functional testing of door entry systems, what must be verified?', '["Audio quality only","Call functions, door release operates, audio communication clear, lock mechanism secure","Video quality only","Power supply only"]'::jsonb, 1, 'Functional testing verifies call operation, door release function, clear audio, secure lock mechanism, and proper integration of all components.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-28', 'What must be checked during functional testing of socket outlets?', '["Colour only","Polarity correct, earth present, secure fixing, protective shutters operate","Brand name","Age of socket"]'::jsonb, 1, 'Functional testing verifies correct polarity, earth continuity, secure mounting, protective shutter operation, and switch function (if switched).', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-29', 'When functionally testing photocell-controlled lighting, what must be verified?', '["Lighting colour temperature","Photocell correctly responds to light levels, appropriate time delay, override function","Brand of photocell","Mounting height only"]'::jsonb, 1, 'Functional testing verifies photocell responds correctly to changing light levels, appropriate delay prevents rapid switching, and manual override works.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-30', 'What is the purpose of functional testing as distinct from other verification tests?', '["Measure electrical values","Verify that installed equipment and accessories operate correctly as designed","Test cable insulation","Measure earth resistance"]'::jsonb, 1, 'Functional testing verifies that all electrical equipment, accessories, and controls operate correctly, safely, and as intended under normal conditions.', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-31', 'When testing a changeover switch for generator backup, what must be verified?', '["Switch rating only","Positive changeover, interlocking prevents parallel operation, clear position indication","Cable size","Generator fuel level"]'::jsonb, 1, 'Changeover switches must provide positive changeover, mechanical interlocking to prevent mains/generator paralleling, and clear position indication.', 'Functional Testing', 'Advanced', '551.1', NULL, 'inspection-testing'),
  ('functional-32', 'During functional testing of thermostatic controls, what should be checked?', '["Colour of unit","Correct temperature range, accurate operation, fail-safe operation","Brand name","Installation date"]'::jsonb, 1, 'Thermostatic controls must operate within correct temperature range, respond accurately, and fail-safe (typically open circuit on failure).', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-33', 'What must be tested on electric vehicle charging points during functional testing?', '["Charging port fits vehicles","Operation of charging functions, RCD protection, emergency isolation, status indicators","Cable length only","Mounting height"]'::jsonb, 1, 'EV charging points require verification of charging operation, RCD protection (Type B/F), emergency stop, isolation, and status indication.', 'Functional Testing', 'Advanced', '722', NULL, 'inspection-testing'),
  ('functional-34', 'When functionally testing key-operated switches, what must be verified?', '["Key type only","Switch operates only with correct key, secure mounting, clear labelling","Key availability","Switch colour"]'::jsonb, 1, 'Key-operated switches must operate only with correct key, be securely mounted, clearly labelled, and provide positive on/off indication.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-35', 'What must be checked during functional testing of neon indicators on switches?', '["Brightness only","Illuminates when circuit is live, adequate brightness, correct polarity","Colour of neon","Brand of indicator"]'::jsonb, 1, 'Neon indicators must illuminate when circuit is energised, provide adequate brightness for visibility, and be correctly wired.', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-36', 'During functional testing of mechanical interlocks, what is essential to verify?', '["Visual appearance","Interlock prevents unsafe operation sequence, operates smoothly, cannot be bypassed","Material type","Age of interlock"]'::jsonb, 1, 'Mechanical interlocks must physically prevent unsafe operation sequences, operate reliably, and be designed so they cannot be easily defeated or bypassed.', 'Functional Testing', 'Advanced', '536.4', NULL, 'inspection-testing'),
  ('functional-37', 'What must be functionally tested on solar PV systems in addition to standard tests?', '["Panel efficiency","DC and AC isolators, RCD protection, emergency shutdown, generation meter","Panel cleanliness","Inverter brand"]'::jsonb, 1, 'PV systems require functional testing of DC/AC isolation, RCD operation, emergency shutdown procedures, and generation metering.', 'Functional Testing', 'Advanced', '712', NULL, 'inspection-testing'),
  ('functional-38', 'When functionally testing socket outlets with USB charging ports, what additional check is required?', '["USB port colour","USB ports provide correct voltage (5V), adequate current, standard outlets function","Number of USB ports","Brand compatibility"]'::jsonb, 1, 'USB charging sockets require verification that USB ports provide correct voltage (~5V DC), adequate current output, and standard sockets function normally.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-39', 'What must be tested on residual current monitoring devices (RCMs)?', '["Display only","Accurate earth leakage current measurement, alarm settings, data logging functions","Colour of display","Size of unit"]'::jsonb, 1, 'RCMs must accurately measure and display earth leakage current, alarm at set thresholds, and log data (if equipped) for analysis.', 'Functional Testing', 'Advanced', '531.2.6', NULL, 'inspection-testing'),
  ('functional-40', 'During functional testing of lighting control modules, what should be verified?', '["Module brand","Scenes operate correctly, dimming functions smooth, timing accurate, override works","Installation location","Cable colour"]'::jsonb, 1, 'Lighting control systems require verification of scene operation, smooth dimming, accurate timing functions, and manual override capability.', 'Functional Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('functional-41', 'What must be functionally tested on ceiling rose installations?', '["Style of rose","Secure fixing, earth continuity, switch control, correct connection of pendant","Age of fitting","Colour match to ceiling"]'::jsonb, 1, 'Ceiling roses require verification of secure fixing, earth continuity (if metal), correct switch operation, and proper pendant connection.', 'Functional Testing', 'Beginner', '612.13', NULL, 'inspection-testing'),
  ('functional-42', 'When testing emergency stop buttons, what is the most critical functional requirement?', '["Button colour (red)","Latching action, immediate power interruption, requires deliberate reset","Size of button","Mounting height"]'::jsonb, 1, 'Emergency stops must latch when pressed, immediately interrupt all power, and require deliberate manual reset before equipment can restart.', 'Functional Testing', 'Intermediate', '537.4', NULL, 'inspection-testing'),
  ('functional-43', 'What must be checked during functional testing of IT equipment socket outlets?', '["Socket brand compatibility","Clean power supply, RCD protection appropriate, surge protection present","Desk height","Cable management only"]'::jsonb, 1, 'IT equipment outlets require verification of clean power, appropriate RCD protection (delayed if necessary), and surge protection for sensitive equipment.', 'Functional Testing', 'Advanced', '612.13', NULL, 'inspection-testing'),
  ('functional-44', 'During functional testing of outdoor lighting with PIR control, what additional check is important?', '["Light colour","Detection zone coverage, adjustable time delay, daylight override, weatherproofing","Lamp brand","Mounting height only"]'::jsonb, 1, 'Outdoor PIR lighting requires verification of detection coverage, appropriate time delays, daylight override function, and adequate weatherproofing (IP rating).', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-45', 'What must be tested on electrical installations with programmable timers?', '["Timer brand","Correct time setting, programme operates accurately, manual override, battery backup","Display size","Button colour"]'::jsonb, 1, 'Programmable timers require verification of correct time, accurate programme execution, manual override function, and battery backup (if fitted).', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-46', 'When functionally testing SPDs (Surge Protection Devices), what should be checked?', '["Brand name","Status indicator shows healthy, correct connection, appropriate rating for location","Colour of device","Installation date only"]'::jsonb, 1, 'SPD functional testing verifies status indicators show device health, correct installation (phase, neutral, earth), and appropriate rating for installation.', 'Functional Testing', 'Advanced', '534.4', NULL, 'inspection-testing'),
  ('functional-47', 'What must be verified during functional testing of emergency lighting test facilities?', '["Key switch aesthetics","Test button/switch initiates lamp test, duration test operates, results logged","Lamp colour temperature","Fitting brand"]'::jsonb, 1, 'Emergency lighting test facilities must initiate function test (lamp operation) and duration test, with results logged for compliance records.', 'Functional Testing', 'Advanced', '560.8', NULL, 'inspection-testing'),
  ('functional-48', 'During functional testing of socket outlets on ring finals, what specific check is important?', '["All sockets live from both directions of ring","Socket colour","Brand consistency","Age of sockets"]'::jsonb, 0, 'Ring final sockets should be supplied from both legs of the ring. Testing verifies ring continuity and each socket receives supply from both directions.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing'),
  ('functional-49', 'What must be functionally tested on installations with automatic transfer switches (ATS)?', '["Switch brand","Automatic changeover on power loss, transfer time, return to normal, status indication","Cabinet colour","Installation location"]'::jsonb, 1, 'ATS systems require verification of automatic changeover on mains failure, appropriate transfer time, return to normal supply, and clear status indication.', 'Functional Testing', 'Advanced', '551.1', NULL, 'inspection-testing'),
  ('functional-50', 'When testing final circuits, what confirms proper functional testing has been completed?', '["All accessories operate correctly as designed with proper safety features","Visual inspection only","Measurement of voltage","Cable colours noted"]'::jsonb, 0, 'Functional testing is complete when all equipment, accessories, controls, and safety features operate correctly as designed under normal conditions.', 'Functional Testing', 'Intermediate', '612.13', NULL, 'inspection-testing');
